import React, { useState, useEffect } from 'react';
import { getStudentMonitoring, getStudentEnrollments } from '../../services/api';

const Monitoring = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [reportsRes, enrollmentsRes] = await Promise.all([
        getStudentMonitoring(user.id),
        getStudentEnrollments(user.id)
      ]);
      setReports(reportsRes.data);
      setEnrollments(enrollmentsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Filter reports by enrolled courses
  const filteredReports = selectedCourse === 'all' 
    ? reports.filter(report => 
        enrollments.some(enrollment => enrollment.course_code === report.course_code)
      )
    : reports.filter(report => report.course_code === selectedCourse);

  if (loading) return <div>Loading reports...</div>;

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h4>Lecture Reports - My Courses</h4>
          <div>
            <select 
              className="form-select"
              value={selectedCourse}
              onChange={(e) => setSelectedCourse(e.target.value)}
            >
              <option value="all">All My Courses</option>
              {enrollments.map((enrollment) => (
                <option key={enrollment.course_code} value={enrollment.course_code}>
                  {enrollment.course_name}
                </option>
              ))}
            </select>
          </div>
        </div>
        <div className="card-body">
          {filteredReports.length === 0 ? (
            <p>No reports available for your enrolled courses.</p>
          ) : (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Date</th>
                    <th>Topic</th>
                    <th>Attendance</th>
                    <th>Venue</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map((report) => (
                    <tr key={report.id}>
                      <td>{report.course_name}</td>
                      <td>{report.lecturer_name}</td>
                      <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                      <td>{report.topic_taught}</td>
                      <td>{report.actual_students_present}/{report.total_students_registered}</td>
                      <td>{report.venue}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Monitoring;
