import React, { useState } from 'react';
import { logout } from '../../utils/auth';
import Courses from './Courses';
import Reports from './Reports';
import Monitoring from './Monitoring';
import Classes from './Classes';
import Lectures from './Lectures';
import Rating from './Rating';
import ClassManagement from './ClassManagement';
import './PL.css'; // Import the PL CSS

const PLDashboard = ({ user }) => {
  const [activeTab, setActiveTab] = useState('dashboard');

  const handleLogout = () => {
    logout();
    window.location.href = '/login';
  };

  return (
    <div className="pl-dashboard">
      <nav className="navbar navbar-expand-lg navbar-dark pl-navbar">
        <div className="container">
          <span className="navbar-brand">Program Leader Dashboard</span>
          <div className="navbar-nav ms-auto">
            <span className="navbar-text me-3">Welcome, {user?.name}</span>
            <button className="btn pl-logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <div className="row">
          <div className="col-md-3">
            <div className="pl-sidebar">
              <button 
                className={`pl-nav-item ${activeTab === 'dashboard' ? 'active' : ''}`}
                onClick={() => setActiveTab('dashboard')}
              >
                Dashboard
              </button>
              <button 
                className={`pl-nav-item ${activeTab === 'courses' ? 'active' : ''}`}
                onClick={() => setActiveTab('courses')}
              >
                Courses
              </button>
              <button 
                className={`pl-nav-item ${activeTab === 'class-management' ? 'active' : ''}`}
                onClick={() => setActiveTab('class-management')}
              >
                Class Management
              </button>
              <button 
                className={`pl-nav-item ${activeTab === 'reports' ? 'active' : ''}`}
                onClick={() => setActiveTab('reports')}
              >
                Reports
              </button>
              <button 
                className={`pl-nav-item ${activeTab === 'monitoring' ? 'active' : ''}`}
                onClick={() => setActiveTab('monitoring')}
              >
                Monitoring
              </button>
              <button 
                className={`pl-nav-item ${activeTab === 'classes' ? 'active' : ''}`}
                onClick={() => setActiveTab('classes')}
              >
                Classes
              </button>
              <button 
                className={`pl-nav-item ${activeTab === 'lectures' ? 'active' : ''}`}
                onClick={() => setActiveTab('lectures')}
              >
                Lectures
              </button>
              <button 
                className={`pl-nav-item ${activeTab === 'rating' ? 'active' : ''}`}
                onClick={() => setActiveTab('rating')}
              >
                Rating
              </button>
            </div>
          </div>
          <div className="col-md-9">
            <div className="pl-content-area">
              {activeTab === 'dashboard' && (
                <div className="pl-dashboard-overview pl-fade-in">
                  <div className="pl-dashboard-header">
                    <h4>üëã Welcome, {user?.name}</h4>
                  </div>
                  <div className="pl-dashboard-body">
                    <p>Welcome to the Program Leader Dashboard. Here's what you can manage:</p>
                    <ul>
                      <li>üìö Manage courses and assign lecturers</li>
                      <li>üè´ Assign classes to lecturers</li>
                      <li>üìä View reports with PRL feedback</li>
                      <li>üìà Monitor overall program performance</li>
                      <li>üë®‚Äçüè´ Manage classes and lecturers</li>
                      <li>‚≠ê Review rating data across the program</li>
                      <li>üìã Track program analytics and metrics</li>
                      <li>üîç Monitor lecturer performance</li>
                    </ul>
                    
                    <div className="row mt-4">
                      <div className="col-md-6">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5>Quick Stats</h5>
                            <div className="row text-center">
                              <div className="col-4">
                                <div className="text-primary fw-bold fs-3">12</div>
                                <small className="text-muted">Courses</small>
                              </div>
                              <div className="col-4">
                                <div className="text-success fw-bold fs-3">8</div>
                                <small className="text-muted">Lecturers</small>
                              </div>
                              <div className="col-4">
                                <div className="text-info fw-bold fs-3">25</div>
                                <small className="text-muted">Classes</small>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-6">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5>Recent Activity</h5>
                            <div className="small">
                              <div>‚Ä¢ New course added: Web Development</div>
                              <div>‚Ä¢ 5 reports submitted today</div>
                              <div>‚Ä¢ 3 classes assigned to lecturers</div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              {activeTab === 'courses' && <Courses user={user} />}
              {activeTab === 'class-management' && <ClassManagement user={user} />}
              {activeTab === 'reports' && <Reports user={user} />}
              {activeTab === 'monitoring' && <Monitoring user={user} />}
              {activeTab === 'classes' && <Classes user={user} />}
              {activeTab === 'lectures' && <Lectures user={user} />}
              {activeTab === 'rating' && <Rating user={user} />}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PLDashboard;
