import React, { useState, useEffect } from 'react';
import { getPLReports, getPLCourses } from '../../services/api';

const Monitoring = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [reportsRes, coursesRes] = await Promise.all([
        getPLReports(),
        getPLCourses()
      ]);
      setReports(reportsRes.data);
      setCourses(coursesRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading monitoring data...</div>;

  const totalLecturers = new Set(reports.map(report => report.lecturer_id)).size;
  const totalCourses = courses.length;
  const totalReports = reports.length;
  const reportsWithFeedback = reports.filter(report => report.feedback_text).length;

  return (
    <div className="card">
      <div className="card-header">
        <h4>Program Monitoring Dashboard</h4>
      </div>
      <div className="card-body">
        <div className="row">
          <div className="col-md-3">
            <div className="card text-white bg-primary mb-3">
              <div className="card-body">
                <h5 className="card-title">Total Courses</h5>
                <p className="card-text display-6">{totalCourses}</p>
              </div>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card text-white bg-success mb-3">
              <div className="card-body">
                <h5 className="card-title">Total Lecturers</h5>
                <p className="card-text display-6">{totalLecturers}</p>
              </div>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card text-white bg-info mb-3">
              <div className="card-body">
                <h5 className="card-title">Total Reports</h5>
                <p className="card-text display-6">{totalReports}</p>
              </div>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card text-white bg-warning mb-3">
              <div className="card-body">
                <h5 className="card-title">Reports with Feedback</h5>
                <p className="card-text display-6">{reportsWithFeedback}</p>
              </div>
            </div>
          </div>
        </div>

        <div className="mt-4">
          <h5>Program Overview</h5>
          <div className="row">
            <div className="col-md-6">
              <div className="card">
                <div className="card-body">
                  <h6>Recent Activity</h6>
                  <ul className="list-group">
                    {reports.slice(0, 5).map((report) => (
                      <li key={report.id} className="list-group-item">
                        <strong>{report.course_name}</strong> - {report.lecturer_name}
                        <br />
                        <small>{new Date(report.date_of_lecture).toLocaleDateString()}</small>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="card">
                <div className="card-body">
                  <h6>Quick Stats</h6>
                  <p>Feedback Rate: {((reportsWithFeedback / totalReports) * 100).toFixed(1)}%</p>
                  <p>Active Courses: {courses.filter(c => c.assigned_lecturer_id).length}</p>
                  <p>Reporting Compliance: Monitoring</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Monitoring;
