import React, { useState, useEffect } from 'react';
import { getPLReports } from '../../services/api';
import './PL.css'; // Import the PL CSS

const Reports = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await getPLReports();
      setReports(response.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading reports...</div>;

  return (
    <div className="card">
      <div className="card-header">
        <h4>Reports with PRL Feedback</h4>
      </div>
      <div className="card-body">
        {reports.length === 0 ? (
          <p>No reports available.</p>
        ) : (
          <div className="table-responsive">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Course</th>
                  <th>Lecturer</th>
                  <th>Date</th>
                  <th>Attendance</th>
                  <th>PRL Feedback</th>
                  <th>Feedback Date</th>
                </tr>
              </thead>
              <tbody>
                {reports.map((report) => (
                  <tr key={report.id}>
                    <td>{report.course_name}</td>
                    <td>{report.lecturer_name}</td>
                    <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                    <td>{report.actual_students_present}/{report.total_students_registered}</td>
                    <td>
                      {report.feedback_text ? (
                        <span className="badge bg-success">Feedback Provided</span>
                      ) : (
                        <span className="badge bg-warning">Pending Feedback</span>
                      )}
                    </td>
                    <td>
                      {report.feedback_date ? new Date(report.feedback_date).toLocaleDateString() : 'N/A'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reports;
