import React, { useState, useEffect } from 'react';
import { getPRLMonitoring, getPRLCourses, getPRLReports } from '../../services/api';
import './PRL.css';

const Monitoring = ({ user }) => {
  const [stats, setStats] = useState(null);
  const [courses, setCourses] = useState([]);
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [apiDetails, setApiDetails] = useState(null);

  useEffect(() => {
    fetchMonitoringData();
  }, [user?.id]);

  const fetchMonitoringData = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching monitoring data...');
      setApiDetails(null);
      
      console.log('üìä PRL Monitoring - Fetching data for PRL:', user);
      
      // Test each API endpoint individually to find which one fails
      let monitoringData = null;
      let coursesData = [];
      let reportsData = [];
      let apiErrors = [];

      try {
        console.log('üîç Testing PRL Monitoring endpoint...');
        const monitoringRes = await getPRLMonitoring(user.id);
        console.log('üìà Monitoring Response:', monitoringRes);
        monitoringData = monitoringRes.data || monitoringRes;
        setApiDetails(prev => ({
          ...prev,
          monitoring: { status: 'success', data: monitoringRes.data }
        }));
      } catch (monitoringError) {
        console.error('‚ùå PRL Monitoring endpoint failed:', monitoringError);
        apiErrors.push(`Monitoring: ${monitoringError.message}`);
        setApiDetails(prev => ({
          ...prev,
          monitoring: { 
            status: 'error', 
            error: monitoringError.message,
            statusCode: monitoringError.response?.status
          }
        }));
      }

      try {
        console.log('üîç Testing PRL Courses endpoint...');
        const coursesRes = await getPRLCourses(user.id);
        console.log('üìö Courses Response:', coursesRes);
        coursesData = coursesRes.data || coursesRes || [];
        setApiDetails(prev => ({
          ...prev,
          courses: { status: 'success', data: coursesRes.data, count: coursesData.length }
        }));
      } catch (coursesError) {
        console.error('‚ùå PRL Courses endpoint failed:', coursesError);
        apiErrors.push(`Courses: ${coursesError.message}`);
        setApiDetails(prev => ({
          ...prev,
          courses: { 
            status: 'error', 
            error: coursesError.message,
            statusCode: coursesError.response?.status
          }
        }));
      }

      try {
        console.log('üîç Testing PRL Reports endpoint...');
        const reportsRes = await getPRLReports(user.id);
        console.log('üìã Reports Response:', reportsRes);
        reportsData = reportsRes.data || reportsRes || [];
        setApiDetails(prev => ({
          ...prev,
          reports: { status: 'success', data: reportsRes.data, count: reportsData.length }
        }));
      } catch (reportsError) {
        console.error('‚ùå PRL Reports endpoint failed:', reportsError);
        apiErrors.push(`Reports: ${reportsError.message}`);
        setApiDetails(prev => ({
          ...prev,
          reports: { 
            status: 'error', 
            error: reportsError.message,
            statusCode: reportsError.response?.status
          }
        }));
      }

      setCourses(coursesData);
      setReports(reportsData);

      // If monitoring API failed, calculate stats manually
      if (!monitoringData) {
        console.log('‚ö†Ô∏è No monitoring stats from API, calculating manually...');
        const calculatedStats = calculateStats(coursesData, reportsData);
        setStats(calculatedStats);
        setDebugInfo(`Using calculated stats from ${coursesData.length} courses and ${reportsData.length} reports`);
      } else {
        setStats(monitoringData);
        setDebugInfo('Using API monitoring stats');
      }

      // Set error if any API calls failed
      if (apiErrors.length > 0) {
        setError(`Some API endpoints failed: ${apiErrors.join('; ')}`);
      }

    } catch (error) {
      console.error('‚ùå General error in fetchMonitoringData:', error);
      setError(`Failed to load monitoring data: ${error.message}`);
      setDebugInfo(`General error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Calculate stats manually if API doesn't provide them
  const calculateStats = (coursesData, reportsData) => {
    const lecturerIds = new Set();
    reportsData.forEach(report => {
      if (report.lecturer_id) lecturerIds.add(report.lecturer_id);
    });
    
    const totalLecturers = lecturerIds.size;
    const totalReports = reportsData.length;
    
    // Calculate average attendance from reports
    const validAttendanceReports = reportsData.filter(report => 
      report.actual_students_present && 
      report.total_students_registered &&
      report.total_students_registered > 0
    );
    
    const avgAttendance = validAttendanceReports.length > 0 
      ? validAttendanceReports.reduce((sum, report) => {
          const rate = (report.actual_students_present / report.total_students_registered) * 100;
          return sum + rate;
        }, 0) / validAttendanceReports.length
      : 0;

    const totalCourses = coursesData.length;
    const activeCourses = coursesData.filter(course => course.assigned_lecturer_id).length;
    
    // Get recent reports (last 7 days)
    const oneWeekAgo = new Date();
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
    const recentReports = reportsData.filter(report => {
      const reportDate = new Date(report.created_at || report.date_of_lecture || report.submitted_at);
      return reportDate > oneWeekAgo;
    });

    return {
      total_lecturers: totalLecturers,
      total_reports: totalReports,
      avg_attendance: avgAttendance,
      total_courses: totalCourses,
      active_courses: activeCourses,
      recent_reports: recentReports.length,
      calculated: true
    };
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleStats = {
      total_lecturers: 8,
      total_reports: 45,
      avg_attendance: 78.5,
      total_courses: 12,
      active_courses: 10,
      recent_reports: 15,
      sample_data: true
    };

    const sampleCourses = [
      { id: 1, course_name: 'Web Development', course_code: 'DIWA2110', assigned_lecturer_id: 1 },
      { id: 2, course_name: 'Database Systems', course_code: 'DBS3010', assigned_lecturer_id: 2 },
      { id: 3, course_name: 'Network Security', course_code: 'NS4010', assigned_lecturer_id: null }
    ];

    const sampleReports = [
      { 
        id: 1, 
        lecturer_id: 1, 
        course_code: 'DIWA2110',
        actual_students_present: 25, 
        total_students_registered: 30,
        created_at: new Date().toISOString() 
      },
      { 
        id: 2, 
        lecturer_id: 2, 
        course_code: 'DBS3010',
        actual_students_present: 28, 
        total_students_registered: 32,
        created_at: new Date().toISOString() 
      }
    ];

    setStats(sampleStats);
    setCourses(sampleCourses);
    setReports(sampleReports);
    setError('');
    setDebugInfo('‚úÖ Loaded sample data for testing');
    console.log('üß™ Sample monitoring data loaded');
  };

  // Test individual API endpoints
  const testIndividualEndpoints = async () => {
    setDebugInfo('Testing individual API endpoints...');
    
    const endpoints = [
      { name: 'Monitoring', url: `/api/prl/${user.id}/monitoring` },
      { name: 'Courses', url: `/api/prl/${user.id}/courses` },
      { name: 'Reports', url: `/api/prl/${user.id}/reports` }
    ];

    for (const endpoint of endpoints) {
      try {
        const response = await fetch(`http://localhost:5000${endpoint.url}`);
        const data = await response.json();
        console.log(`‚úÖ ${endpoint.name} Endpoint:`, { status: response.status, data });
        setDebugInfo(prev => prev + `\n‚úÖ ${endpoint.name}: ${response.status}`);
      } catch (error) {
        console.error(`‚ùå ${endpoint.name} Endpoint Failed:`, error);
        setDebugInfo(prev => prev + `\n‚ùå ${endpoint.name}: ${error.message}`);
      }
    }
  };

  if (loading) {
    return (
      <div className="prl-monitoring-container">
        <div className="loading-spinner"></div>
        <p>Loading monitoring data...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-monitoring-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>üß™ PRL MONITORING DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Stats: {stats ? 'Available' : 'Null'} | Courses: {courses.length} | Reports: {reports.length}</div>
        <div>{debugInfo}</div>
        
        {/* API Details */}
        {apiDetails && (
          <div className="api-details mt-2">
            <strong>API Endpoint Status:</strong>
            {Object.entries(apiDetails).map(([endpoint, details]) => (
              <div key={endpoint} className={`api-status ${details.status}`}>
                {endpoint}: {details.status} 
                {details.statusCode && ` (${details.statusCode})`}
                {details.error && ` - ${details.error}`}
              </div>
            ))}
          </div>
        )}
        
        <div className="debug-actions mt-2">
          <button onClick={loadSampleData} className="btn-test">
            üß™ Load Sample Data
          </button>

          <button onClick={testIndividualEndpoints} className="btn-test-api">
            üîó Test Endpoints
          </button>
        </div>
      </div>


      {!stats ? (
        <div className="card">
          <div className="card-header">
            <h4>üìä Stream Monitoring Dashboard</h4>
          </div>
          <div className="card-body text-center py-5">
            <div className="empty-state-icon">üìà</div>
            <h5>No Monitoring Data Available</h5>
            <p className="text-muted">
              Unable to load monitoring statistics. This could be because:
            </p>
            <ul className="text-start text-muted">
              <li>The PRL monitoring API endpoint is returning 400 errors</li>
              <li>Your PRL account might not be properly configured</li>
              <li>There are no courses or reports in your stream</li>
              <li>Database tables might be missing or empty</li>
            </ul>
            <div className="mt-4">
              <button className="btn btn-primary me-2" onClick={fetchMonitoringData}>
                üîÑ Try Again
              </button>
              <button className="btn btn-outline-secondary me-2" onClick={loadSampleData}>
                üß™ Load Sample Data
              </button>
              <button className="btn btn-info" onClick={testIndividualEndpoints}>
                üîó Test API Endpoints
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h4>üìä Stream Monitoring Dashboard</h4>
            <div>
              {stats.calculated && (
                <span className="badge bg-warning text-dark">Calculated Stats</span>
              )}
              {stats.sample_data && (
                <span className="badge bg-info">Sample Data</span>
              )}
            </div>
          </div>
          <div className="card-body">
            {/* Main Stats Row */}
            <div className="row">
              <div className="col-md-3">
                <div className="stat-card bg-primary">
                  <div className="stat-icon">üë®‚Äçüè´</div>
                  <div className="stat-number">{stats.total_lecturers || 0}</div>
                  <div className="stat-label">Total Lecturers</div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="stat-card bg-success">
                  <div className="stat-icon">üìã</div>
                  <div className="stat-number">{stats.total_reports || 0}</div>
                  <div className="stat-label">Total Reports</div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="stat-card bg-info">
                  <div className="stat-icon">üë•</div>
                  <div className="stat-number">
                    {stats.avg_attendance ? Math.round(stats.avg_attendance) : 0}%
                  </div>
                  <div className="stat-label">Avg Attendance</div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="stat-card bg-warning">
                  <div className="stat-icon">üìö</div>
                  <div className="stat-number">{stats.total_courses || 0}</div>
                  <div className="stat-label">Total Courses</div>
                </div>
              </div>
            </div>

            {/* Data Source Info */}
            <div className="row mt-3">
              <div className="col-12">
                <div className={`alert ${stats.sample_data ? 'alert-info' : stats.calculated ? 'alert-warning' : 'alert-success'}`}>
                  <small>
                    <strong>‚ÑπÔ∏è Data Source:</strong>{' '}
                    {stats.sample_data 
                      ? 'Displaying sample data for demonstration' 
                      : stats.calculated
                      ? 'Displaying calculated statistics from available course and report data'
                      : 'Displaying real-time data from monitoring API'
                    }
                    {courses.length > 0 && ` (${courses.length} courses, ${reports.length} reports)`}
                  </small>
                </div>
              </div>
            </div>

            {/* Stream Overview */}
            <div className="row mt-3">
              <div className="col-12">
                <div className="card">
                  <div className="card-header">
                    <h5>üéØ Stream Overview</h5>
                  </div>
                  <div className="card-body">
                    <p>Monitor teaching activity and performance in your academic stream.</p>
                    
                    <div className="row">
                      <div className="col-md-6">
                        <h6>üìà Activity Summary</h6>
                        <div className="activity-stats">
                          <div className="activity-item">
                            <span className="activity-label">Active Courses:</span>
                            <span className="activity-value">{stats.active_courses || 0}</span>
                          </div>
                          <div className="activity-item">
                            <span className="activity-label">Recent Reports:</span>
                            <span className="activity-value">{stats.recent_reports || 0}</span>
                          </div>
                          <div className="activity-item">
                            <span className="activity-label">Course Coverage:</span>
                            <span className="activity-value">
                              {stats.total_courses > 0 
                                ? Math.round(((stats.active_courses || 0) / stats.total_courses) * 100) 
                                : 0
                              }%
                            </span>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-6">
                        <h6>üîç Quick Insights</h6>
                        <ul className="insights-list">
                          <li>üìä Monitor reporting compliance</li>
                          <li>üìà Track attendance trends</li>
                          <li>‚≠ê Review teaching quality</li>
                          <li>üîÑ Identify areas for improvement</li>
                        </ul>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Monitoring;
