import React, { useState, useEffect } from 'react';
import { getPRLMonitoring, getPRLCourses, getPRLReports } from '../../services/api';
import './PRL.css';

const Monitoring = ({ user }) => {
  const [stats, setStats] = useState(null);
  const [courses, setCourses] = useState([]);
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    fetchMonitoringData();
  }, [user?.id]);

  const fetchMonitoringData = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching monitoring data...');
      
      console.log('ğŸ“Š PRL Monitoring - Fetching data for PRL:', user);
      
      const [monitoringRes, coursesRes, reportsRes] = await Promise.all([
        getPRLMonitoring(user.id),
        getPRLCourses(user.id),
        getPRLReports(user.id)
      ]);

      console.log('ğŸ“ˆ Monitoring Response:', monitoringRes);
      console.log('ğŸ“š Courses Response:', coursesRes);
      console.log('ğŸ“‹ Reports Response:', reportsRes);

      const monitoringData = monitoringRes.data || monitoringRes || null;
      const coursesData = coursesRes.data || coursesRes || [];
      const reportsData = reportsRes.data || reportsRes || [];

      setStats(monitoringData);
      setCourses(coursesData);
      setReports(reportsData);

      setDebugInfo(`
        PRL ID: ${user.id}
        Monitoring Data: ${monitoringData ? 'Received' : 'Null'}
        Courses: ${coursesData.length}
        Reports: ${reportsData.length}
      `);

      // If no monitoring stats, calculate them manually
      if (!monitoringData) {
        console.log('âš ï¸ No monitoring stats, calculating manually...');
        const calculatedStats = calculateStats(coursesData, reportsData);
        setStats(calculatedStats);
        setDebugInfo(prev => prev + '\nğŸ“Š Using calculated stats');
      }

    } catch (error) {
      console.error('âŒ Error fetching PRL monitoring data:', error);
      console.error('Error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      setError(`Failed to load monitoring data: ${error.message}`);
      setDebugInfo(`Error: ${error.message} - Status: ${error.response?.status}`);
    } finally {
      setLoading(false);
    }
  };

  // Calculate stats manually if API doesn't provide them
  const calculateStats = (coursesData, reportsData) => {
    const totalLecturers = new Set(reportsData.map(report => report.lecturer_id)).size;
    
    const totalReports = reportsData.length;
    
    // Calculate average attendance
    const attendanceRates = reportsData
      .filter(report => report.actual_students_present && report.total_students_registered)
      .map(report => (report.actual_students_present / report.total_students_registered) * 100);
    
    const avgAttendance = attendanceRates.length > 0 
      ? attendanceRates.reduce((sum, rate) => sum + rate, 0) / attendanceRates.length
      : 0;

    // Calculate additional stats
    const totalCourses = coursesData.length;
    const activeCourses = coursesData.filter(course => course.assigned_lecturer_id).length;
    
    // Get recent reports (last 7 days)
    const oneWeekAgo = new Date();
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
    const recentReports = reportsData.filter(report => 
      new Date(report.created_at || report.date_of_lecture) > oneWeekAgo
    );

    return {
      total_lecturers: totalLecturers,
      total_reports: totalReports,
      avg_attendance: avgAttendance,
      total_courses: totalCourses,
      active_courses: activeCourses,
      recent_reports: recentReports.length,
      calculated: true // Flag to indicate these are calculated stats
    };
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleStats = {
      total_lecturers: 8,
      total_reports: 45,
      avg_attendance: 78.5,
      total_courses: 12,
      active_courses: 10,
      recent_reports: 15,
      sample_data: true
    };

    const sampleCourses = [
      { id: 1, course_name: 'Web Development', assigned_lecturer_id: 1 },
      { id: 2, course_name: 'Database Systems', assigned_lecturer_id: 2 },
      { id: 3, course_name: 'Network Security', assigned_lecturer_id: null }
    ];

    const sampleReports = [
      { id: 1, lecturer_id: 1, actual_students_present: 25, total_students_registered: 30 },
      { id: 2, lecturer_id: 2, actual_students_present: 28, total_students_registered: 32 }
    ];

    setStats(sampleStats);
    setCourses(sampleCourses);
    setReports(sampleReports);
    setDebugInfo('âœ… Loaded sample data for testing');
    console.log('ğŸ§ª Sample monitoring data loaded:', sampleStats);
  };

  if (loading) {
    return (
      <div className="prl-monitoring-container">
        <div className="loading-spinner"></div>
        <p>Loading monitoring data...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-monitoring-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>ğŸ§ª PRL MONITORING DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Stats: {stats ? 'Available' : 'Null'} | Courses: {courses.length} | Reports: {reports.length}</div>
        <div>{debugInfo}</div>
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            ğŸ§ª Load Sample Data
          </button>
          <button onClick={fetchMonitoringData} className="btn-refresh">
            ğŸ”„ Refresh Data
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-warning">
          <strong>âš ï¸ API Error:</strong> {error}
        </div>
      )}

      {!stats ? (
        <div className="card">
          <div className="card-header">
            <h4>ğŸ“Š Stream Monitoring Dashboard</h4>
          </div>
          <div className="card-body text-center py-5">
            <div className="empty-state-icon">ğŸ“ˆ</div>
            <h5>No Monitoring Data Available</h5>
            <p className="text-muted">
              Unable to load monitoring statistics. This could be because:
            </p>
            <ul className="text-start text-muted">
              <li>The monitoring API endpoint is not returning data</li>
              <li>There are no courses or reports in your stream</li>
              <li>There might be a database connection issue</li>
            </ul>
            <div className="mt-4">
              <button className="btn btn-primary me-2" onClick={fetchMonitoringData}>
                ğŸ”„ Try Again
              </button>
              <button className="btn btn-outline-secondary" onClick={loadSampleData}>
                ğŸ§ª Load Sample Data
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h4>ğŸ“Š Stream Monitoring Dashboard</h4>
            <div>
              {stats.calculated && (
                <span className="badge bg-warning text-dark">Calculated Stats</span>
              )}
              {stats.sample_data && (
                <span className="badge bg-info">Sample Data</span>
              )}
            </div>
          </div>
          <div className="card-body">
            {/* Main Stats Row */}
            <div className="row">
              <div className="col-md-3">
                <div className="stat-card bg-primary">
                  <div className="stat-icon">ğŸ‘¨â€ğŸ«</div>
                  <div className="stat-number">{stats.total_lecturers || 0}</div>
                  <div className="stat-label">Total Lecturers</div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="stat-card bg-success">
                  <div className="stat-icon">ğŸ“‹</div>
                  <div className="stat-number">{stats.total_reports || 0}</div>
                  <div className="stat-label">Total Reports</div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="stat-card bg-info">
                  <div className="stat-icon">ğŸ‘¥</div>
                  <div className="stat-number">
                    {stats.avg_attendance ? Math.round(stats.avg_attendance) : 0}%
                  </div>
                  <div className="stat-label">Avg Attendance</div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="stat-card bg-warning">
                  <div className="stat-icon">ğŸ“š</div>
                  <div className="stat-number">{stats.total_courses || courses.length || 0}</div>
                  <div className="stat-label">Total Courses</div>
                </div>
              </div>
            </div>

            {/* Secondary Stats Row */}
            <div className="row mt-3">
              <div className="col-md-4">
                <div className="stat-card-sm bg-secondary">
                  <div className="stat-number">{stats.active_courses || courses.filter(c => c.assigned_lecturer_id).length || 0}</div>
                  <div className="stat-label">Active Courses</div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="stat-card-sm bg-purple">
                  <div className="stat-number">{stats.recent_reports || 0}</div>
                  <div className="stat-label">Recent Reports (7 days)</div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="stat-card-sm bg-orange">
                  <div className="stat-number">
                    {courses.length > 0 ? Math.round((stats.active_courses / courses.length) * 100) : 0}%
                  </div>
                  <div className="stat-label">Course Coverage</div>
                </div>
              </div>
            </div>

            {/* Stream Overview */}
            <div className="row mt-4">
              <div className="col-12">
                <div className="card">
                  <div className="card-header">
                    <h5>ğŸ¯ Stream Overview</h5>
                  </div>
                  <div className="card-body">
                    <p>This dashboard shows the overall teaching activity and performance in your academic stream.</p>
                    
                    <div className="row">
                      <div className="col-md-6">
                        <h6>ğŸ“ˆ Key Metrics</h6>
                        <ul>
                          <li>Monitor lecturer reporting compliance</li>
                          <li>Track student attendance trends</li>
                          <li>Review teaching quality metrics</li>
                          <li>Monitor course assignment status</li>
                        </ul>
                      </div>
                      <div className="col-md-6">
                        <h6>ğŸ” Current Status</h6>
                        <div className="status-list">
                          <div className="status-item">
                            <span className="status-indicator success"></span>
                            <span>Lecturers Active: {stats.total_lecturers || 0}</span>
                          </div>
                          <div className="status-item">
                            <span className="status-indicator info"></span>
                            <span>Reports Submitted: {stats.total_reports || 0}</span>
                          </div>
                          <div className="status-item">
                            <span className="status-indicator warning"></span>
                            <span>Courses Covered: {stats.active_courses || 0}/{stats.total_courses || courses.length || 0}</span>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Quick Actions */}
                    <div className="mt-3">
                      <h6>âš¡ Quick Actions</h6>
                      <div className="btn-group">
                        <button className="btn btn-outline-primary btn-sm">
                          View Detailed Reports
                        </button>
                        <button className="btn btn-outline-success btn-sm">
                          Analyze Attendance
                        </button>
                        <button className="btn btn-outline-info btn-sm">
                          Review Ratings
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Data Source Info */}
            {(stats.calculated || stats.sample_data) && (
              <div className="row mt-3">
                <div className="col-12">
                  <div className="alert alert-info">
                    <small>
                      <strong>â„¹ï¸ Data Source:</strong>{' '}
                      {stats.sample_data 
                        ? 'Displaying sample data for demonstration' 
                        : 'Displaying calculated statistics from available course and report data'
                      }
                    </small>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Monitoring;
