import React, { useState, useEffect } from 'react';
import { getPRLCourses, getPRLMonitoring, getAllCourses } from '../../services/api';
import './PRL.css';

const Classes = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [apiResponse, setApiResponse] = useState(null);

  useEffect(() => {
    fetchCourses();
  }, [user?.id]);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching courses...');
      setApiResponse(null);
      
      console.log('üéØ PRL Classes - Fetching data for PRL:', user);
      
      // Fetch PRL-specific courses
      const response = await getPRLCourses(user.id);
      console.log('üìä PRL Courses API Response:', response);
      setApiResponse(response);
      
      const coursesData = response.data || response || [];
      setCourses(coursesData);
      
      setDebugInfo(`
        PRL ID: ${user.id}
        Courses Found: ${coursesData.length}
        User Role: ${user.role}
        API Status: ${response.status}
      `);
      
      // If no courses found, try to get ALL courses to see what's available
      if (coursesData.length === 0) {
        console.log('üîç No PRL courses found, checking all courses...');
        try {
          const allCoursesResponse = await getAllCourses();
          console.log('üìã All courses response:', allCoursesResponse);
          const allData = allCoursesResponse.data || allCoursesResponse || [];
          setAllCourses(allData);
          
          // Also try monitoring endpoint
          try {
            const monitoringResponse = await getPRLMonitoring(user.id);
            console.log('üìà PRL Monitoring response:', monitoringResponse);
          } catch (monitoringError) {
            console.log('‚ö†Ô∏è Could not fetch monitoring data:', monitoringError);
          }
        } catch (allCoursesError) {
          console.log('‚ö†Ô∏è Could not fetch all courses:', allCoursesError);
        }
      }
      
    } catch (error) {
      console.error('‚ùå Error fetching PRL courses:', error);
      console.error('‚ùå Error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      setError(`Failed to load courses: ${error.message}`);
      setDebugInfo(`Error: ${error.message} - Status: ${error.response?.status}`);
    } finally {
      setLoading(false);
    }
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleCourses = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110",
        stream: "Information Technology",
        assigned_lecturer_id: 101,
        assigned_lecturer_name: "Dr. Smith",
        student_count: 45,
        active: true
      },
      {
        id: 2,
        course_name: "Database Systems",
        course_code: "DBS3010",
        stream: "Information Technology", 
        assigned_lecturer_id: 102,
        assigned_lecturer_name: "Prof. Johnson",
        student_count: 38,
        active: true
      },
      {
        id: 3,
        course_name: "Network Security",
        course_code: "NS4010",
        stream: "Cyber Security",
        assigned_lecturer_id: null,
        assigned_lecturer_name: null,
        student_count: 25,
        active: false
      }
    ];

    setCourses(sampleCourses);
    setDebugInfo('‚úÖ Loaded sample data for testing');
    console.log('üß™ Sample courses loaded:', sampleCourses);
  };

  // Test API connection directly
  const testAPIConnection = async () => {
    try {
      setDebugInfo('Testing API connection...');
      
      // Test basic API health
      const testResponse = await fetch('http://localhost:5000/api/health');
      const health = await testResponse.json();
      console.log('üè• API Health:', health);
      
      // Test PRL endpoint directly
      const prlResponse = await fetch(`http://localhost:5000/api/prl/${user.id}/courses`);
      const prlData = await prlResponse.json();
      console.log('üîó Direct PRL API Response:', prlData);
      
      setDebugInfo(`API Health: ${health.status} - PRL Endpoint: ${prlResponse.status}`);
      
    } catch (testError) {
      console.error('‚ùå API Connection Test Failed:', testError);
      setDebugInfo(`API Test Failed: ${testError.message}`);
    }
  };

  if (loading) {
    return (
      <div className="prl-classes-container">
        <div className="loading-spinner"></div>
        <p>Loading classes data...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-classes-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>üß™ PRL CLASSES DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Courses in Stream: {courses.length}</div>
        <div>All Available Courses: {allCourses.length}</div>
        <div>{debugInfo}</div>
        
        {/* API Response Details */}
        {apiResponse && (
          <div className="api-details">
            <strong>API Response:</strong>
            <div>Status: {apiResponse.status}</div>
            <div>URL: {apiResponse.config?.url}</div>
          </div>
        )}
        
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            üß™ Load Sample Data
          </button>
          <button onClick={fetchCourses} className="btn-refresh">
            üîÑ Refresh Data
          </button>
          <button onClick={testAPIConnection} className="btn-test-api">
            üîó Test API
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h4>üè´ Classes in My Stream</h4>
          <div>
            <span className="badge bg-primary">{courses.length} classes</span>
          </div>
        </div>
        
        <div className="card-body">
          {error && (
            <div className="alert alert-warning">
              <strong>‚ö†Ô∏è API Error:</strong> {error}
            </div>
          )}

          {courses.length === 0 ? (
            <div className="text-center py-5">
              <div className="empty-state-icon">üè´</div>
              <h5>No Classes Found in Your Stream</h5>
              <p className="text-muted">
                This could be because:
              </p>
              <ul className="text-start text-muted">
                <li>You haven't been assigned to any academic stream</li>
                <li>No courses are assigned to your stream</li>
                <li>The API endpoint might not be working correctly</li>
                <li>There might be a database connection issue</li>
              </ul>
              
              <div className="mt-4">
                <button className="btn btn-primary me-2" onClick={fetchCourses}>
                  üîÑ Check Again
                </button>
                <button className="btn btn-outline-secondary me-2" onClick={loadSampleData}>
                  üß™ Load Sample Data
                </button>
                <button className="btn btn-info" onClick={testAPIConnection}>
                  üîó Test API Connection
                </button>
              </div>

              {/* Show all available courses for debugging */}
              {allCourses.length > 0 && (
                <div className="mt-4">
                  <div className="alert alert-info">
                    <h6>üìã All Courses in System ({allCourses.length})</h6>
                    <p>These are all courses found in the system (not filtered by your stream):</p>
                    <div className="row">
                      {allCourses.slice(0, 6).map((course, index) => (
                        <div key={index} className="col-md-6 mb-2">
                          <div className="card">
                            <div className="card-body p-2">
                              <small>
                                <strong>{course.course_code}</strong>: {course.course_name}<br/>
                                {course.stream && <span>Stream: {course.stream}</span>}
                              </small>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                    {allCourses.length > 6 && (
                      <small className="text-muted">... and {allCourses.length - 6} more courses</small>
                    )}
                  </div>
                </div>
              )}

              {/* API Response Details */}
              {apiResponse && (
                <div className="mt-4">
                  <h6>üîß API Response Details</h6>
                  <div className="card">
                    <div className="card-body">
                      <pre className="debug-pre">
                        {JSON.stringify({
                          status: apiResponse.status,
                          statusText: apiResponse.statusText,
                          url: apiResponse.config?.url,
                          data: apiResponse.data,
                          headers: apiResponse.headers
                        }, null, 2)}
                      </pre>
                    </div>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <>
              <div className="row">
                {courses.map((course) => (
                  <div key={course.id} className="col-md-6 col-lg-4 mb-4">
                    <div className={`card h-100 ${course.assigned_lecturer_id ? 'border-success' : 'border-warning'}`}>
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <strong>{course.course_code}</strong>
                        <span className={`badge ${course.assigned_lecturer_id ? 'bg-success' : 'bg-warning'}`}>
                          {course.assigned_lecturer_id ? 'üü¢ Active' : 'üü° Unassigned'}
                        </span>
                      </div>
                      <div className="card-body">
                        <h5 className="card-title">{course.course_name}</h5>
                        
                        <div className="course-details">
                          <div className="detail-item">
                            <span className="label">Stream:</span>
                            <span className="value">{course.stream || 'Not specified'}</span>
                          </div>
                          
                          <div className="detail-item">
                            <span className="label">Lecturer:</span>
                            <span className="value">
                              {course.assigned_lecturer_name || 'Not assigned'}
                            </span>
                          </div>
                          
                          {course.student_count !== undefined && (
                            <div className="detail-item">
                              <span className="label">Students:</span>
                              <span className="value">{course.student_count}</span>
                            </div>
                          )}
                          
                          {course.lecture_count !== undefined && (
                            <div className="detail-item">
                              <span className="label">Lectures:</span>
                              <span className="value">{course.lecture_count}</span>
                            </div>
                          )}
                        </div>
                      </div>
                      
                      <div className="card-footer">
                        <small className="text-muted">
                          Course ID: {course.id}
                          {course.assigned_lecturer_id && ` ‚Ä¢ Lecturer ID: ${course.assigned_lecturer_id}`}
                        </small>
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Summary Stats */}
              <div className="row mt-4">
                <div className="col-12">
                  <div className="summary-stats">
                    <div className="stat-card">
                      <div className="stat-number">{courses.length}</div>
                      <div className="stat-label">Total Courses</div>
                    </div>
                    <div className="stat-card">
                      <div className="stat-number">
                        {courses.filter(c => c.assigned_lecturer_id).length}
                      </div>
                      <div className="stat-label">Active Courses</div>
                    </div>
                    <div className="stat-card">
                      <div className="stat-number">
                        {courses.filter(c => !c.assigned_lecturer_id).length}
                      </div>
                      <div className="stat-label">Unassigned</div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Classes;
