import React, { useState, useEffect } from 'react';
import { getStudentMonitoring, getStudentEnrollments } from '../../services/api';

const Monitoring = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    console.log('Monitoring component mounted for user:', user);
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');
      console.log('Fetching data for student ID:', user.id);
      
      const [reportsRes, enrollmentsRes] = await Promise.all([
        getStudentMonitoring(user.id),
        getStudentEnrollments(user.id)
      ]);
      
      console.log('Reports response:', reportsRes);
      console.log('Enrollments response:', enrollmentsRes);
      
      setReports(reportsRes.data || []);
      setEnrollments(enrollmentsRes.data || []);
      
    } catch (error) {
      console.error('Error fetching monitoring data:', error);
      console.error('Error details:', error.response);
      setError('Failed to load monitoring data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Filter reports by enrolled courses
  const filteredReports = selectedCourse === 'all' 
    ? reports.filter(report => 
        enrollments.some(enrollment => enrollment.course_code === report.course_code)
      )
    : reports.filter(report => report.course_code === selectedCourse);

  console.log('Current state:', {
    reportsCount: reports.length,
    enrollmentsCount: enrollments.length,
    filteredReportsCount: filteredReports.length,
    selectedCourse,
    user
  });

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="card">
          <div className="card-body text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mt-2">Loading lecture reports...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger">
          <h5>Error Loading Data</h5>
          <p>{error}</p>
          <button className="btn btn-primary" onClick={fetchData}>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h4>Lecture Reports Monitoring</h4>
          <div className="d-flex align-items-center gap-3">
            <span className="badge bg-secondary">
              {reports.length} total reports
            </span>
            <select 
              className="form-select"
              value={selectedCourse}
              onChange={(e) => setSelectedCourse(e.target.value)}
            >
              <option value="all">All My Courses</option>
              {enrollments.map((enrollment) => (
                <option key={enrollment.course_code} value={enrollment.course_code}>
                  {enrollment.course_name}
                </option>
              ))}
            </select>
            <button 
              className="btn btn-outline-primary btn-sm"
              onClick={fetchData}
              title="Refresh data"
            >
              â†»
            </button>
          </div>
        </div>
        <div className="card-body">
          {enrollments.length === 0 ? (
            <div className="alert alert-warning text-center">
              <h5>No Courses Enrolled</h5>
              <p>You are not enrolled in any courses yet.</p>
              <p>Please enroll in courses from the "My Classes" section to see reports.</p>
            </div>
          ) : filteredReports.length === 0 ? (
            <div className="alert alert-info text-center">
              <h5>No Reports Available</h5>
              <p>
                {reports.length === 0 
                  ? "No lecture reports found for your enrolled courses." 
                  : `No reports found for ${selectedCourse === 'all' ? 'your enrolled courses' : 'the selected course'}.`}
              </p>
              <p className="text-muted">
                Reports will appear here when lecturers submit them for your courses.
              </p>
              <button className="btn btn-primary" onClick={fetchData}>
                Refresh Data
              </button>
            </div>
          ) : (
            <>
              <div className="mb-3">
                <p className="text-muted">
                  Showing {filteredReports.length} report(s) for{' '}
                  {selectedCourse === 'all' ? 'all your courses' : 'selected course'}
                </p>
              </div>
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead className="table-dark">
                    <tr>
                      <th>Course</th>
                      <th>Lecturer</th>
                      <th>Date</th>
                      <th>Topic</th>
                      <th>Attendance</th>
                      <th>Venue</th>
                      <th>Week</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredReports.map((report) => (
                      <tr key={report.id}>
                        <td>
                          <strong>{report.course_name}</strong>
                          <br />
                          <small className="text-muted">{report.course_code}</small>
                        </td>
                        <td>{report.lecturer_name}</td>
                        <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                        <td>
                          <span title={report.topic_taught}>
                            {report.topic_taught.length > 50 
                              ? report.topic_taught.substring(0, 50) + '...' 
                              : report.topic_taught}
                          </span>
                        </td>
                        <td>
                          <span className={`badge ${
                            (report.actual_students_present / report.total_students_registered) > 0.7 
                              ? 'bg-success' 
                              : (report.actual_students_present / report.total_students_registered) > 0.5 
                                ? 'bg-warning' 
                                : 'bg-danger'
                          }`}>
                            {report.actual_students_present}/{report.total_students_registered}
                          </span>
                        </td>
                        <td>{report.venue}</td>
                        <td>{report.week_of_reporting}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Monitoring;
