import React, { useState } from 'react';
import { Link, Routes, Route } from 'react-router-dom';
import { logout } from '../../utils/auth';
import Monitoring from './Monitoring';
import Rating from './Rating';
import Enrollment from './Enrollment';

const StudentDashboard = ({ user }) => {
  const [activeTab, setActiveTab] = useState('dashboard');

  return (
    <div className="container-fluid">
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container">
          <span className="navbar-brand">Student Dashboard</span>
          <div className="navbar-nav ms-auto">
            <span className="navbar-text me-3">Welcome, {user?.name}</span>
            <button className="btn btn-outline-light btn-sm" onClick={logout}>
              Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <div className="row">
          <div className="col-md-3">
            <div className="list-group">
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'dashboard' ? 'active' : ''}`}
                onClick={() => setActiveTab('dashboard')}
              >
                Dashboard
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'enrollment' ? 'active' : ''}`}
                onClick={() => setActiveTab('enrollment')}
              >
                My Classes
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'monitoring' ? 'active' : ''}`}
                onClick={() => setActiveTab('monitoring')}
              >
                Monitoring
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'rating' ? 'active' : ''}`}
                onClick={() => setActiveTab('rating')}
              >
                Rating
              </button>
            </div>
          </div>
          <div className="col-md-9">
            {activeTab === 'dashboard' && (
              <div className="card">
                <div className="card-header">
                  <h4>Student Overview</h4>
                </div>
                <div className="card-body">
                  <p>Welcome to the Student Dashboard. Use the navigation menu to:</p>
                  <ul>
                    <li>Enroll in and manage your classes</li>
                    <li>Monitor lecture reports from your enrolled classes</li>
                    <li>Rate lectures and lecturers</li>
                  </ul>
                </div>
              </div>
            )}
            {activeTab === 'enrollment' && <Enrollment user={user} />}
            {activeTab === 'monitoring' && <Monitoring user={user} />}
            {activeTab === 'rating' && <Rating user={user} />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
