import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  getStudentMonitoring, 
  getStudentEnrollments, 
  getStudentRatings,
  
} from '../../services/api';
import './Student.css';

const Monitoring = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [isSubscribed, setIsSubscribed] = useState(false);
  const navigate = useNavigate();

  // Fetch all data
  const fetchData = useCallback(async () => {
    try {
      setError('');
      
      console.log('üîÑ Fetching latest data for student:', user.id);
      
      const [reportsRes, enrollmentsRes, ratingsRes] = await Promise.all([
        getStudentMonitoring(user.id),
        getStudentEnrollments(user.id),
        getStudentRatings(user.id)
      ]);
      
      const reportsData = reportsRes.data || reportsRes || [];
      const enrollmentsData = enrollmentsRes.data || enrollmentsRes || [];
      const ratingsData = ratingsRes.data || ratingsRes || [];
      
      setReports(reportsData);
      setEnrollments(enrollmentsData);
      setRatings(ratingsData);
      setLastUpdate(new Date());
      
      console.log('‚úÖ Data refreshed successfully');
      console.log('   - Reports:', reportsData.length);
      console.log('   - Enrollments:', enrollmentsData.length);
      console.log('   - Ratings:', ratingsData.length);
      
    } catch (error) {
      console.error('‚ùå Error fetching monitoring data:', error);
      setError(`Failed to load data: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  }, [user.id]);

  // Initial data load
  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // Real-time subscription to new reports
  useEffect(() => {
    if (!user?.id || isSubscribed) return;

    const setupRealTimeUpdates = async () => {
      try {
        console.log('üîî Setting up real-time updates for student:', user.id);
        
        // Method 1: WebSocket/Server-Sent Events
        const unsubscribe = subscribeToReports(user.id, (newReport) => {
          console.log('üì® New report received:', newReport);
          
          setReports(prevReports => {
            // Check if report already exists to avoid duplicates
            const exists = prevReports.some(report => report.id === newReport.id);
            if (!exists) {
              console.log('üÜï Adding new report to list:', newReport.course_name);
              return [newReport, ...prevReports];
            }
            return prevReports;
          });
          
          setLastUpdate(new Date());
        });

        setIsSubscribed(true);
        
        // Cleanup subscription on unmount
        return () => {
          if (unsubscribe) unsubscribe();
          setIsSubscribed(false);
        };
        
      } catch (error) {
        console.error('‚ùå Error setting up real-time updates:', error);
        // Fallback to polling if real-time fails
        setupPolling();
      }
    };

    setupRealTimeUpdates();
  }, [user.id, isSubscribed]);

  // Fallback: Polling for updates every 30 seconds
  const setupPolling = useCallback(() => {
    console.log('‚è∞ Setting up polling for updates every 30 seconds');
    
    const pollInterval = setInterval(() => {
      fetchData();
    }, 30000); // 30 seconds

    return () => clearInterval(pollInterval);
  }, [fetchData]);

  // Manual refresh with visual feedback
  const handleManualRefresh = async () => {
    setLoading(true);
    await fetchData();
  };

  // Auto-refresh when component becomes visible (tab switch)
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.visibilityState === 'visible') {
        console.log('üëÄ Page became visible, refreshing data...');
        fetchData();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [fetchData]);

  // Filter reports for enrolled courses only
  const filteredReports = selectedCourse === 'all' 
    ? reports.filter(report => 
        enrollments.some(enrollment => 
          enrollment.course_code === report.course_code
        )
      )
    : reports.filter(report => report.course_code === selectedCourse);

  // Check if student has rated a report
  const hasRatedReport = (reportId) => {
    return ratings.some(rating => rating.report_id === reportId);
  };

  const getStudentRating = (reportId) => {
    const rating = ratings.find(r => r.report_id === reportId);
    return rating ? rating.rating_value : null;
  };

  // Navigate to rating page
  const navigateToRating = (reportId) => {
    navigate(`/student/rating?reportId=${reportId}`);
  };

  // Format time since last update
  const getTimeSinceUpdate = () => {
    const now = new Date();
    const diffInSeconds = Math.floor((now - lastUpdate) / 1000);
    
    if (diffInSeconds < 60) return 'just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)} min ago`;
    return `${Math.floor(diffInSeconds / 3600)} hour${Math.floor(diffInSeconds / 3600) > 1 ? 's' : ''} ago`;
  };

  if (loading) {
    return (
      <div className="student-monitoring-container student-fade-in">
        <div className="student-loading">
          <div className="student-loading-spinner"></div>
          <p>Loading your lecture reports...</p>
          <small>Checking for new reports</small>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="student-monitoring-container student-fade-in">
        <div className="student-alert student-alert-danger">
          <h5>‚ö†Ô∏è Unable to Load Reports</h5>
          <p>{error}</p>
          <button className="guest-btn guest-btn-primary" onClick={handleManualRefresh}>
            üîÑ Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="student-monitoring-container student-fade-in">
      <div className="student-monitoring-header">
        <div className="student-monitoring-title-section">
          <h4>üìä Live Lecture Reports Monitoring</h4>
          <p className="student-monitoring-subtitle">
            Real-time updates when lecturers submit new reports
            {isSubscribed && <span className="live-indicator"> ‚óè LIVE</span>}
          </p>
        </div>
        
        <div className="student-monitoring-controls">
          <div className="student-monitoring-stats">
            <span className="student-stat-badge">
              {filteredReports.length} reports ‚Ä¢ Updated {getTimeSinceUpdate()}
            </span>
          </div>
          
          <select 
            className="student-monitoring-select"
            value={selectedCourse}
            onChange={(e) => setSelectedCourse(e.target.value)}
          >
            <option value="all">üìö All My Courses</option>
            {enrollments.map((enrollment) => (
              <option key={enrollment.course_code || enrollment.id} value={enrollment.course_code}>
                {enrollment.course_name} ({enrollment.course_code})
              </option>
            ))}
          </select>
          
          <button 
            className="student-monitoring-refresh"
            onClick={handleManualRefresh}
            title="Refresh reports"
            disabled={loading}
          >
            {loading ? '‚è≥' : 'üîÑ'}
          </button>
        </div>
      </div>

      <div className="student-monitoring-content">
        {/* ... (same empty states as before) ... */}
        
        {filteredReports.length > 0 && (
          <>
            <div className="student-monitoring-summary">
              <p>
                üìà Showing <strong>{filteredReports.length}</strong> report(s) for{' '}
                <strong>
                  {selectedCourse === 'all' 
                    ? 'all your courses' 
                    : enrollments.find(e => e.course_code === selectedCourse)?.course_name
                  }
                </strong>
              </p>
            </div>

            <div className="student-table-container">
              <table className="student-monitoring-table">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Date & Time</th>
                    <th>Topic Covered</th>
                    <th>Attendance</th>
                    <th>Your Rating</th>
                    <th>Actions</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map((report, index) => {
                    const attendanceRate = report.actual_students_present / report.total_students_registered;
                    const attendanceClass = attendanceRate > 0.7 ? 'high' : attendanceRate > 0.5 ? 'medium' : 'low';
                    const hasRated = hasRatedReport(report.id);
                    const studentRating = getStudentRating(report.id);
                    const isNewReport = index === 0 && Date.now() - new Date(report.created_at).getTime() < 300000; // 5 minutes
                    
                    return (
                      <tr key={report.id} className={`student-report-row ${isNewReport ? 'new-report' : ''}`}>
                        <td>
                          <div className="student-course-info">
                            <strong>{report.course_name}</strong>
                            <span className="student-course-code">{report.course_code}</span>
                          </div>
                        </td>
                        <td className="student-lecturer-info">
                          üë®‚Äçüè´ {report.lecturer_name}
                        </td>
                        <td className="student-date-info">
                          <div>
                            {new Date(report.date_of_lecture).toLocaleDateString()}
                          </div>
                          <small className="text-muted">
                            {new Date(report.created_at).toLocaleTimeString()}
                          </small>
                        </td>
                        <td>
                          <div className="student-topic-info" title={report.topic_taught}>
                            {report.topic_taught}
                            {report.learning_outcomes && (
                              <small className="text-muted d-block">
                                {report.learning_outcomes.substring(0, 50)}...
                              </small>
                            )}
                          </div>
                        </td>
                        <td>
                          <span className={`student-attendance-badge ${attendanceClass}`}>
                            {report.actual_students_present}/{report.total_students_registered}
                            <small>({Math.round(attendanceRate * 100)}%)</small>
                          </span>
                        </td>
                        <td className="student-rating-info">
                          {hasRated ? (
                            <div className="student-rating-display">
                              <span className="student-rating-stars">
                                {'‚≠ê'.repeat(studentRating)}
                              </span>
                              <span className="student-rating-value">({studentRating}/5)</span>
                            </div>
                          ) : (
                            <span className="student-not-rated">Not rated</span>
                          )}
                        </td>
                        <td className="student-actions">
                          {hasRated ? (
                            <button 
                              className="student-view-report-btn"
                              onClick={() => navigateToRating(report.id)}
                            >
                              üìã View
                            </button>
                          ) : (
                            <button 
                              className="student-rate-btn"
                              onClick={() => navigateToRating(report.id)}
                            >
                              ‚≠ê Rate
                            </button>
                          )}
                        </td>
                        <td className="student-status">
                          {isNewReport && (
                            <span className="new-indicator" title="New report">
                              NEW
                            </span>
                          )}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Monitoring;
