import React, { useState, useEffect } from 'react';
import { getPRLCourses, getAllCourses, healthCheck } from '../../services/api';
import './PRL.css';

const Courses = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [apiResponse, setApiResponse] = useState(null);

  useEffect(() => {
    fetchCourses();
  }, [user?.id]);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching courses...');
      setApiResponse(null);
      
      console.log('ðŸŽ¯ PRL Courses - Fetching data for PRL:', user);
      
      // First, test API health
      try {
        const health = await healthCheck();
        console.log('ðŸ¥ API Health:', health.data);
      } catch (healthError) {
        console.error('âŒ API Health Check Failed:', healthError);
      }

      // Fetch PRL-specific courses
      const response = await getPRLCourses(user.id);
      console.log('ðŸ“Š PRL Courses API Response:', response);
      setApiResponse(response);
      
      const coursesData = response.data || response || [];
      setCourses(coursesData);
      
      setDebugInfo(`
        PRL ID: ${user.id}
        Courses Found: ${coursesData.length}
        API Status: ${response.status}
        User Role: ${user.role}
      `);
      
      // If no courses found, try to get ALL courses to see what's available
      if (coursesData.length === 0) {
        console.log('ðŸ” No PRL courses found, checking all courses...');
        try {
          const allCoursesResponse = await getAllCourses();
          console.log('ðŸ“‹ All courses response:', allCoursesResponse);
          const allData = allCoursesResponse.data || allCoursesResponse || [];
          setAllCourses(allData);
        } catch (allCoursesError) {
          console.log('âš ï¸ Could not fetch all courses:', allCoursesError);
        }
      }
      
    } catch (error) {
      console.error('âŒ Error fetching PRL courses:', error);
      console.error('âŒ Error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      setError(`Failed to load courses: ${error.message}`);
      setDebugInfo(`Error: ${error.message} - Status: ${error.response?.status}`);
      
      // Try to get all courses as fallback
      try {
        const allCoursesResponse = await getAllCourses();
        const allData = allCoursesResponse.data || allCoursesResponse || [];
        setAllCourses(allData);
      } catch (fallbackError) {
        console.log('âš ï¸ Fallback also failed:', fallbackError);
      }
    } finally {
      setLoading(false);
    }
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleCourses = [
      {
        id: 1,
        course_code: "DIWA2110",
        course_name: "Web Development",
        stream: "Information Technology",
        assigned_lecturer_id: 101,
        assigned_lecturer_name: "Dr. Smith",
        credits: 3,
        semester: "Spring 2024"
      },
      {
        id: 2,
        course_code: "DBS3010",
        course_name: "Database Systems",
        stream: "Information Technology", 
        assigned_lecturer_id: 102,
        assigned_lecturer_name: "Prof. Johnson",
        credits: 4,
        semester: "Spring 2024"
      },
      {
        id: 3,
        course_code: "NS4010",
        course_name: "Network Security",
        stream: "Cyber Security",
        assigned_lecturer_id: null,
        assigned_lecturer_name: null,
        credits: 3,
        semester: "Spring 2024"
      },
      {
        id: 4,
        course_code: "BIT2010",
        course_name: "Business IT",
        stream: "Business Information Technology",
        assigned_lecturer_id: 103,
        assigned_lecturer_name: "Dr. Wilson",
        credits: 3,
        semester: "Spring 2024"
      }
    ];

    setCourses(sampleCourses);
    setDebugInfo('âœ… Loaded sample data for testing');
    console.log('ðŸ§ª Sample courses loaded:', sampleCourses);
  };

  // Test API connection directly
  const testAPIConnection = async () => {
    try {
      setDebugInfo('Testing API connection...');
      
      // Test basic API health
      const testResponse = await fetch('http://localhost:5000/api/health');
      const health = await testResponse.json();
      console.log('ðŸ¥ API Health:', health);
      
      // Test PRL endpoint directly
      const prlResponse = await fetch(`http://localhost:5000/api/prl/${user.id}/courses`);
      const prlData = await prlResponse.json();
      console.log('ðŸ”— Direct PRL API Response:', prlData);
      
      // Test all courses endpoint
      const allResponse = await fetch('http://localhost:5000/api/courses');
      const allData = await allResponse.json();
      console.log('ðŸ“š All Courses Response:', allData);
      
      setDebugInfo(`
        API Health: ${health.status}
        PRL Endpoint: ${prlResponse.status}
        All Courses: ${allResponse.status}
      `);
      
    } catch (testError) {
      console.error('âŒ API Connection Test Failed:', testError);
      setDebugInfo(`API Test Failed: ${testError.message}`);
    }
  };

  if (loading) {
    return (
      <div className="prl-courses-container">
        <div className="loading-spinner"></div>
        <p>Loading courses data...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-courses-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>ðŸ§ª PRL COURSES DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Courses in Stream: {courses.length}</div>
        <div>All Available Courses: {allCourses.length}</div>
        <div>{debugInfo}</div>
        
        {/* API Response Details */}
        {apiResponse && (
          <div className="api-details">
            <strong>API Response:</strong>
            <div>Status: {apiResponse.status}</div>
            <div>URL: {apiResponse.config?.url}</div>
            {apiResponse.data && (
              <div>Data Type: {Array.isArray(apiResponse.data) ? 'Array' : typeof apiResponse.data}</div>
            )}
          </div>
        )}
        
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            ðŸ§ª Load Sample Data
          </button>
          <button onClick={fetchCourses} className="btn-refresh">
            ðŸ”„ Refresh Data
          </button>
          <button onClick={testAPIConnection} className="btn-test-api">
            ðŸ”— Test API
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h4>ðŸ« Courses in My Stream</h4>
          <div>
            <span className="badge bg-primary">{courses.length} courses</span>
            {courses.some(c => c.sample_data) && (
              <span className="badge bg-info ms-2">Sample Data</span>
            )}
          </div>
        </div>
        
        <div className="card-body">
          {error && (
            <div className="alert alert-warning">
              <strong>âš ï¸ API Error:</strong> {error}
              <div className="mt-2">
                <small>
                  Common causes for 400 errors:
                  <ul>
                    <li>PRL not found in database</li>
                    <li>PRL not assigned to a stream</li>
                    <li>No courses in PRL's stream</li>
                    <li>Database table missing</li>
                  </ul>
                </small>
              </div>
            </div>
          )}

          {courses.length === 0 ? (
            <div className="text-center py-5">
              <div className="empty-state-icon">ðŸ“š</div>
              <h5>No Courses Found in Your Stream</h5>
              <p className="text-muted">
                This could be because:
              </p>
              <ul className="text-start text-muted">
                <li>You haven't been assigned to any academic stream</li>
                <li>No courses are assigned to your stream</li>
                <li>The API endpoint might not be working correctly</li>
                <li>There might be a database connection issue</li>
              </ul>
              
              <div className="mt-4">
                <button className="btn btn-primary me-2" onClick={fetchCourses}>
                  ðŸ”„ Check Again
                </button>
                <button className="btn btn-outline-secondary me-2" onClick={loadSampleData}>
                  ðŸ§ª Load Sample Data
                </button>
                <button className="btn btn-info" onClick={testAPIConnection}>
                  ðŸ”— Test API Connection
                </button>
              </div>

              {/* Show all available courses for debugging */}
              {allCourses.length > 0 && (
                <div className="mt-4">
                  <div className="alert alert-info">
                    <h6>ðŸ“‹ All Courses in System ({allCourses.length})</h6>
                    <p>These are all courses found in the system (not filtered by your stream):</p>
                    <div className="table-responsive">
                      <table className="table table-sm table-bordered">
                        <thead>
                          <tr>
                            <th>Code</th>
                            <th>Name</th>
                            <th>Stream</th>
                            <th>Lecturer</th>
                          </tr>
                        </thead>
                        <tbody>
                          {allCourses.slice(0, 10).map((course, index) => (
                            <tr key={index}>
                              <td><code>{course.course_code}</code></td>
                              <td>{course.course_name}</td>
                              <td>{course.stream || 'Not set'}</td>
                              <td>{course.assigned_lecturer_name || 'Not assigned'}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                    {allCourses.length > 10 && (
                      <small className="text-muted">... and {allCourses.length - 10} more courses</small>
                    )}
                  </div>
                </div>
              )}

              {/* API Response Details */}
              {apiResponse && (
                <div className="mt-4">
                  <h6>ðŸ”§ API Response Details</h6>
                  <div className="card">
                    <div className="card-body">
                      <pre className="debug-pre">
                        {JSON.stringify({
                          status: apiResponse.status,
                          statusText: apiResponse.statusText,
                          url: apiResponse.config?.url,
                          data: apiResponse.data,
                          headers: apiResponse.headers
                        }, null, 2)}
                      </pre>
                    </div>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <>
              {/* Summary Stats */}
              <div className="row mb-4">
                <div className="col-md-3">
                  <div className="stat-card-sm bg-primary">
                    <div className="stat-number">{courses.length}</div>
                    <div className="stat-label">Total Courses</div>
                  </div>
                </div>
                <div className="col-md-3">
                  <div className="stat-card-sm bg-success">
                    <div className="stat-number">
                      {courses.filter(c => c.assigned_lecturer_id).length}
                    </div>
                    <div className="stat-label">Assigned</div>
                  </div>
                </div>
                <div className="col-md-3">
                  <div className="stat-card-sm bg-warning">
                    <div className="stat-number">
                      {courses.filter(c => !c.assigned_lecturer_id).length}
                    </div>
                    <div className="stat-label">Unassigned</div>
                  </div>
                </div>
                <div className="col-md-3">
                  <div className="stat-card-sm bg-info">
                    <div className="stat-number">
                      {new Set(courses.map(c => c.stream)).size}
                    </div>
                    <div className="stat-label">Streams</div>
                  </div>
                </div>
              </div>

              {/* Courses Table */}
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead className="table-dark">
                    <tr>
                      <th>Course Code</th>
                      <th>Course Name</th>
                      <th>Stream</th>
                      <th>Assigned Lecturer</th>
                      <th>Status</th>
                      <th>Credits</th>
                    </tr>
                  </thead>
                  <tbody>
                    {courses.map((course) => (
                      <tr key={course.id || course.course_code}>
                        <td>
                          <strong>{course.course_code}</strong>
                        </td>
                        <td>
                          <strong>{course.course_name}</strong>
                          {course.semester && (
                            <div>
                              <small className="text-muted">{course.semester}</small>
                            </div>
                          )}
                        </td>
                        <td>
                          <span className="badge bg-secondary">{course.stream}</span>
                        </td>
                        <td>
                          {course.assigned_lecturer_name ? (
                            <div>
                              <div>{course.assigned_lecturer_name}</div>
                              <small className="text-muted">ID: {course.assigned_lecturer_id}</small>
                            </div>
                          ) : (
                            <span className="text-warning">Not Assigned</span>
                          )}
                        </td>
                        <td>
                          <span className={`badge ${course.assigned_lecturer_id ? 'bg-success' : 'bg-warning'}`}>
                            {course.assigned_lecturer_id ? 'ðŸŸ¢ Active' : 'ðŸŸ¡ Pending'}
                          </span>
                        </td>
                        <td>
                          {course.credits || 'N/A'}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Stream Breakdown */}
              <div className="row mt-4">
                <div className="col-12">
                  <div className="card">
                    <div className="card-header">
                      <h5>ðŸ“Š Stream Breakdown</h5>
                    </div>
                    <div className="card-body">
                      <div className="row">
                        {Array.from(new Set(courses.map(c => c.stream))).map(stream => {
                          const streamCourses = courses.filter(c => c.stream === stream);
                          const assignedCount = streamCourses.filter(c => c.assigned_lecturer_id).length;
                          
                          return (
                            <div key={stream} className="col-md-4 mb-3">
                              <div className="card">
                                <div className="card-body">
                                  <h6>{stream}</h6>
                                  <div className="stream-stats">
                                    <small>
                                      <strong>{streamCourses.length}</strong> courses<br/>
                                      <strong>{assignedCount}</strong> assigned<br/>
                                      <strong>{streamCourses.length - assignedCount}</strong> pending
                                    </small>
                                  </div>
                                </div>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Courses;
