import React, { useState, useEffect } from 'react';
import { getPRLReports, addFeedback } from '../../services/api';

const Reports = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    if (user && user.id) {
      console.log('🔍 User object:', user);
      console.log('🔍 User ID:', user.id);
      console.log('🔍 User role:', user.role);
      fetchReports();
    } else {
      console.log('❌ User not available');
      setLoading(false);
      setError('User information not available');
    }
  }, [user]);

  const fetchReports = async () => {
    try {
      setError(null);
      setLoading(true);
      console.log('🔄 Fetching reports for user ID:', user.id);
      
      const response = await getPRLReports(user.id);
      console.log('📊 API Response:', response);
      console.log('📊 Response data:', response.data);
      
      if (response && response.data) {
        console.log('✅ Reports found:', response.data.length);
        setReports(response.data);
      } else if (Array.isArray(response)) {
        console.log('✅ Reports found (array response):', response.length);
        setReports(response);
      } else {
        console.log('❌ No reports data found in response');
        setReports([]);
      }
    } catch (error) {
      console.error('❌ Error fetching reports:', error);
      console.error('❌ Error details:', error.response || error.message);
      setError(`Failed to load reports: ${error.message}`);
      setReports([]);
    } finally {
      setLoading(false);
    }
  };

  // Test with mock data function
  const loadMockData = () => {
    console.log('🔄 Loading mock data for testing');
    const mockReports = [
      {
        id: 1,
        course_name: "Computer Science 101",
        lecturer_name: "Dr. Smith",
        date_of_lecture: "2024-01-15",
        actual_students_present: 45,
        total_students_registered: 50,
        topic_taught: "Introduction to Programming and Basic Algorithms"
      },
      {
        id: 2,
        course_name: "Mathematics 202",
        lecturer_name: "Prof. Johnson",
        date_of_lecture: "2024-01-16",
        actual_students_present: 38,
        total_students_registered: 40,
        topic_taught: "Advanced Calculus and Differential Equations"
      },
      {
        id: 3,
        course_name: "Software Engineering",
        lecturer_name: "Dr. Williams",
        date_of_lecture: "2024-01-17",
        actual_students_present: 42,
        total_students_registered: 45,
        topic_taught: "Agile Development Methodologies and Scrum Framework"
      }
    ];
    setReports(mockReports);
    setError(null);
  };

  const handleSubmitFeedback = async (e) => {
    e.preventDefault();
    if (!feedback.trim()) {
      alert('Please enter feedback text');
      return;
    }

    setSubmitting(true);
    try {
      await addFeedback({
        report_id: selectedReport.id,
        pl_id: user.id,
        feedback_text: feedback.trim()
      });
      alert('Feedback submitted successfully!');
      setSelectedReport(null);
      setFeedback('');
      fetchReports();
    } catch (error) {
      console.error('Error submitting feedback:', error);
      alert('Error submitting feedback. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return (
    <div className="text-center p-4">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
      <p className="mt-2">Loading program reports...</p>
    </div>
  );

  return (
    <div className="row">
      <div className="col-12 mb-3">
        <div className="card">
          <div className="card-body">
            <h5>Debug Information</h5>
            <p><strong>User ID:</strong> {user?.id || 'Not available'}</p>
            <p><strong>Reports Count:</strong> {reports.length}</p>
            <p><strong>Status:</strong> {loading ? 'Loading...' : error ? 'Error' : 'Loaded'}</p>
            <div className="btn-group">
              <button className="btn btn-primary btn-sm" onClick={fetchReports}>
                Refresh Reports
              </button>
              <button className="btn btn-secondary btn-sm" onClick={loadMockData}>
                Load Mock Data
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="col-md-8">
        <div className="card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h4>Lecture Reports in My Program</h4>
            <span className="badge bg-primary">{reports.length} reports</span>
          </div>
          <div className="card-body">
            {error && (
              <div className="alert alert-danger">
                <strong>Error:</strong> {error}
              </div>
            )}
            
            {reports.length === 0 ? (
              <div className="text-center p-4">
                <div className="mb-3">
                  <i className="fas fa-inbox fa-3x text-muted"></i>
                </div>
                <p className="text-muted">No reports available in your program.</p>
                <p className="text-muted small mb-3">
                  This could mean there are no lectures reported yet, or your program filter is not matching any reports.
                </p>
                <div className="btn-group">
                  <button className="btn btn-primary" onClick={fetchReports}>
                    <i className="fas fa-refresh me-2"></i>Refresh
                  </button>
                  <button className="btn btn-outline-secondary" onClick={loadMockData}>
                    <i className="fas fa-database me-2"></i>Load Test Data
                  </button>
                </div>
              </div>
            ) : (
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>Course</th>
                      <th>Lecturer</th>
                      <th>Date</th>
                      <th>Attendance</th>
                      <th>Topic</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reports.map((report) => (
                      <tr key={report.id}>
                        <td>
                          <strong>{report.course_name}</strong>
                          <br />
                          <small className="text-muted">{report.course_code}</small>
                        </td>
                        <td>{report.lecturer_name}</td>
                        <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                        <td>
                          <span className={`badge ${report.actual_students_present / report.total_students_registered > 0.7 ? 'bg-success' : 'bg-warning'}`}>
                            {report.actual_students_present}/{report.total_students_registered}
                          </span>
                        </td>
                        <td>
                          <span title={report.topic_taught}>
                            {report.topic_taught?.substring(0, 50)}...
                          </span>
                        </td>
                        <td>
                          <button 
                            className="btn btn-primary btn-sm"
                            onClick={() => setSelectedReport(report)}
                          >
                            <i className="fas fa-comment me-1"></i>
                            Add Feedback
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="col-md-4">
        {selectedReport && (
          <div className="card">
            <div className="card-header">
              <h5>Add Feedback</h5>
            </div>
            <div className="card-body">
              <p><strong>Course:</strong> {selectedReport.course_name}</p>
              <p><strong>Lecturer:</strong> {selectedReport.lecturer_name}</p>
              <p><strong>Topic:</strong> {selectedReport.topic_taught}</p>
              
              <form onSubmit={handleSubmitFeedback}>
                <div className="mb-3">
                  <label className="form-label">Your Feedback</label>
                  <textarea
                    className="form-control"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    rows="5"
                    required
                    placeholder="Provide constructive feedback about this lecture..."
                  />
                </div>
                <button type="submit" className="btn btn-success">
                  <i className="fas fa-paper-plane me-1"></i>
                  Submit Feedback
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary ms-2"
                  onClick={() => {
                    setSelectedReport(null);
                    setFeedback('');
                  }}
                >
                  Cancel
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reports;
