import React, { useState, useEffect } from 'react';
import { getPRLReports, getPRLCourses, getPRLRatings } from '../../services/api';
import './PRL.css';

const Rating = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [courses, setCourses] = useState([]);
  const [studentRatings, setStudentRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [viewMode, setViewMode] = useState('attendance'); // 'attendance' or 'student-ratings'

  useEffect(() => {
    fetchRatingData();
  }, [user?.id]);

  const fetchRatingData = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching rating data...');
      
      console.log('‚≠ê PRL Rating - Fetching data for PRL:', user);
      
      const [reportsRes, coursesRes, ratingsRes] = await Promise.all([
        getPRLReports(user.id),
        getPRLCourses(user.id),
        getPRLRatings ? getPRLRatings(user.id) : Promise.resolve({ data: [] }) // Fallback if not implemented
      ]);

      console.log('üìä PRL Reports Response:', reportsRes);
      console.log('üìö PRL Courses Response:', coursesRes);
      console.log('üéØ Student Ratings Response:', ratingsRes);

      const reportsData = reportsRes.data || reportsRes || [];
      const coursesData = coursesRes.data || coursesRes || [];
      const ratingsData = ratingsRes?.data || ratingsRes || [];

      setReports(reportsData);
      setCourses(coursesData);
      setStudentRatings(ratingsData);

      setDebugInfo(`
        PRL ID: ${user.id}
        Reports: ${reportsData.length}
        Courses: ${coursesData.length}
        Student Ratings: ${ratingsData.length}
      `);

    } catch (error) {
      console.error('‚ùå Error fetching PRL rating data:', error);
      console.error('‚ùå Error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      setError(`Failed to load rating data: ${error.message}`);
      setDebugInfo(`Error: ${error.message} - Status: ${error.response?.status}`);
    } finally {
      setLoading(false);
    }
  };

  // Calculate ratings and statistics
  const calculateStats = () => {
    if (reports.length === 0) return null;

    // Attendance-based ratings
    const attendanceStats = reports.map(report => {
      const attendanceRate = (report.actual_students_present / report.total_students_registered) * 100;
      let rating, badgeClass, description;
      
      if (attendanceRate >= 80) {
        rating = 'Excellent';
        badgeClass = 'success';
        description = 'Strong student engagement';
      } else if (attendanceRate >= 60) {
        rating = 'Good';
        badgeClass = 'primary';
        description = 'Good participation';
      } else if (attendanceRate >= 40) {
        rating = 'Fair';
        badgeClass = 'warning';
        description = 'Moderate attendance';
      } else {
        rating = 'Poor';
        badgeClass = 'danger';
        description = 'Low attendance needs attention';
      }

      return {
        ...report,
        attendanceRate,
        rating,
        badgeClass,
        description
      };
    });

    // Lecturer performance summary
    const lecturerPerformance = {};
    attendanceStats.forEach(report => {
      if (!lecturerPerformance[report.lecturer_name]) {
        lecturerPerformance[report.lecturer_name] = {
          totalReports: 0,
          totalAttendance: 0,
          ratings: []
        };
      }
      lecturerPerformance[report.lecturer_name].totalReports++;
      lecturerPerformance[report.lecturer_name].totalAttendance += report.attendanceRate;
      lecturerPerformance[report.lecturer_name].ratings.push(report.rating);
    });

    // Calculate averages
    Object.keys(lecturerPerformance).forEach(lecturer => {
      const data = lecturerPerformance[lecturer];
      data.averageAttendance = data.totalAttendance / data.totalReports;
      
      // Count rating distribution
      data.ratingCounts = {
        Excellent: data.ratings.filter(r => r === 'Excellent').length,
        Good: data.ratings.filter(r => r === 'Good').length,
        Fair: data.ratings.filter(r => r === 'Fair').length,
        Poor: data.ratings.filter(r => r === 'Poor').length
      };
    });

    return {
      attendanceStats,
      lecturerPerformance,
      totalReports: reports.length,
      uniqueLecturers: Object.keys(lecturerPerformance).length,
      averageAttendance: attendanceStats.reduce((sum, report) => sum + report.attendanceRate, 0) / attendanceStats.length
    };
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleReports = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110",
        lecturer_name: "Dr. Smith",
        lecturer_id: 1,
        date_of_lecture: "2024-01-20",
        actual_students_present: 25,
        total_students_registered: 30,
        topic_taught: "React Components and State Management",
        venue: "Lab 101",
        week_of_reporting: 6
      },
      {
        id: 2,
        course_name: "Web Development",
        course_code: "DIWA2110",
        lecturer_name: "Dr. Smith",
        lecturer_id: 1,
        date_of_lecture: "2024-01-27",
        actual_students_present: 28,
        total_students_registered: 30,
        topic_taught: "React Hooks and Context API",
        venue: "Lab 101",
        week_of_reporting: 7
      },
      {
        id: 3,
        course_name: "Database Systems",
        course_code: "DBS3010",
        lecturer_name: "Prof. Johnson",
        lecturer_id: 2,
        date_of_lecture: "2024-01-19",
        actual_students_present: 22,
        total_students_registered: 32,
        topic_taught: "SQL Joins and Advanced Queries",
        venue: "Room 205",
        week_of_reporting: 6
      },
      {
        id: 4,
        course_name: "Network Security",
        course_code: "NS4010",
        lecturer_name: "Dr. Brown",
        lecturer_id: 3,
        date_of_lecture: "2024-01-18",
        actual_students_present: 15,
        total_students_registered: 25,
        topic_taught: "Cryptography Principles",
        venue: "Lab 302",
        week_of_reporting: 6
      }
    ];

    const sampleCourses = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110",
        stream: "Information Technology"
      },
      {
        id: 2,
        course_name: "Database Systems",
        course_code: "DBS3010",
        stream: "Information Technology"
      }
    ];

    setReports(sampleReports);
    setCourses(sampleCourses);
    setDebugInfo('‚úÖ Loaded sample data for testing');
    console.log('üß™ Sample rating data loaded');
  };

  // Test API connection
  const testAPIConnection = async () => {
    try {
      setDebugInfo('Testing API connection...');
      
      const response = await fetch(`http://localhost:5000/api/prl/${user.id}/reports`);
      const data = await response.json();
      console.log('üîó Direct PRL Reports API Response:', { status: response.status, data });
      
      setDebugInfo(`PRL Reports API: ${response.status}`);
      
    } catch (testError) {
      console.error('‚ùå API Connection Test Failed:', testError);
      setDebugInfo(`API Test Failed: ${testError.message}`);
    }
  };

  const stats = calculateStats();

  if (loading) {
    return (
      <div className="prl-rating-container">
        <div className="loading-spinner"></div>
        <p>Loading teaching quality ratings...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-rating-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>üß™ PRL RATING DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Reports: {reports.length} | Courses: {courses.length} | Student Ratings: {studentRatings.length}</div>
        <div>{debugInfo}</div>
        
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            üß™ Load Sample Data
          </button>
          <button onClick={fetchRatingData} className="btn-refresh">
            üîÑ Refresh Data
          </button>
          <button onClick={testAPIConnection} className="btn-test-api">
            üîó Test API
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-warning">
          <strong>‚ö†Ô∏è API Error:</strong> {error}
        </div>
      )}

      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h4>üìä Teaching Quality Ratings</h4>
          <div>
            <span className="badge bg-primary">{reports.length} reports</span>
            {reports.some(r => r.sample_data) && (
              <span className="badge bg-info ms-2">Sample Data</span>
            )}
          </div>
        </div>
        
        <div className="card-body">
          {reports.length === 0 ? (
            <div className="text-center py-5">
              <div className="empty-state-icon">‚≠ê</div>
              <h5>No Reports Available for Rating Analysis</h5>
              <p className="text-muted">
                This could be because:
              </p>
              <ul className="text-start text-muted">
                <li>No lecture reports have been submitted in your stream</li>
                <li>Lecturers in your stream haven't submitted any reports yet</li>
                <li>There might be no courses assigned to your stream</li>
                <li>The API endpoint might not be returning data</li>
              </ul>
              
              <div className="mt-4">
                <button className="btn btn-primary me-2" onClick={fetchRatingData}>
                  üîÑ Check Again
                </button>
                <button className="btn btn-outline-secondary me-2" onClick={loadSampleData}>
                  üß™ Load Sample Data
                </button>
                <button className="btn btn-info" onClick={testAPIConnection}>
                  üîó Test API
                </button>
              </div>

              {/* Show courses for reference */}
              {courses.length > 0 && (
                <div className="mt-4">
                  <div className="alert alert-info">
                    <h6>üìö Courses in Your Stream ({courses.length})</h6>
                    <p>These courses are in your stream but have no reports yet:</p>
                    <div className="row">
                      {courses.slice(0, 6).map((course, index) => (
                        <div key={index} className="col-md-6 mb-2">
                          <div className="card">
                            <div className="card-body p-2">
                              <small>
                                <strong>{course.course_code}</strong>: {course.course_name}<br/>
                                <span className="text-muted">Stream: {course.stream}</span>
                              </small>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <>
              {/* View Mode Toggle */}
              <div className="row mb-4">
                <div className="col-12">
                  <div className="btn-group" role="group">
                    <button
                      type="button"
                      className={`btn ${viewMode === 'attendance' ? 'btn-primary' : 'btn-outline-primary'}`}
                      onClick={() => setViewMode('attendance')}
                    >
                      üìä Attendance-Based Ratings
                    </button>
                    <button
                      type="button"
                      className={`btn ${viewMode === 'student-ratings' ? 'btn-primary' : 'btn-outline-primary'}`}
                      onClick={() => setViewMode('student-ratings')}
                    >
                      ‚≠ê Student Ratings
                    </button>
                  </div>
                </div>
              </div>

              {/* Overall Statistics */}
              {stats && (
                <div className="row mb-4">
                  <div className="col-md-3">
                    <div className="stat-card bg-primary">
                      <div className="stat-number">{stats.totalReports}</div>
                      <div className="stat-label">Total Reports</div>
                    </div>
                  </div>
                  <div className="col-md-3">
                    <div className="stat-card bg-success">
                      <div className="stat-number">{stats.uniqueLecturers}</div>
                      <div className="stat-label">Lecturers</div>
                    </div>
                  </div>
                  <div className="col-md-3">
                    <div className="stat-card bg-info">
                      <div className="stat-number">{Math.round(stats.averageAttendance)}%</div>
                      <div className="stat-label">Avg Attendance</div>
                    </div>
                  </div>
                  <div className="col-md-3">
                    <div className="stat-card bg-warning">
                      <div className="stat-number">
                        {Math.round((stats.attendanceStats.filter(r => r.rating === 'Excellent' || r.rating === 'Good').length / stats.totalReports) * 100)}%
                      </div>
                      <div className="stat-label">Positive Ratings</div>
                    </div>
                  </div>
                </div>
              )}

              {viewMode === 'attendance' ? (
                <>
                  {/* Attendance-Based Ratings Table */}
                  <div className="table-responsive">
                    <table className="table table-striped table-hover">
                      <thead className="table-dark">
                        <tr>
                          <th>Lecturer</th>
                          <th>Course</th>
                          <th>Date</th>
                          <th>Attendance</th>
                          <th>Rating</th>
                          <th>Topic Coverage</th>
                          <th>Week</th>
                        </tr>
                      </thead>
                      <tbody>
                        {stats?.attendanceStats.map((report) => (
                          <tr key={report.id}>
                            <td>
                              <strong>üë®‚Äçüè´ {report.lecturer_name}</strong>
                            </td>
                            <td>
                              <strong>{report.course_name}</strong>
                              <br />
                              <small className="text-muted">{report.course_code}</small>
                            </td>
                            <td>
                              {new Date(report.date_of_lecture).toLocaleDateString('en-US', {
                                month: 'short',
                                day: 'numeric'
                              })}
                            </td>
                            <td>
                              <div>
                                {report.actual_students_present}/{report.total_students_registered}
                                <br />
                                <small className="text-muted">({Math.round(report.attendanceRate)}%)</small>
                              </div>
                            </td>
                            <td>
                              <span className={`badge bg-${report.badgeClass}`} title={report.description}>
                                {report.rating}
                              </span>
                            </td>
                            <td>
                              <span title={report.topic_taught}>
                                {report.topic_taught.length > 50 ? 
                                  report.topic_taught.substring(0, 50) + '...' : 
                                  report.topic_taught
                                }
                              </span>
                            </td>
                            <td>
                              <span className="badge bg-secondary">Week {report.week_of_reporting}</span>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>

                  {/* Lecturer Performance Summary */}
                  {stats && (
                    <div className="row mt-4">
                      <div className="col-12">
                        <div className="card">
                          <div className="card-header">
                            <h5>üë®‚Äçüè´ Lecturer Performance Summary</h5>
                          </div>
                          <div className="card-body">
                            <div className="row">
                              {Object.entries(stats.lecturerPerformance)
                                .sort(([,a], [,b]) => b.averageAttendance - a.averageAttendance)
                                .map(([lecturer, data]) => (
                                  <div key={lecturer} className="col-md-6 col-lg-4 mb-3">
                                    <div className="card h-100">
                                      <div className="card-body">
                                        <h6 className="card-title">{lecturer}</h6>
                                        <div className="lecturer-stats">
                                          <div className="stat-item">
                                            <span className="label">Reports:</span>
                                            <span className="value">{data.totalReports}</span>
                                          </div>
                                          <div className="stat-item">
                                            <span className="label">Avg Attendance:</span>
                                            <span className="value">{Math.round(data.averageAttendance)}%</span>
                                          </div>
                                          <div className="rating-breakdown">
                                            <small>
                                              {data.ratingCounts.Excellent > 0 && <span className="badge bg-success me-1">{data.ratingCounts.Excellent}E</span>}
                                              {data.ratingCounts.Good > 0 && <span className="badge bg-primary me-1">{data.ratingCounts.Good}G</span>}
                                              {data.ratingCounts.Fair > 0 && <span className="badge bg-warning me-1">{data.ratingCounts.Fair}F</span>}
                                              {data.ratingCounts.Poor > 0 && <span className="badge bg-danger me-1">{data.ratingCounts.Poor}P</span>}
                                            </small>
                                          </div>
                                        </div>
                                      </div>
                                    </div>
                                  </div>
                                ))
                              }
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </>
              ) : (
                /* Student Ratings View */
                <div className="text-center py-4">
                  <div className="empty-state-icon">‚≠ê</div>
                  <h5>Student Ratings Analysis</h5>
                  <p className="text-muted">
                    {studentRatings.length > 0 
                      ? `Viewing ${studentRatings.length} student ratings`
                      : 'No student ratings available yet. Students will appear here once they rate lectures.'
                    }
                  </p>
                  {studentRatings.length === 0 && (
                    <div className="alert alert-info">
                      <small>
                        Student ratings provide valuable feedback on teaching quality, content delivery, 
                        and overall learning experience.
                      </small>
                    </div>
                  )}
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Rating;
