import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { logout, getCurrentUser } from '../../utils/auth'; // Fixed import path
import './Navbar.css';

const Navbar = () => {
  const [user, setUser] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const currentUser = getCurrentUser();
    setUser(currentUser);
  }, [location]);

  const handleLogout = () => {
    logout();
    setUser(null);
    navigate('/');
    setIsMenuOpen(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  // Navigation items based on user role
  const getNavItems = () => {
    if (!user) {
      return [
        { path: '/', label: '🏠 Home', icon: '🏠' },
        { path: '/login', label: '🔐 Login', icon: '🔐' },
        { path: '/register', label: '📝 Register', icon: '📝' }
      ];
    }

    const baseItems = [
      { path: '/', label: '🏠 Home', icon: '🏠' }
    ];

    switch (user.role) {
      case 'student':
        return [
          ...baseItems,
          { path: '/student', label: '📊 Dashboard', icon: '📊' },
          { path: '/student?tab=enrollment', label: '📚 My Classes', icon: '📚' },
          { path: '/student?tab=monitoring', label: '📈 Monitoring', icon: '📈' },
          { path: '/student?tab=rating', label: '⭐ Rating', icon: '⭐' }
        ];
      
      case 'lecturer':
        return [
          ...baseItems,
          { path: '/lecturer', label: '📊 Dashboard', icon: '📊' },
          { path: '/lecturer?tab=classes', label: '🏫 Classes', icon: '🏫' },
          { path: '/lecturer?tab=reports', label: '📋 Reports', icon: '📋' },
          { path: '/lecturer?tab=new-report', label: '📝 New Report', icon: '📝' },
          { path: '/lecturer?tab=monitoring', label: '📈 Monitoring', icon: '📈' },
          { path: '/lecturer?tab=rating', label: '⭐ Rating', icon: '⭐' }
        ];
      
      case 'prl':
        return [
          ...baseItems,
          { path: '/prl', label: '📊 Dashboard', icon: '📊' },
          { path: '/prl?tab=courses', label: '📚 Courses', icon: '📚' },
          { path: '/prl?tab=reports', label: '📋 Reports', icon: '📋' },
          { path: '/prl?tab=monitoring', label: '📈 Monitoring', icon: '📈' },
          { path: '/prl?tab=rating', label: '⭐ Rating', icon: '⭐' },
          { path: '/prl?tab=classes', label: '🏫 Classes', icon: '🏫' }
        ];
      
      case 'pl':
        return [
          ...baseItems,
          { path: '/pl', label: '📊 Dashboard', icon: '📊' },
          { path: '/pl?tab=courses', label: '📚 Courses', icon: '📚' },
          { path: '/pl?tab=class-management', label: '🏫 Class Management', icon: '🏫' },
          { path: '/pl?tab=reports', label: '📋 Reports', icon: '📋' },
          { path: '/pl?tab=monitoring', label: '📈 Monitoring', icon: '📈' },
          { path: '/pl?tab=classes', label: '📖 Classes', icon: '📖' },
          { path: '/pl?tab=lectures', label: '👨‍🏫 Lectures', icon: '👨‍🏫' },
          { path: '/pl?tab=rating', label: '⭐ Rating', icon: '⭐' }
        ];
      
      default:
        return baseItems;
    }
  };

  const getRoleDisplayName = () => {
    if (!user) return 'Guest';
    switch (user.role) {
      case 'student': return 'Student';
      case 'lecturer': return 'Lecturer';
      case 'prl': return 'Principal Lecturer';
      case 'pl': return 'Program Leader';
      default: return 'User';
    }
  };

  const navItems = getNavItems();

  return (
    <nav className="navbar">
      <div className="nav-container">
        {/* Logo/Brand */}
        <div className="nav-brand">
          <Link to="/" onClick={closeMenu}>
            <span className="nav-logo">🎓</span>
            <span className="nav-title">LUCT Reporting System</span>
          </Link>
        </div>

        {/* Mobile Menu Button */}
        <div className="nav-toggle" onClick={toggleMenu}>
          <span className={`hamburger ${isMenuOpen ? 'active' : ''}`}>
            <span></span>
            <span></span>
            <span></span>
          </span>
        </div>

        {/* Navigation Menu */}
        <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          {/* Navigation Links */}
          <div className="nav-links">
            {navItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`nav-link ${location.pathname === item.path.split('?')[0] ? 'active' : ''}`}
                onClick={closeMenu}
              >
                <span className="nav-icon">{item.icon}</span>
                {item.label}
              </Link>
            ))}
          </div>

          {/* User Info & Auth Section */}
          <div className="nav-auth">
            {user ? (
              <div className="user-section">
                <div className="user-info">
                  <span className="user-avatar">👤</span>
                  <div className="user-details">
                    <span className="user-name">{user.name}</span>
                    <span className="user-role">{getRoleDisplayName()}</span>
                  </div>
                </div>
                <button 
                  className="logout-btn"
                  onClick={handleLogout}
                  title="Logout"
                >
                  <span className="logout-icon">🚪</span>
                  Logout
                </button>
              </div>
            ) : (
              <div className="auth-buttons">
                <Link 
                  to="/login" 
                  className="auth-btn login-btn"
                  onClick={closeMenu}
                >
                  <span className="btn-icon">🔐</span>
                  Login
                </Link>
                <Link 
                  to="/register" 
                  className="auth-btn register-btn"
                  onClick={closeMenu}
                >
                  <span className="btn-icon">📝</span>
                  Register
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
