import React, { useState, useEffect } from 'react';
import { getPRLCourses } from '../../services/api';
import './PRL.css';

const Courses = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    fetchCourses();
  }, [user?.id]);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching courses...');
      
      console.log('üéØ PRL Courses - Fetching data for PRL:', user);
      
      const response = await getPRLCourses(user.id);
      console.log('üìä PRL Courses API Response:', response);
      
      const coursesData = response.data || response || [];
      setCourses(coursesData);
      
      setDebugInfo(`
        PRL ID: ${user.id}
        Courses Found: ${coursesData.length}
        API Status: ${response.status}
      `);
      
    } catch (error) {
      console.error('‚ùå Error fetching PRL courses:', error);
      console.error('‚ùå Error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      setError(`Failed to load courses: ${error.message}`);
      setDebugInfo(`Error: ${error.message} - Status: ${error.response?.status}`);
    } finally {
      setLoading(false);
    }
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleCourses = [
      {
        id: 1,
        course_code: "DIWA2110",
        course_name: "Web Development",
        stream: "Information Technology",
        assigned_lecturer_id: 101,
        assigned_lecturer_name: "Dr. Smith"
      },
      {
        id: 2,
        course_code: "DBS3010",
        course_name: "Database Systems",
        stream: "Information Technology", 
        assigned_lecturer_id: 102,
        assigned_lecturer_name: "Prof. Johnson"
      },
      {
        id: 3,
        course_code: "NS4010",
        course_name: "Network Security",
        stream: "Cyber Security",
        assigned_lecturer_id: null,
        assigned_lecturer_name: null
      }
    ];

    setCourses(sampleCourses);
    setDebugInfo('‚úÖ Loaded sample data for testing');
    console.log('üß™ Sample courses loaded:', sampleCourses);
  };

  // Test API connection directly
  const testAPIConnection = async () => {
    try {
      setDebugInfo('Testing API connection...');
      
      // Test PRL endpoint directly using fetch
      const response = await fetch(`http://localhost:5000/api/prl/${user.id}/courses`);
      const data = await response.json();
      console.log('üîó Direct API Response:', { status: response.status, data });
      
      setDebugInfo(`API Status: ${response.status}`);
      
    } catch (testError) {
      console.error('‚ùå API Connection Test Failed:', testError);
      setDebugInfo(`API Test Failed: ${testError.message}`);
    }
  };

  if (loading) {
    return (
      <div className="prl-courses-container">
        <div className="loading-spinner"></div>
        <p>Loading courses data...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-courses-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>üß™ PRL COURSES DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Courses Found: {courses.length}</div>
        <div>{debugInfo}</div>
        
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            üß™ Load Sample Data
          </button>
          <button onClick={fetchCourses} className="btn-refresh">
            üîÑ Refresh Data
          </button>
          <button onClick={testAPIConnection} className="btn-test-api">
            üîó Test API
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h4>üè´ Courses in My Stream</h4>
          <div>
            <span className="badge bg-primary">{courses.length} courses</span>
          </div>
        </div>
        
        <div className="card-body">
          {error && (
            <div className="alert alert-warning">
              <strong>‚ö†Ô∏è API Error:</strong> {error}
              <div className="mt-2">
                <small>
                  Common causes for errors:
                  <ul>
                    <li>PRL not found in database</li>
                    <li>PRL not assigned to a stream</li>
                    <li>No courses in PRL's stream</li>
                    <li>Database connection issues</li>
                  </ul>
                </small>
              </div>
            </div>
          )}

          {courses.length === 0 ? (
            <div className="text-center py-5">
              <div className="empty-state-icon">üìö</div>
              <h5>No Courses Found in Your Stream</h5>
              <p className="text-muted">
                This could be because:
              </p>
              <ul className="text-start text-muted">
                <li>You haven't been assigned to any academic stream</li>
                <li>No courses are assigned to your stream</li>
                <li>The API endpoint might not be working correctly</li>
                <li>There might be a database connection issue</li>
              </ul>
              
              <div className="mt-4">
                <button className="btn btn-primary me-2" onClick={fetchCourses}>
                  üîÑ Check Again
                </button>
                <button className="btn btn-outline-secondary me-2" onClick={loadSampleData}>
                  üß™ Load Sample Data
                </button>
                <button className="btn btn-info" onClick={testAPIConnection}>
                  üîó Test API Connection
                </button>
              </div>
            </div>
          ) : (
            <>
              {/* Summary Stats */}
              <div className="row mb-4">
                <div className="col-md-4">
                  <div className="stat-card-sm bg-primary">
                    <div className="stat-number">{courses.length}</div>
                    <div className="stat-label">Total Courses</div>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="stat-card-sm bg-success">
                    <div className="stat-number">
                      {courses.filter(c => c.assigned_lecturer_id).length}
                    </div>
                    <div className="stat-label">Assigned</div>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="stat-card-sm bg-warning">
                    <div className="stat-number">
                      {courses.filter(c => !c.assigned_lecturer_id).length}
                    </div>
                    <div className="stat-label">Unassigned</div>
                  </div>
                </div>
              </div>

              {/* Courses Table */}
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead className="table-dark">
                    <tr>
                      <th>Course Code</th>
                      <th>Course Name</th>
                      <th>Stream</th>
                      <th>Assigned Lecturer</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {courses.map((course) => (
                      <tr key={course.id || course.course_code}>
                        <td>
                          <strong>{course.course_code}</strong>
                        </td>
                        <td>
                          <strong>{course.course_name}</strong>
                        </td>
                        <td>
                          <span className="badge bg-secondary">{course.stream}</span>
                        </td>
                        <td>
                          {course.assigned_lecturer_name ? (
                            <div>
                              <div>{course.assigned_lecturer_name}</div>
                              <small className="text-muted">ID: {course.assigned_lecturer_id}</small>
                            </div>
                          ) : (
                            <span className="text-warning">Not Assigned</span>
                          )}
                        </td>
                        <td>
                          <span className={`badge ${course.assigned_lecturer_id ? 'bg-success' : 'bg-warning'}`}>
                            {course.assigned_lecturer_id ? 'üü¢ Active' : 'üü° Pending'}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Stream Breakdown */}
              <div className="row mt-4">
                <div className="col-12">
                  <div className="card">
                    <div className="card-header">
                      <h5>üìä Stream Breakdown</h5>
                    </div>
                    <div className="card-body">
                      <div className="row">
                        {Array.from(new Set(courses.map(c => c.stream))).map(stream => {
                          const streamCourses = courses.filter(c => c.stream === stream);
                          const assignedCount = streamCourses.filter(c => c.assigned_lecturer_id).length;
                          
                          return (
                            <div key={stream} className="col-md-4 mb-3">
                              <div className="card">
                                <div className="card-body">
                                  <h6>{stream}</h6>
                                  <div className="stream-stats">
                                    <small>
                                      <strong>{streamCourses.length}</strong> courses<br/>
                                      <strong>{assignedCount}</strong> assigned<br/>
                                      <strong>{streamCourses.length - assignedCount}</strong> pending
                                    </small>
                                  </div>
                                </div>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Courses;
