import React, { useState, useEffect } from 'react';
import { getPRLReports } from '../../services/api';

const Rating = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await getPRLReports(user.id);
      setReports(response.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setLoading(false);
    }
  };

  // Calculate average attendance for rating purposes
  const getAttendanceRating = (report) => {
    const attendanceRate = (report.actual_students_present / report.total_students_registered) * 100;
    if (attendanceRate >= 80) return 'Excellent';
    if (attendanceRate >= 60) return 'Good';
    if (attendanceRate >= 40) return 'Fair';
    return 'Poor';
  };

  if (loading) return <div>Loading rating data...</div>;

  return (
    <div className="card">
      <div className="card-header">
        <h4>Teaching Quality Ratings</h4>
      </div>
      <div className="card-body">
        {reports.length === 0 ? (
          <p>No reports available for rating analysis.</p>
        ) : (
          <div className="table-responsive">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Lecturer</th>
                  <th>Course</th>
                  <th>Date</th>
                  <th>Attendance</th>
                  <th>Attendance Rating</th>
                  <th>Topic Coverage</th>
                </tr>
              </thead>
              <tbody>
                {reports.map((report) => (
                  <tr key={report.id}>
                    <td>{report.lecturer_name}</td>
                    <td>{report.course_name}</td>
                    <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                    <td>{report.actual_students_present}/{report.total_students_registered}</td>
                    <td>
                      <span className={`badge ${
                        getAttendanceRating(report) === 'Excellent' ? 'bg-success' :
                        getAttendanceRating(report) === 'Good' ? 'bg-primary' :
                        getAttendanceRating(report) === 'Fair' ? 'bg-warning' : 'bg-danger'
                      }`}>
                        {getAttendanceRating(report)}
                      </span>
                    </td>
                    <td>
                      {report.topic_taught.length > 50 ? 
                        report.topic_taught.substring(0, 50) + '...' : 
                        report.topic_taught
                      }
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Rating;
