import React, { useState, useEffect } from 'react';
import { getPRLMonitoring } from '../../services/api';

const Monitoring = ({ user }) => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await getPRLMonitoring(user.id);
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading monitoring data...</div>;
  if (!stats) return <div>No data available</div>;

  return (
    <div className="card">
      <div className="card-header">
        <h4>Stream Monitoring Dashboard</h4>
      </div>
      <div className="card-body">
        <div className="row">
          <div className="col-md-4">
            <div className="card text-white bg-primary mb-3">
              <div className="card-body">
                <h5 className="card-title">Total Lecturers</h5>
                <p className="card-text display-6">{stats.total_lecturers || 0}</p>
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <div className="card text-white bg-success mb-3">
              <div className="card-body">
                <h5 className="card-title">Total Reports</h5>
                <p className="card-text display-6">{stats.total_reports || 0}</p>
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <div className="card text-white bg-info mb-3">
              <div className="card-body">
                <h5 className="card-title">Avg Attendance</h5>
                <p className="card-text display-6">
                  {stats.avg_attendance ? Math.round(stats.avg_attendance) : 0}%
                </p>
              </div>
            </div>
          </div>
        </div>
        
        <div className="mt-4">
          <h5>Stream Overview</h5>
          <p>This dashboard shows the overall teaching activity and performance in your academic stream.</p>
          <ul>
            <li>Monitor lecturer reporting compliance</li>
            <li>Track student attendance trends</li>
            <li>Review teaching quality metrics</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Monitoring;
