import React, { useState, useEffect } from 'react';
import { getPRLReports, addFeedback, getPRLCourses } from '../../services/api';
import './PRL.css';

const Reports = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [courses, setCourses] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    fetchReports();
  }, [user?.id]);

  const fetchReports = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching reports...');
      
      console.log('ðŸ“‹ PRL Reports - Fetching data for PRL:', user);
      
      const [reportsRes, coursesRes] = await Promise.all([
        getPRLReports(user.id),
        getPRLCourses(user.id)
      ]);

      console.log('ðŸ“Š PRL Reports API Response:', reportsRes);
      console.log('ðŸ“š PRL Courses API Response:', coursesRes);

      const reportsData = reportsRes.data || reportsRes || [];
      const coursesData = coursesRes.data || coursesRes || [];

      setReports(reportsData);
      setCourses(coursesData);

      setDebugInfo(`
        PRL ID: ${user.id}
        Reports Found: ${reportsData.length}
        Courses Found: ${coursesData.length}
        User Role: ${user.role}
      `);

    } catch (error) {
      console.error('âŒ Error fetching PRL reports:', error);
      console.error('âŒ Error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      setError(`Failed to load reports: ${error.message}`);
      setDebugInfo(`Error: ${error.message} - Status: ${error.response?.status}`);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitFeedback = async (e) => {
    e.preventDefault();
    try {
      setError('');
      console.log('ðŸ’¬ Submitting feedback for report:', selectedReport.id);
      
      await addFeedback({
        report_id: selectedReport.id,
        prl_id: user.id,
        feedback_text: feedback,
        lecturer_id: selectedReport.lecturer_id
      });
      
      alert('âœ… Feedback submitted successfully!');
      setSelectedReport(null);
      setFeedback('');
      fetchReports(); // Refresh to see updated feedback
      
    } catch (error) {
      console.error('âŒ Error submitting feedback:', error);
      alert(`âŒ Error submitting feedback: ${error.message}`);
    }
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleReports = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110",
        lecturer_name: "Dr. Smith",
        lecturer_id: 1,
        date_of_lecture: "2024-01-20",
        actual_students_present: 25,
        total_students_registered: 30,
        topic_taught: "React Components and State Management with Hooks and Context API",
        venue: "Lab 101",
        week_of_reporting: 6,
        learning_outcomes: "Students should understand React component lifecycle and state management",
        recommendations: "Provide more practical examples in next lecture",
        created_at: new Date().toISOString()
      },
      {
        id: 2,
        course_name: "Database Systems",
        course_code: "DBS3010",
        lecturer_name: "Prof. Johnson",
        lecturer_id: 2,
        date_of_lecture: "2024-01-19",
        actual_students_present: 28,
        total_students_registered: 32,
        topic_taught: "SQL Joins and Advanced Query Optimization Techniques",
        venue: "Room 205",
        week_of_reporting: 6,
        learning_outcomes: "Master complex SQL joins and query performance optimization",
        recommendations: "Include more real-world database examples",
        created_at: new Date().toISOString()
      },
      {
        id: 3,
        course_name: "Network Security",
        course_code: "NS4010",
        lecturer_name: "Dr. Brown",
        lecturer_id: 3,
        date_of_lecture: "2024-01-18",
        actual_students_present: 22,
        total_students_registered: 25,
        topic_taught: "Cryptography Principles and Network Encryption Protocols",
        venue: "Lab 302",
        week_of_reporting: 6,
        learning_outcomes: "Understand cryptographic algorithms and secure communication",
        recommendations: "Add hands-on encryption exercises",
        created_at: new Date().toISOString()
      }
    ];

    const sampleCourses = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110",
        stream: "Information Technology"
      },
      {
        id: 2,
        course_name: "Database Systems",
        course_code: "DBS3010",
        stream: "Information Technology"
      }
    ];

    setReports(sampleReports);
    setCourses(sampleCourses);
    setDebugInfo('âœ… Loaded sample data for testing');
    console.log('ðŸ§ª Sample reports loaded:', sampleReports);
  };

  // Test API connection directly
  const testAPIConnection = async () => {
    try {
      setDebugInfo('Testing API connection...');
      
      // Test PRL reports endpoint directly
      const response = await fetch(`http://localhost:5000/api/prl/${user.id}/reports`);
      const data = await response.json();
      console.log('ðŸ”— Direct PRL Reports API Response:', { status: response.status, data });
      
      setDebugInfo(`PRL Reports API: ${response.status}`);
      
    } catch (testError) {
      console.error('âŒ API Connection Test Failed:', testError);
      setDebugInfo(`API Test Failed: ${testError.message}`);
    }
  };

  if (loading) {
    return (
      <div className="prl-reports-container">
        <div className="loading-spinner"></div>
        <p>Loading reports data...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-reports-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>ðŸ§ª PRL REPORTS DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Reports Found: {reports.length} | Courses: {courses.length}</div>
        <div>{debugInfo}</div>
        
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            ðŸ§ª Load Sample Data
          </button>
          <button onClick={fetchReports} className="btn-refresh">
            ðŸ”„ Refresh Data
          </button>
          <button onClick={testAPIConnection} className="btn-test-api">
            ðŸ”— Test API
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-warning">
          <strong>âš ï¸ API Error:</strong> {error}
          <div className="mt-2">
            <small>
              Common causes for no reports:
              <ul>
                <li>PRL not assigned to a stream</li>
                <li>No lecturers in your stream have submitted reports</li>
                <li>Reports exist but not in your stream</li>
                <li>Database connection issues</li>
              </ul>
            </small>
          </div>
        </div>
      )}

      <div className="row">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h4>ðŸ“‹ Lecture Reports in My Stream</h4>
              <div>
                <span className="badge bg-primary">{reports.length} reports</span>
                {reports.some(r => r.sample_data) && (
                  <span className="badge bg-info ms-2">Sample Data</span>
                )}
              </div>
            </div>
            <div className="card-body">
              {reports.length === 0 ? (
                <div className="text-center py-5">
                  <div className="empty-state-icon">ðŸ“</div>
                  <h5>No Reports Available</h5>
                  <p className="text-muted">
                    No lecture reports found in your academic stream.
                  </p>
                  <ul className="text-start text-muted">
                    <li>Lecturers in your stream haven't submitted any reports yet</li>
                    <li>There might be no courses assigned to your stream</li>
                    <li>The API endpoint might not be returning data</li>
                    <li>Check if lecturers have submitted reports for their courses</li>
                  </ul>
                  
                  <div className="mt-4">
                    <button className="btn btn-primary me-2" onClick={fetchReports}>
                      ðŸ”„ Check Again
                    </button>
                    <button className="btn btn-outline-secondary me-2" onClick={loadSampleData}>
                      ðŸ§ª Load Sample Data
                    </button>
                    <button className="btn btn-info" onClick={testAPIConnection}>
                      ðŸ”— Test API
                    </button>
                  </div>

                  {/* Show courses for reference */}
                  {courses.length > 0 && (
                    <div className="mt-4">
                      <div className="alert alert-info">
                        <h6>ðŸ“š Courses in Your Stream ({courses.length})</h6>
                        <p>These courses are in your stream but have no reports yet:</p>
                        <div className="row">
                          {courses.slice(0, 6).map((course, index) => (
                            <div key={index} className="col-md-6 mb-2">
                              <div className="card">
                                <div className="card-body p-2">
                                  <small>
                                    <strong>{course.course_code}</strong>: {course.course_name}<br/>
                                    <span className="text-muted">Stream: {course.stream}</span>
                                  </small>
                                </div>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-striped table-hover">
                    <thead className="table-dark">
                      <tr>
                        <th>Course</th>
                        <th>Lecturer</th>
                        <th>Date</th>
                        <th>Attendance</th>
                        <th>Topic</th>
                        <th>Week</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {reports.map((report) => {
                        const attendanceRate = (report.actual_students_present / report.total_students_registered) * 100;
                        const attendanceClass = attendanceRate > 70 ? 'success' : attendanceRate > 50 ? 'warning' : 'danger';
                        
                        return (
                          <tr key={report.id}>
                            <td>
                              <strong>{report.course_name}</strong>
                              <br />
                              <small className="text-muted">{report.course_code}</small>
                            </td>
                            <td>ðŸ‘¨â€ðŸ« {report.lecturer_name}</td>
                            <td>
                              {new Date(report.date_of_lecture).toLocaleDateString('en-US', {
                                month: 'short',
                                day: 'numeric'
                              })}
                            </td>
                            <td>
                              <span className={`badge bg-${attendanceClass}`}>
                                {report.actual_students_present}/{report.total_students_registered}
                                <small> ({Math.round(attendanceRate)}%)</small>
                              </span>
                            </td>
                            <td>
                              <span title={report.topic_taught}>
                                {report.topic_taught.length > 40 
                                  ? report.topic_taught.substring(0, 40) + '...' 
                                  : report.topic_taught}
                              </span>
                            </td>
                            <td>
                              <span className="badge bg-secondary">Week {report.week_of_reporting}</span>
                            </td>
                            <td>
                              <button 
                                className="btn btn-primary btn-sm"
                                onClick={() => setSelectedReport(report)}
                                title="Add feedback for this report"
                              >
                                ðŸ’¬ Feedback
                              </button>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Feedback Sidebar */}
        <div className="col-md-4">
          {selectedReport && (
            <div className="card sticky-top" style={{top: '20px'}}>
              <div className="card-header bg-primary text-white">
                <h5 className="mb-0">ðŸ’¬ Add Feedback</h5>
              </div>
              <div className="card-body">
                <div className="report-details mb-3">
                  <h6>Report Details</h6>
                  <p><strong>Course:</strong> {selectedReport.course_name}</p>
                  <p><strong>Lecturer:</strong> {selectedReport.lecturer_name}</p>
                  <p><strong>Date:</strong> {new Date(selectedReport.date_of_lecture).toLocaleDateString()}</p>
                  <p><strong>Topic:</strong> {selectedReport.topic_taught}</p>
                  <p><strong>Attendance:</strong> {selectedReport.actual_students_present}/{selectedReport.total_students_registered}</p>
                  
                  {selectedReport.learning_outcomes && (
                    <p><strong>Learning Outcomes:</strong> {selectedReport.learning_outcomes}</p>
                  )}
                  
                  {selectedReport.recommendations && (
                    <p><strong>Lecturer's Notes:</strong> {selectedReport.recommendations}</p>
                  )}
                </div>
                
                <form onSubmit={handleSubmitFeedback}>
                  <div className="mb-3">
                    <label className="form-label"><strong>Your Feedback</strong></label>
                    <textarea
                      className="form-control"
                      value={feedback}
                      onChange={(e) => setFeedback(e.target.value)}
                      rows="6"
                      placeholder="Provide constructive feedback about this lecture report. Consider aspects like teaching methodology, content coverage, student engagement, and suggestions for improvement..."
                      required
                      style={{resize: 'vertical'}}
                    />
                    <small className="text-muted">
                      Your feedback will be visible to the lecturer and used for quality improvement.
                    </small>
                  </div>
                  
                  <div className="d-grid gap-2">
                    <button type="submit" className="btn btn-success">
                      âœ… Submit Feedback
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-outline-secondary"
                      onClick={() => {
                        setSelectedReport(null);
                        setFeedback('');
                      }}
                    >
                      âŒ Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          {/* Quick Stats */}
          {reports.length > 0 && (
            <div className="card mt-3">
              <div className="card-header">
                <h6 className="mb-0">ðŸ“Š Quick Stats</h6>
              </div>
              <div className="card-body">
                <div className="stats-grid">
                  <div className="stat-item">
                    <div className="stat-number">{reports.length}</div>
                    <div className="stat-label">Total Reports</div>
                  </div>
                  <div className="stat-item">
                    <div className="stat-number">
                      {new Set(reports.map(r => r.lecturer_name)).size}
                    </div>
                    <div className="stat-label">Lecturers</div>
                  </div>
                  <div className="stat-item">
                    <div className="stat-number">
                      {Math.round(reports.reduce((sum, r) => sum + (r.actual_students_present / r.total_students_registered), 0) / reports.length * 100)}%
                    </div>
                    <div className="stat-label">Avg Attendance</div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Reports;
