import React, { useState, useEffect } from 'react';
import { getPRLReports, getPRLCourses, getPRLRatings } from '../../services/api';
import './PRL.css';

const PRLRating = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('all');
  const [timeRange, setTimeRange] = useState('all');

  useEffect(() => {
    fetchData();
  }, [user?.id]);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching rating analysis data...');
      
      console.log('ðŸ“Š PRL Rating - Fetching data for PRL:', user);
      
      const [reportsRes, ratingsRes, coursesRes] = await Promise.all([
        getPRLReports(user.id),
        getPRLRatings(user.id),
        getPRLCourses(user.id)
      ]);

      const reportsData = reportsRes.data || reportsRes || [];
      const ratingsData = ratingsRes.data || ratingsRes || [];
      const coursesData = coursesRes.data || coursesRes || [];

      setReports(reportsData);
      setRatings(ratingsData);
      setCourses(coursesData);

      setDebugInfo(`
        PRL ID: ${user.id}
        Reports: ${reportsData.length}
        Ratings: ${ratingsData.length}
        Courses: ${coursesData.length}
      `);

      console.log('ðŸ“ˆ PRL Rating Data:', {
        reports: reportsData,
        ratings: ratingsData,
        courses: coursesData
      });

    } catch (error) {
      console.error('âŒ Error fetching PRL rating data:', error);
      setError(`Failed to load rating data: ${error.message}`);
      setDebugInfo(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Calculate statistics
  const calculateStats = () => {
    const filteredRatings = filterRatings();
    
    const totalRatings = filteredRatings.length;
    const avgRating = totalRatings > 0 
      ? (filteredRatings.reduce((sum, r) => sum + r.rating, 0) / totalRatings).toFixed(1)
      : 0;
    
    const ratingDistribution = [1, 2, 3, 4, 5].map(stars => ({
      stars,
      count: filteredRatings.filter(r => r.rating === stars).length,
      percentage: totalRatings > 0 ? ((filteredRatings.filter(r => r.rating === stars).length / totalRatings) * 100).toFixed(1) : 0
    }));

    const lecturerStats = {};
    filteredRatings.forEach(rating => {
      if (!lecturerStats[rating.lecturer_name]) {
        lecturerStats[rating.lecturer_name] = {
          total: 0,
          sum: 0,
          ratings: []
        };
      }
      lecturerStats[rating.lecturer_name].total++;
      lecturerStats[rating.lecturer_name].sum += rating.rating;
      lecturerStats[rating.lecturer_name].ratings.push(rating);
    });

    // Calculate average for each lecturer
    Object.keys(lecturerStats).forEach(lecturer => {
      lecturerStats[lecturer].average = (lecturerStats[lecturer].sum / lecturerStats[lecturer].total).toFixed(1);
    });

    return {
      totalRatings,
      avgRating,
      ratingDistribution,
      lecturerStats,
      filteredRatings
    };
  };

  // Filter ratings based on selected course and time range
  const filterRatings = () => {
    let filtered = ratings;

    // Filter by course
    if (selectedCourse !== 'all') {
      filtered = filtered.filter(rating => rating.course_code === selectedCourse);
    }

    // Filter by time range (you can implement this based on your data)
    if (timeRange !== 'all') {
      const now = new Date();
      let cutoffDate = new Date();
      
      switch (timeRange) {
        case 'week':
          cutoffDate.setDate(now.getDate() - 7);
          break;
        case 'month':
          cutoffDate.setMonth(now.getMonth() - 1);
          break;
        case 'quarter':
          cutoffDate.setMonth(now.getMonth() - 3);
          break;
        default:
          break;
      }
      
      filtered = filtered.filter(rating => new Date(rating.created_at) >= cutoffDate);
    }

    return filtered;
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleRatings = [
      {
        id: 1,
        report_id: 101,
        course_name: "Web Development",
        course_code: "DIWA2110",
        lecturer_name: "Dr. Smith",
        lecturer_id: 1,
        rating: 5,
        comment: "Excellent lecture! Very engaging.",
        student_name: "John Doe",
        created_at: "2024-01-20T10:00:00Z"
      },
      {
        id: 2,
        report_id: 101,
        course_name: "Web Development",
        course_code: "DIWA2110",
        lecturer_name: "Dr. Smith",
        lecturer_id: 1,
        rating: 4,
        comment: "Good content, but could use more examples.",
        student_name: "Jane Smith",
        created_at: "2024-01-20T10:00:00Z"
      },
      {
        id: 3,
        report_id: 102,
        course_name: "Database Systems",
        course_code: "DBS3010",
        lecturer_name: "Prof. Johnson",
        lecturer_id: 2,
        rating: 3,
        comment: "Content was too fast-paced.",
        student_name: "Mike Johnson",
        created_at: "2024-01-19T14:30:00Z"
      },
      {
        id: 4,
        report_id: 103,
        course_name: "Network Security",
        course_code: "NS4010",
        lecturer_name: "Dr. Brown",
        lecturer_id: 3,
        rating: 5,
        comment: "Outstanding presentation!",
        student_name: "Sarah Wilson",
        created_at: "2024-01-18T09:15:00Z"
      }
    ];

    const sampleCourses = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110",
        stream: "Information Technology"
      },
      {
        id: 2,
        course_name: "Database Systems",
        course_code: "DBS3010",
        stream: "Information Technology"
      }
    ];

    setRatings(sampleRatings);
    setCourses(sampleCourses);
    setDebugInfo('âœ… Loaded sample data for testing');
    console.log('ðŸ§ª Sample rating data loaded:', sampleRatings);
  };

  const stats = calculateStats();

  if (loading) {
    return (
      <div className="prl-rating-container">
        <div className="loading-spinner"></div>
        <p>Loading teaching quality ratings...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-rating-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>ðŸ§ª PRL RATING DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Ratings: {ratings.length} | Courses: {courses.length} | Reports: {reports.length}</div>
        <div>{debugInfo}</div>
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            ðŸ§ª Load Sample Data
          </button>
          <button onClick={fetchData} className="btn-refresh">
            ðŸ”„ Refresh Data
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h4>ðŸ“Š Teaching Quality Ratings</h4>
          <div>
            <span className="badge bg-primary">{stats.totalRatings} ratings</span>
          </div>
        </div>
        
        <div className="card-body">
          {error && (
            <div className="alert alert-warning">
              <strong>âš ï¸ Notice:</strong> {error}
            </div>
          )}

          {/* Filters */}
          <div className="row mb-4">
            <div className="col-md-6">
              <label className="form-label">Filter by Course</label>
              <select 
                className="form-select"
                value={selectedCourse}
                onChange={(e) => setSelectedCourse(e.target.value)}
              >
                <option value="all">All Courses</option>
                {courses.map(course => (
                  <option key={course.course_code} value={course.course_code}>
                    {course.course_name} ({course.course_code})
                  </option>
                ))}
              </select>
            </div>
            <div className="col-md-6">
              <label className="form-label">Time Range</label>
              <select 
                className="form-select"
                value={timeRange}
                onChange={(e) => setTimeRange(e.target.value)}
              >
                <option value="all">All Time</option>
                <option value="week">Last Week</option>
                <option value="month">Last Month</option>
                <option value="quarter">Last 3 Months</option>
              </select>
            </div>
          </div>

          {stats.totalRatings === 0 ? (
            <div className="text-center py-5">
              <div className="empty-state-icon">ðŸ“Š</div>
              <h5>No Ratings Available for Analysis</h5>
              <p className="text-muted">
                This could be because:
              </p>
              <ul className="text-start text-muted">
                <li>No students have rated lectures in your stream yet</li>
                <li>Lecturers in your stream haven't submitted reports</li>
                <li>There might be a data loading issue</li>
              </ul>
              
              <div className="mt-4">
                <button className="btn btn-primary me-2" onClick={fetchData}>
                  ðŸ”„ Check Again
                </button>
                <button className="btn btn-outline-secondary" onClick={loadSampleData}>
                  ðŸ§ª Load Sample Data
                </button>
              </div>
            </div>
          ) : (
            <>
              {/* Overall Statistics */}
              <div className="row mb-4">
                <div className="col-md-3">
                  <div className="stat-card">
                    <div className="stat-number">{stats.totalRatings}</div>
                    <div className="stat-label">Total Ratings</div>
                  </div>
                </div>
                <div className="col-md-3">
                  <div className="stat-card">
                    <div className="stat-number">{stats.avgRating}</div>
                    <div className="stat-label">Average Rating</div>
                  </div>
                </div>
                <div className="col-md-3">
                  <div className="stat-card">
                    <div className="stat-number">
                      {Object.keys(stats.lecturerStats).length}
                    </div>
                    <div className="stat-label">Lecturers Rated</div>
                  </div>
                </div>
                <div className="col-md-3">
                  <div className="stat-card">
                    <div className="stat-number">
                      {new Set(stats.filteredRatings.map(r => r.course_name)).size}
                    </div>
                    <div className="stat-label">Courses Rated</div>
                  </div>
                </div>
              </div>

              {/* Rating Distribution */}
              <div className="row mb-4">
                <div className="col-12">
                  <div className="card">
                    <div className="card-header">
                      <h5>â­ Rating Distribution</h5>
                    </div>
                    <div className="card-body">
                      {stats.ratingDistribution.map(dist => (
                        <div key={dist.stars} className="rating-dist-item mb-2">
                          <div className="d-flex justify-content-between align-items-center">
                            <span>
                              {'â­'.repeat(dist.stars)} 
                              <small className="text-muted ms-2">({dist.stars} stars)</small>
                            </span>
                            <div className="d-flex align-items-center">
                              <div className="progress" style={{width: '200px'}}>
                                <div 
                                  className="progress-bar bg-warning" 
                                  style={{width: `${dist.percentage}%`}}
                                ></div>
                              </div>
                              <span className="ms-2">
                                {dist.count} ({dist.percentage}%)
                              </span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              {/* Lecturer Performance */}
              <div className="row">
                <div className="col-12">
                  <div className="card">
                    <div className="card-header">
                      <h5>ðŸ‘¨â€ðŸ« Lecturer Performance</h5>
                    </div>
                    <div className="card-body">
                      <div className="table-responsive">
                        <table className="table table-striped">
                          <thead>
                            <tr>
                              <th>Lecturer</th>
                              <th>Average Rating</th>
                              <th>Total Ratings</th>
                              <th>Courses</th>
                              <th>Performance</th>
                            </tr>
                          </thead>
                          <tbody>
                            {Object.entries(stats.lecturerStats)
                              .sort(([,a], [,b]) => b.average - a.average)
                              .map(([lecturer, data]) => (
                                <tr key={lecturer}>
                                  <td>
                                    <strong>{lecturer}</strong>
                                  </td>
                                  <td>
                                    <span className="rating-stars">
                                      {'â­'.repeat(Math.round(data.average))}
                                    </span>
                                    <small className="text-muted ms-2">({data.average})</small>
                                  </td>
                                  <td>{data.total}</td>
                                  <td>
                                    {Array.from(new Set(data.ratings.map(r => r.course_name))).join(', ')}
                                  </td>
                                  <td>
                                    <span className={`badge ${
                                      data.average >= 4.5 ? 'bg-success' :
                                      data.average >= 3.5 ? 'bg-warning' :
                                      'bg-danger'
                                    }`}>
                                      {data.average >= 4.5 ? 'Excellent' :
                                       data.average >= 3.5 ? 'Good' :
                                       'Needs Improvement'}
                                    </span>
                                  </td>
                                </tr>
                              ))
                            }
                          </tbody>
                        </table>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Recent Ratings */}
              <div className="row mt-4">
