import React, { useState, useEffect } from 'react';
import { getPLReports, addFeedback } from '../../services/api';

const Reports = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    if (user && user.id) {
      fetchReports();
    }
  }, [user]);

  const fetchReports = async () => {
    try {
      setError(null);
      setLoading(true);
      const response = await getPLReports(user.id);
      
      // Handle different response structures
      if (response && response.data) {
        setReports(response.data);
      } else if (Array.isArray(response)) {
        setReports(response);
      } else {
        setReports([]);
      }
    } catch (error) {
      console.error('Error fetching PL reports:', error);
      setError('Failed to load reports. Please try again.');
      setReports([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitFeedback = async (e) => {
    e.preventDefault();
    if (!feedback.trim()) {
      alert('Please enter feedback text');
      return;
    }

    setSubmitting(true);
    try {
      await addFeedback({
        report_id: selectedReport.id,
        pl_id: user.id,
        feedback_text: feedback.trim()
      });
      alert('Feedback submitted successfully!');
      setSelectedReport(null);
      setFeedback('');
      fetchReports(); // Refresh to see updated feedback
    } catch (error) {
      console.error('Error submitting feedback:', error);
      alert('Error submitting feedback. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <div className="text-center p-4">Loading PL reports...</div>;
  
  if (error) return (
    <div className="alert alert-danger">
      {error}
      <button className="btn btn-sm btn-outline-danger ms-2" onClick={fetchReports}>
        Retry
      </button>
    </div>
  );

  return (
    <div className="row">
      <div className="col-md-8">
        <div className="card">
          <div className="card-header">
            <h4>Lecture Reports in My Program</h4>
          </div>
          <div className="card-body">
            {reports.length === 0 ? (
              <div className="text-center p-4">
                <p>No reports available in your program.</p>
                <button className="btn btn-primary" onClick={fetchReports}>
                  Refresh
                </button>
              </div>
            ) : (
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>Course</th>
                      <th>Lecturer</th>
                      <th>Date</th>
                      <th>Attendance</th>
                      <th>Topic</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reports.map((report) => (
                      <tr key={report.id || report.report_id}>
                        <td>{report.course_name || 'N/A'}</td>
                        <td>{report.lecturer_name || 'N/A'}</td>
                        <td>
                          {report.date_of_lecture ? 
                            new Date(report.date_of_lecture).toLocaleDateString() : 
                            'N/A'
                          }
                        </td>
                        <td>
                          {report.actual_students_present || 0}/{report.total_students_registered || 0}
                        </td>
                        <td>
                          {report.topic_taught ? 
                            (report.topic_taught.length > 50 ? 
                              report.topic_taught.substring(0, 50) + '...' : 
                              report.topic_taught
                            ) : 
                            'N/A'
                          }
                        </td>
                        <td>
                          <button 
                            className="btn btn-primary btn-sm"
                            onClick={() => setSelectedReport(report)}
                            disabled={submitting}
                          >
                            Add Feedback
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="col-md-4">
        {selectedReport && (
          <div className="card">
            <div className="card-header">
              <h5>Add Feedback</h5>
            </div>
            <div className="card-body">
              <p><strong>Course:</strong> {selectedReport.course_name || 'N/A'}</p>
              <p><strong>Lecturer:</strong> {selectedReport.lecturer_name || 'N/A'}</p>
              <p><strong>Topic:</strong> {selectedReport.topic_taught || 'N/A'}</p>
              
              <form onSubmit={handleSubmitFeedback}>
                <div className="mb-3">
                  <label className="form-label">Your Feedback</label>
                  <textarea
                    className="form-control"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    rows="5"
                    required
                    disabled={submitting}
                    placeholder="Enter your feedback here..."
                  />
                </div>
                <button 
                  type="submit" 
                  className="btn btn-success"
                  disabled={submitting || !feedback.trim()}
                >
                  {submitting ? 'Submitting...' : 'Submit Feedback'}
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary ms-2"
                  onClick={() => {
                    setSelectedReport(null);
                    setFeedback('');
                  }}
                  disabled={submitting}
                >
                  Cancel
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reports;
