import axios from 'axios';

// Create axios instance with better configuration
const API = axios.create({ 
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json'
  },
  timeout: 10000, // 10 second timeout
  withCredentials: true // Include cookies in requests
});

// Request interceptor with enhanced logging
API.interceptors.request.use((config) => {
  const timestamp = new Date().toISOString();
  console.log(`ðŸš€ [${timestamp}] API Request: ${config.method?.toUpperCase()} ${config.url}`, config.params || '');
  
  const token = localStorage.getItem('token') || sessionStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  
  // Add cache busting for GET requests
  if (config.method === 'get') {
    config.params = {
      ...config.params,
      _t: Date.now() // Cache buster
    };
  }
  
  return config;
}, (error) => {
  console.error('âŒ Request Interceptor Error:', error);
  return Promise.reject(error);
});

// Response interceptor with enhanced error handling
API.interceptors.response.use(
  (response) => {
    console.log(`âœ… API Success: ${response.status} ${response.config.url}`, response.data);
    return response;
  },
  (error) => {
    const errorMessage = error.response?.data?.message || error.message;
    const status = error.response?.status;
    
    console.error(`âŒ API Error ${status}: ${error.config?.url}`, {
      message: errorMessage,
      status: status,
      data: error.response?.data
    });
    
    // Handle specific HTTP status codes
    if (status === 401) {
      console.warn('ðŸ›‘ Authentication failed, redirecting to login...');
      localStorage.removeItem('token');
      sessionStorage.removeItem('token');
      window.location.href = '/login';
    } else if (status === 403) {
      console.warn('â›” Access forbidden');
    } else if (status === 404) {
      console.warn('ðŸ” Resource not found');
    } else if (status >= 500) {
      console.error('ðŸ”¥ Server error occurred');
    }
    
    return Promise.reject({
      message: errorMessage,
      status: status,
      data: error.response?.data,
      originalError: error
    });
  }
);

// Generic API call function with retry logic
const apiCallWithRetry = async (apiCall, maxRetries = 2, delay = 1000) => {
  for (let attempt = 1; attempt <= maxRetries + 1; attempt++) {
    try {
      return await apiCall();
    } catch (error) {
      if (attempt > maxRetries) {
        throw error;
      }
      
      console.warn(`ðŸ”„ Attempt ${attempt} failed, retrying in ${delay}ms...`);
      await new Promise(resolve => setTimeout(resolve, delay * attempt));
    }
  }
};

// Guest endpoints
export const getGuestDashboard = () => apiCallWithRetry(() => API.get('/guest/dashboard'));
export const searchReports = (query) => apiCallWithRetry(() => API.get(`/guest/search?query=${encodeURIComponent(query)}`));

// Auth endpoints
export const login = (userData) => apiCallWithRetry(() => API.post('/auth/login', userData));
export const register = (userData) => apiCallWithRetry(() => API.post('/auth/register', userData));
export const logout = () => apiCallWithRetry(() => API.post('/auth/logout'));

// Student endpoints with enhanced error handling
export const getStudentMonitoring = (studentId) => {
  console.log('ðŸŽ“ Fetching student monitoring for:', studentId);
  return apiCallWithRetry(() => API.get(`/students/${studentId}/monitoring`));
};

export const submitRating = (ratingData) => {
  console.log('â­ Submitting rating:', ratingData);
  return apiCallWithRetry(() => API.post('/students/ratings', ratingData));
};

export const getStudentRatings = (studentId) => {
  console.log('ðŸ“Š Fetching student ratings for:', studentId);
  return apiCallWithRetry(() => API.get(`/students/${studentId}/ratings`));
