import React, { useState, useEffect } from 'react';
import { getPRLReports, addFeedback } from '../../services/api';
import './PRL.css'; // Import the PRL CSS

const Reports = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await getPRLReports(user.id);
      setReports(response.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitFeedback = async (e) => {
    e.preventDefault();
    try {
      await addFeedback({
        report_id: selectedReport.id,
        prl_id: user.id,
        feedback_text: feedback
      });
      alert('Feedback submitted successfully!');
      setSelectedReport(null);
      setFeedback('');
      fetchReports(); // Refresh to see updated feedback
    } catch (error) {
      alert('Error submitting feedback');
    }
  };

  if (loading) return <div>Loading reports...</div>;

  return (
    <div className="row">
      <div className="col-md-8">
        <div className="card">
          <div className="card-header">
            <h4>Lecture Reports in My Stream</h4>
          </div>
          <div className="card-body">
            {reports.length === 0 ? (
              <p>No reports available.</p>
            ) : (
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>Course</th>
                      <th>Lecturer</th>
                      <th>Date</th>
                      <th>Attendance</th>
                      <th>Topic</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reports.map((report) => (
                      <tr key={report.id}>
                        <td>{report.course_name}</td>
                        <td>{report.lecturer_name}</td>
                        <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                        <td>{report.actual_students_present}/{report.total_students_registered}</td>
                        <td>{report.topic_taught.substring(0, 50)}...</td>
                        <td>
                          <button 
                            className="btn btn-primary btn-sm"
                            onClick={() => setSelectedReport(report)}
                          >
                            Add Feedback
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="col-md-4">
        {selectedReport && (
          <div className="card">
            <div className="card-header">
              <h5>Add Feedback</h5>
            </div>
            <div className="card-body">
              <p><strong>Course:</strong> {selectedReport.course_name}</p>
              <p><strong>Lecturer:</strong> {selectedReport.lecturer_name}</p>
              <p><strong>Topic:</strong> {selectedReport.topic_taught}</p>
              
              <form onSubmit={handleSubmitFeedback}>
                <div className="mb-3">
                  <label className="form-label">Your Feedback</label>
                  <textarea
                    className="form-control"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    rows="5"
                    required
                  />
                </div>
                <button type="submit" className="btn btn-success">Submit Feedback</button>
                <button 
                  type="button" 
                  className="btn btn-secondary ms-2"
                  onClick={() => {
                    setSelectedReport(null);
                    setFeedback('');
                  }}
                >
                  Cancel
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reports;
