import React, { useState, useEffect } from 'react';
import { getPRLCourses } from '../../services/api';

const Classes = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await getPRLCourses(user.id);
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading classes data...</div>;

  return (
    <div className="card">
      <div className="card-header">
        <h4>Classes in My Stream</h4>
      </div>
      <div className="card-body">
        {courses.length === 0 ? (
          <p>No classes found in your stream.</p>
        ) : (
          <div className="row">
            {courses.map((course) => (
              <div key={course.id} className="col-md-6 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">{course.course_name}</h5>
                    <p className="card-text">
                      <strong>Code:</strong> {course.course_code}<br/>
                      <strong>Stream:</strong> {course.stream}<br/>
                      <strong>Status:</strong> {course.assigned_lecturer_id ? 'Active' : 'Not Assigned'}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Classes;
