import React, { useState, useEffect } from 'react';
import { getStudentMonitoring, getStudentEnrollments } from '../../services/api';
import './Student.css';

const Monitoring = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');
      
      console.log('🔄 Fetching data for student:', user);
      
      const [reportsRes, enrollmentsRes] = await Promise.all([
        getStudentMonitoring(user.id),
        getStudentEnrollments(user.id)
      ]);
      
      console.log('📊 Reports API Response:', reportsRes);
      console.log('🎓 Enrollments API Response:', enrollmentsRes);
      
      // Handle different response formats
      const reportsData = reportsRes.data || reportsRes || [];
      const enrollmentsData = enrollmentsRes.data || enrollmentsRes || [];
      
      setReports(reportsData);
      setEnrollments(enrollmentsData);
      
      console.log('✅ Data loaded successfully:');
      console.log('   - Reports:', reportsData.length);
      console.log('   - Enrollments:', enrollmentsData.length);
      console.log('   - Enrollment details:', enrollmentsData);
      
    } catch (error) {
      console.error('❌ Error fetching monitoring data:', error);
      setError(`Failed to load data: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Enhanced filtering with better debugging
  const filteredReports = selectedCourse === 'all' 
    ? reports.filter(report => {
        const isEnrolled = enrollments.some(enrollment => 
          enrollment.course_code === report.course_code || 
          enrollment.course_id === report.course_id
        );
        console.log(`📋 Report ${report.id} - Course: ${report.course_code}, Enrolled: ${isEnrolled}`);
        return isEnrolled;
      })
    : reports.filter(report => {
        const matches = report.course_code === selectedCourse;
        console.log(`🎯 Filtering report ${report.id} - ${report.course_code} vs ${selectedCourse}: ${matches}`);
        return matches;
      });

  // Calculate statistics for better UX
  const stats = {
    totalReports: reports.length,
    filteredReports: filteredReports.length,
    enrolledCourses: enrollments.length,
    hasData: reports.length > 0 && enrollments.length > 0
  };

  if (loading) {
    return (
      <div className="student-monitoring-container student-fade-in">
        <div className="student-loading">
          <div className="student-loading-spinner"></div>
          <p>Loading your lecture reports...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="student-monitoring-container student-fade-in">
        <div className="student-alert student-alert-danger">
          <h5>⚠️ Unable to Load Reports</h5>
          <p>{error}</p>
          <button className="guest-btn guest-btn-primary" onClick={fetchData}>
            🔄 Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="student-monitoring-container student-fade-in">
      <div className="student-monitoring-header">
        <div className="student-monitoring-title-section">
          <h4>📊 Lecture Reports Monitoring</h4>
          <p className="student-monitoring-subtitle">
            View reports submitted by lecturers for your enrolled courses
          </p>
        </div>
        
        <div className="student-monitoring-controls">
          <div className="student-monitoring-stats">
            <span className="student-stat-badge">
              {stats.totalReports} total • {stats.filteredReports} filtered
            </span>
          </div>
          
          <select 
            className="student-monitoring-select"
            value={selectedCourse}
            onChange={(e) => setSelectedCourse(e.target.value)}
          >
            <option value="all">📚 All My Courses</option>
            {enrollments.map((enrollment) => (
              <option key={enrollment.course_code || enrollment.id} value={enrollment.course_code}>
                {enrollment.course_name} ({enrollment.course_code})
              </option>
            ))}
          </select>
          
          <button 
            className="student-monitoring-refresh"
            onClick={fetchData}
            title="Refresh reports"
          >
            🔄
          </button>
        </div>
      </div>

      <div className="student-monitoring-content">
        {enrollments.length === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">🎓</div>
            <h5>No Courses Enrolled</h5>
            <p>You haven't enrolled in any courses yet.</p>
            <p className="text-muted">
              Visit the <strong>My Classes</strong> section to enroll in courses and see lecture reports here.
            </p>
          </div>
        ) : stats.totalReports === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">📋</div>
            <h5>No Reports Available</h5>
            <p>No lecture reports have been submitted for your enrolled courses yet.</p>
            <p className="text-muted">
              Reports will appear here when lecturers submit them after each lecture session.
            </p>
            <button className="guest-btn guest-btn-primary" onClick={fetchData}>
              Check for New Reports
            </button>
          </div>
        ) : filteredReports.length === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">🔍</div>
            <h5>No Reports Match Your Filter</h5>
            <p>
              No reports found for {selectedCourse === 'all' ? 'your enrolled courses' : 'the selected course'}.
            </p>
            <div className="student-action-buttons">
              <button 
                className="guest-btn guest-btn-outline"
                onClick={() => setSelectedCourse('all')}
              >
                Show All Courses
              </button>
              <button className="guest-btn guest-btn-primary" onClick={fetchData}>
                Refresh Data
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="student-monitoring-summary">
              <p>
                📈 Showing <strong>{filteredReports.length}</strong> report(s) for{' '}
                <strong>
                  {selectedCourse === 'all' 
                    ? 'all your courses' 
                    : enrollments.find(e => e.course_code === selectedCourse)?.course_name
                  }
                </strong>
              </p>
            </div>

            <div className="student-table-container">
              <table className="student-monitoring-table">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Date</th>
                    <th>Topic Covered</th>
                    <th>Attendance</th>
                    <th>Venue</th>
                    <th>Week</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map((report) => {
                    const attendanceRate = report.actual_students_present / report.total_students_registered;
                    const attendanceClass = attendanceRate > 0.7 ? 'high' : attendanceRate > 0.5 ? 'medium' : 'low';
                    
                    return (
                      <tr key={report.id} className="student-report-row">
                        <td>
                          <div className="student-course-info">
                            <strong>{report.course_name}</strong>
                            <span className="student-course-code">{report.course_code}</span>
                          </div>
                        </td>
                        <td className="student-lecturer-info">
                          👨‍🏫 {report.lecturer_name}
                        </td>
                        <td className="student-date-info">
                          {new Date(report.date_of_lecture).toLocaleDateString('en-US', {
                            weekday: 'short',
                            month: 'short',
                            day: 'numeric'
                          })}
                        </td>
                        <td>
                          <div 
                            className="student-topic-info"
                            title={report.topic_taught}
                          >
                            {report.topic_taught}
                          </div>
                        </td>
                        <td>
                          <span className={`student-attendance-badge ${attendanceClass}`}>
                            {report.actual_students_present}/{report.total_students_registered}
                            <small>({Math.round(attendanceRate * 100)}%)</small>
                          </span>
                        </td>
                        <td className="student-venue-info">
                          🏢 {report.venue}
                        </td>
                        <td className="student-week-info">
                          {report.week_of_reporting}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>

            {/* Quick Stats Footer */}
            <div className="student-monitoring-footer">
              <div className="student-quick-stats">
                <span>📊 {filteredReports.length} reports</span>
                <span>•</span>
                <span>👥 {new Set(filteredReports.map(r => r.lecturer_name)).size} lecturers</span>
                <span>•</span>
                <span>📚 {new Set(filteredReports.map(r => r.course_name)).size} courses</span>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Monitoring;
