import React, { useState } from 'react';
import { Link, Routes, Route } from 'react-router-dom';
import { logout } from '../../utils/auth';
import Classes from './Classes';
import Reports from './Reports';
import ReportForm from './ReportForm';
import Monitoring from './Monitoring';
import Rating from './Rating';
import './LecturerDashboard.css'; // Import the CSS

const LecturerDashboard = ({ user }) => {
  const [activeTab, setActiveTab] = useState('dashboard');

  const handleLogout = () => {
    logout();
    window.location.href = '/login';
  };

  return (
    <div className="lecturer-dashboard">
      <nav className="navbar navbar-expand-lg navbar-dark lecturer-navbar">
        <div className="container">
          <span className="navbar-brand">üìä Lecturer Dashboard</span>
          <div className="navbar-nav ms-auto">
            <span className="navbar-text me-3">Welcome, {user?.name}</span>
            <button className="btn lecturer-logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <div className="row">
          <div className="col-md-9">
            <div className="content-area">
              {activeTab === 'dashboard' && (
                <div className="dashboard-overview">
                  <div className="dashboard-header">
                    <h4>üëã Welcome, {user?.name}</h4>
                  </div>
                  <div className="dashboard-body">
                    <p>Welcome to your Lecturer Dashboard. Here's what you can do:</p>
                    <ul>
                      <li>üìö View and manage your classes</li>
                      <li>üìä Submit and view lecture reports</li>
                      <li>üìà Monitor your reporting statistics</li>
                      <li>‚≠ê View student ratings and feedback</li>
                      <li>üìã Track your teaching progress</li>
                    </ul>
                    
                    <div className="stats-grid">
                      <div className="stat-card">
                        <div className="stat-number">12</div>
                        <div className="stat-label">Classes This Week</div>
                      </div>
                      <div className="stat-card">
                        <div className="stat-number">8</div>
                        <div className="stat-label">Reports Submitted</div>
                      </div>
                      <div className="stat-card">
                        <div className="stat-number">4.8</div>
                        <div className="stat-label">Average Rating</div>
                      </div>
                      <div className="stat-card">
                        <div className="stat-number">95%</div>
                        <div className="stat-label">Attendance Rate</div>
                      </div>
                    </div>
                    
                    <div className="quick-actions">
                      <button 
                        className="action-btn"
                        onClick={() => setActiveTab('new-report')}
                      >
                        üìù New Report
                      </button>
                      <button 
                        className="action-btn"
                        onClick={() => setActiveTab('classes')}
                      >
                        üìö My Classes
                      </button>
                      <button 
                        className="action-btn"
                        onClick={() => setActiveTab('reports')}
                      >
                        üìä View Reports
                      </button>
                      <button 
                        className="action-btn"
                        onClick={() => setActiveTab('rating')}
                      >
                        ‚≠ê View Ratings
                      </button>
                    </div>
                  </div>
                </div>
              )}
              {activeTab === 'classes' && <Classes user={user} />}
              {activeTab === 'reports' && <Reports user={user} />}
              {activeTab === 'new-report' && <ReportForm user={user} />}
              {activeTab === 'monitoring' && <Monitoring user={user} />}
              {activeTab === 'rating' && <Rating user={user} />}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LecturerDashboard;
