import React, { useState, useContext } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './Navbar.css';

// Create Auth Context
const AuthContext = React.createContext();

// Auth Provider Component (should be in a separate file, but including for context)
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [userRole, setUserRole] = useState(null);

  const login = (userData) => {
    setUser(userData);
    setUserRole(userData.role);
  };

  const logout = () => {
    setUser(null);
    setUserRole(null);
  };

  return (
    <AuthContext.Provider value={{ user, userRole, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Main Navbar Component
const Navbar = () => {
  const { user, userRole, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
    setIsMobileMenuOpen(false);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };

  // Get navigation links based on user role
  const getNavLinks = () => {
    switch (userRole) {
      case 'student':
        return [
          { path: '/student', label: 'Dashboard', icon: '📊' },
          { path: '/student/monitoring', label: 'Monitoring', icon: '👀' },
          { path: '/student/rating', label: 'Rating', icon: '⭐' },
          { path: '/student/reports', label: 'My Reports', icon: '📋' }
        ];
      case 'lecturer':
        return [
          { path: '/lecturer', label: 'Dashboard', icon: '📊' },
          { path: '/lecturer/classes', label: 'My Classes', icon: '🏫' },
          { path: '/lecturer/reports', label: 'Reports', icon: '📋' },
          { path: '/lecturer/create-report', label: 'Create Report', icon: '✏️' },
          { path: '/lecturer/monitoring', label: 'Monitoring', icon: '👀' },
          { path: '/lecturer/rating', label: 'Rating', icon: '⭐' }
        ];
      case 'prl':
        return [
          { path: '/prl', label: 'Dashboard', icon: '📊' },
          { path: '/prl/courses', label: 'Courses', icon: '📚' },
          { path: '/prl/reports', label: 'Reports', icon: '📋' },
          { path: '/prl/monitoring', label: 'Monitoring', icon: '👀' },
          { path: '/prl/rating', label: 'Rating', icon: '⭐' },
          { path: '/prl/classes', label: 'Classes', icon: '🏫' }
        ];
      case 'pl':
        return [
          { path: '/pl', label: 'Dashboard', icon: '📊' },
          { path: '/pl/courses', label: 'Courses', icon: '📚' },
          { path: '/pl/reports', label: 'Reports', icon: '📋' },
          { path: '/pl/monitoring', label: 'Monitoring', icon: '👀' },
          { path: '/pl/classes', label: 'Classes', icon: '🏫' },
          { path: '/pl/lecturers', label: 'Lecturers', icon: '👨‍🏫' },
          { path: '/pl/rating', label: 'Rating', icon: '⭐' }
        ];
      default:
        return [];
    }
  };

  // Get user display name and role label
  const getUserDisplayInfo = () => {
    const roleLabels = {
      student: 'Student',
      lecturer: 'Lecturer',
      prl: 'Principal Lecturer',
      pl: 'Program Leader'
    };

    return {
      name: user?.name || user?.email || 'User',
      role: roleLabels[userRole] || 'Unknown Role'
    };
  };

  const { name, role } = getUserDisplayInfo();
  const navLinks = getNavLinks();

  // Get the base dashboard path based on user role
  const getDashboardPath = () => {
    switch (userRole) {
      case 'student': return '/student';
      case 'lecturer': return '/lecturer';
      case 'prl': return '/prl';
      case 'pl': return '/pl';
      default: return '/';
    }
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        {/* Logo/Brand */}
        <Link to={getDashboardPath()} className="nav-logo" onClick={closeMobileMenu}>
          <span className="logo-icon">🎓</span>
          LUCT Reporting System
        </Link>

        {/* Mobile Menu Toggle */}
        <div className={`nav-toggle ${isMobileMenuOpen ? 'active' : ''}`} onClick={toggleMobileMenu}>
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>

        {/* Navigation Links */}
        <ul className={`nav-menu ${isMobileMenuOpen ? 'nav-menu-active' : ''}`}>
          {navLinks.map((link) => (
            <li key={link.path} className="nav-item">
              <Link
                to={link.path}
                className={`nav-link ${
                  location.pathname === link.path || 
                  (link.path !== `/${userRole}` && location.pathname.startsWith(link.path)) 
                    ? 'active' : ''
                }`}
                onClick={closeMobileMenu}
              >
                <span className="nav-icon">{link.icon}</span>
                {link.label}
              </Link>
            </li>
          ))}
        </ul>

        {/* User Info and Logout */}
        <div className="nav-user-section">
          <div className="user-info">
            <span className="user-name">{name}</span>
            <span className="user-role">{role}</span>
          </div>
          <button 
            className="logout-btn"
            onClick={handleLogout}
            title="Logout"
          >
            <span className="logout-icon">🚪</span>
            Logout
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
