import React, { useState, useEffect } from 'react';
import { getPLDashboardStats } from '../../services/api';
import './PL.css';

const PLDashboard = ({ user }) => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const response = await getPLDashboardStats();
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
      // Set default stats for demo
      setStats({
        totalCourses: 12,
        totalLecturers: 8,
        totalReports: 45,
        feedbackRate: 78,
        recentActivity: [
          { date: '2024-01-20', description: 'New report submitted for Web Development' },
          { date: '2024-01-19', description: 'Lecturer assigned to Database Systems' },
          { date: '2024-01-18', description: 'Feedback provided for Network Security' }
        ]
      });
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="pl-loading">
        <div className="pl-loading-spinner"></div>
        <span>Loading dashboard...</span>
      </div>
    );
  }

  return (
    <div className="pl-dashboard">
      <div className="pl-dashboard-overview pl-fade-in">
        <div className="pl-dashboard-header">
          <h4>Welcome back, {user?.name || 'Program Leader'}!</h4>
          <p className="mb-0">Manage your program efficiently with real-time insights</p>
        </div>
        
        <div className="pl-dashboard-body">
          {/* Quick Stats */}
          <div className="row mb-4">
            <div className="col-md-3">
              <div className="pl-analytics-card primary">
                <div className="pl-analytics-value">{stats?.totalCourses || 0}</div>
                <div className="pl-analytics-label">Total Courses</div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="pl-analytics-card success">
                <div className="pl-analytics-value">{stats?.totalLecturers || 0}</div>
                <div className="pl-analytics-label">Active Lecturers</div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="pl-analytics-card info">
                <div className="pl-analytics-value">{stats?.totalReports || 0}</div>
                <div className="pl-analytics-label">Reports This Month</div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="pl-analytics-card warning">
                <div className="pl-analytics-value">{stats?.feedbackRate || 0}%</div>
                <div className="pl-analytics-label">Feedback Rate</div>
              </div>
            </div>
          </div>

          {/* Program Overview */}
          <div className="row">
            <div className="col-md-8">
              <div className="card h-100">
                <div className="card-header">
                  <h6 className="mb-0">ðŸ“Š Program Overview</h6>
                </div>
                <div className="card-body">
                  <div className="row text-center">
                    <div className="col-md-4 border-end">
                      <div className="h4 text-primary mb-1">{stats?.totalCourses || 0}</div>
                      <small className="text-muted">Active Courses</small>
                    </div>
                    <div className="col-md-4 border-end">
                      <div className="h4 text-success mb-1">{stats?.totalLecturers || 0}</div>
                      <small className="text-muted">Teaching Staff</small>
                    </div>
                    <div className="col-md-4">
                      <div className="h4 text-info mb-1">{stats?.totalReports || 0}</div>
                      <small className="text-muted">Monthly Reports</small>
                    </div>
                  </div>
                  
                  <div className="mt-4">
                    <h6>Key Responsibilities:</h6>
                    <ul className="list-unstyled">
                      <li className="mb-2">âœ… Manage course assignments and schedules</li>
                      <li className="mb-2">âœ… Monitor lecturer performance and feedback</li>
                      <li className="mb-2">âœ… Review program reports and analytics</li>
                      <li className="mb-2">âœ… Ensure curriculum quality and compliance</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="col-md-4">
              <div className="card h-100">
                <div className="card-header">
                  <h6 className="mb-0">ðŸ“ˆ Recent Activity</h6>
                </div>
                <div className="card-body">
                  {stats?.recentActivity?.length > 0 ? (
                    <div className="activity-timeline">
                      {stats.recentActivity.map((activity, index) => (
                        <div key={index} className="activity-item mb-3 p-2 border-start border-primary border-3 ps-3">
                          <small className="text-muted d-block">{activity.date}</small>
                          <strong className="small">{activity.description}</strong>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="text-muted mb-0">No recent activity</p>
                  )}
                  
                  <div className="mt-3 pt-3 border-top">
                    <button className="btn btn-sm btn-outline-primary w-100">
                      View All Activity
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PLDashboard;
