import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  getStudentMonitoring, 
  getStudentEnrollments, 
  getStudentRatings,
  subscribeToReports 
} from '../../services/api';
import './Student.css';

const Monitoring = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [pollingInterval, setPollingInterval] = useState(null);
  const navigate = useNavigate();

  // Memoized data fetcher
  const fetchData = useCallback(async (showLoading = true) => {
    if (showLoading) setLoading(true);
    setError('');
    
    try {
      console.log('🔄 Fetching latest data for student:', user.id);
      
      const [reportsRes, enrollmentsRes, ratingsRes] = await Promise.allSettled([
        getStudentMonitoring(user.id),
        getStudentEnrollments(user.id),
        getStudentRatings(user.id)
      ]);
      
      // Handle responses with error checking
      const reportsData = reportsRes.status === 'fulfilled' 
        ? (reportsRes.value.data || reportsRes.value || [])
        : [];
        
      const enrollmentsData = enrollmentsRes.status === 'fulfilled'
        ? (enrollmentsRes.value.data || enrollmentsRes.value || [])
        : [];
        
      const ratingsData = ratingsRes.status === 'fulfilled'
        ? (ratingsRes.value.data || ratingsRes.value || [])
        : [];
      
      // Check for individual errors
      const errors = [];
      if (reportsRes.status === 'rejected') errors.push(`Reports: ${reportsRes.reason.message}`);
      if (enrollmentsRes.status === 'rejected') errors.push(`Enrollments: ${enrollmentsRes.reason.message}`);
      if (ratingsRes.status === 'rejected') errors.push(`Ratings: ${ratingsRes.reason.message}`);
      
      if (errors.length > 0) {
        console.warn('⚠️ Partial data loaded with errors:', errors);
      }
      
      setReports(reportsData);
      setEnrollments(enrollmentsData);
      setRatings(ratingsData);
      setLastUpdate(new Date());
      
      console.log('✅ Data refresh completed:', {
        reports: reportsData.length,
        enrollments: enrollmentsData.length,
        ratings: ratingsData.length,
        errors: errors.length
      });
      
      if (errors.length === 3) {
        throw new Error('All API calls failed');
      }
      
    } catch (error) {
      console.error('❌ Error fetching monitoring data:', error);
      const errorMsg = error.message || 'Failed to load monitoring data';
      setError(errorMsg);
    } finally {
      if (showLoading) setLoading(false);
    }
  }, [user.id]);

  // Initial data load
  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // Real-time subscription with better cleanup
  useEffect(() => {
    if (!user?.id) return;

    let unsubscribe = null;
    let pollInterval = null;

    const setupRealTimeUpdates = async () => {
      try {
        console.log('🔔 Setting up real-time updates for student:', user.id);
        
        unsubscribe = subscribeToReports(user.id, (newReport) => {
          console.log('📨 New report received via real-time:', newReport);
          
          setReports(prevReports => {
            const exists = prevReports.some(report => report.id === newReport.id);
            if (!exists) {
              console.log('🆕 Adding new report to list:', newReport.course_name);
              // Show notification to user
              if ('Notification' in window && Notification.permission === 'granted') {
                new Notification('New Lecture Report', {
                  body: `New report for ${newReport.course_name} by ${newReport.lecturer_name}`,
                  icon: '/favicon.ico'
                });
              }
              return [newReport, ...prevReports];
            }
            return prevReports;
          });
          
          setLastUpdate(new Date());
        });

        setIsSubscribed(true);
        
      } catch (error) {
        console.error('❌ Error setting up real-time updates, falling back to polling:', error);
        // Fallback to polling
        pollInterval = setInterval(() => {
          console.log('⏰ Polling for updates...');
          fetchData(false); // Silent refresh
        }, 30000);
        
        setPollingInterval(pollInterval);
      }
    };

    setupRealTimeUpdates();

    // Request notification permission
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }

    // Cleanup function
    return () => {
      if (unsubscribe) unsubscribe();
      if (pollInterval) clearInterval(pollInterval);
      setIsSubscribed(false);
    };
  }, [user.id, fetchData]);

  // Manual refresh with loading state
  const handleManualRefresh = async () => {
    await fetchData();
  };

  // Enhanced filtering with multiple matching strategies
  const filteredReports = useMemo(() => {
    if (selectedCourse === 'all') {
      return reports.filter(report => 
        enrollments.some(enrollment => 
          enrollment.course_code === report.course_code ||
          enrollment.course_id === report.course_id ||
          enrollment.course_name === report.course_name
        )
      );
    }
    
    return reports.filter(report => 
      report.course_code === selectedCourse || 
      report.course_name === selectedCourse
    );
  }, [reports, enrollments, selectedCourse]);

  // Enhanced statistics
  const stats = useMemo(() => {
    const totalAttendance = filteredReports.reduce((sum, report) => 
      sum + (report.actual_students_present / report.total_students_registered), 0
    );
    const avgAttendance = filteredReports.length > 0 ? totalAttendance / filteredReports.length : 0;
    
    return {
      totalReports: reports.length,
      filteredReports: filteredReports.length,
      enrolledCourses: enrollments.length,
      avgAttendance: Math.round(avgAttendance * 100),
      ratedReports: filteredReports.filter(report => 
        ratings.some(rating => rating.report_id === report.id)
      ).length,
      uniqueLecturers: new Set(filteredReports.map(r => r.lecturer_name)).size
    };
  }, [reports, filteredReports, enrollments, ratings]);

  // Check if student has rated a report
  const hasRatedReport = useCallback((reportId) => {
    return ratings.some(rating => rating.report_id === reportId);
  }, [ratings]);

  const getStudentRating = useCallback((reportId) => {
    const rating = ratings.find(r => r.report_id === reportId);
    return rating ? rating.rating_value : null;
  }, [ratings]);

  // Navigation functions
  const navigateToRating = useCallback((reportId) => {
    navigate(`/student/rating?reportId=${reportId}`);
  }, [navigate]);

  const navigateToReportDetails = useCallback((reportId) => {
    navigate(`/student/reports/${reportId}`);
  }, [navigate]);

  // Format time since last update
  const getTimeSinceUpdate = useCallback(() => {
    const now = new Date();
    const diffInSeconds = Math.floor((now - lastUpdate) / 1000);
    
    if (diffInSeconds < 60) return 'just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)} min ago`;
    return `${Math.floor(diffInSeconds / 3600)} hour${Math.floor(diffInSeconds / 3600) > 1 ? 's' : ''} ago`;
  }, [lastUpdate]);

  // Check if report is new (within last 10 minutes)
  const isNewReport = useCallback((report, index) => {
    return index < 3 && Date.now() - new Date(report.created_at || report.date_of_lecture).getTime() < 600000;
  }, []);

  // Loading state
  if (loading) {
    return (
      <div className="student-monitoring-container student-fade-in">
        <div className="student-loading">
          <div className="student-loading-spinner"></div>
          <p>Loading your lecture reports...</p>
          <small>Checking for new reports</small>
          <div className="student-loading-dots">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </div>
    );
  }

  // Error state
  if (error && reports.length === 0) {
    return (
      <div className="student-monitoring-container student-fade-in">
        <div className="student-alert student-alert-danger">
          <h5>⚠️ Unable to Load Reports</h5>
          <p>{error}</p>
          <div className="student-action-buttons">
            <button className="guest-btn guest-btn-primary" onClick={handleManualRefresh}>
              🔄 Try Again
            </button>
            <button 
              className="guest-btn guest-btn-outline" 
              onClick={() => window.location.reload()}
            >
              🔃 Reload Page
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="student-monitoring-container student-fade-in">
      {/* Debug panel for development */}
      {process.env.NODE_ENV === 'development' && (
        <div className="debug-panel">
          <strong>🧪 Debug Info:</strong>
          <div>Student: {user?.id} | Reports: {reports.length} | Enrollments: {enrollments.length} | Filtered: {filteredReports.length}</div>
          <div>Connection: {isSubscribed ? '🟢 LIVE' : pollingInterval ? '🟡 POLLING' : '🔴 OFFLINE'}</div>
        </div>
      )}

      <div className="student-monitoring-header">
        <div className="student-monitoring-title-section">
          <h4>📊 Live Lecture Reports Monitoring</h4>
          <p className="student-monitoring-subtitle">
            Real-time updates when lecturers submit new reports
            {isSubscribed && <span className="live-indicator"> ● LIVE</span>}
            {pollingInterval && <span className="polling-indicator"> ⏰ POLLING</span>}
          </p>
        </div>
        
        <div className="student-monitoring-controls">
          <div className="student-monitoring-stats">
            <span className="student-stat-badge">
              {stats.filteredReports} reports • {stats.ratedReports} rated • Updated {getTimeSinceUpdate()}
            </span>
          </div>
          
          <select 
            className="student-monitoring-select"
            value={selectedCourse}
            onChange={(e) => setSelectedCourse(e.target.value)}
            disabled={enrollments.length === 0}
          >
            <option value="all">📚 All My Courses ({enrollments.length})</option>
            {enrollments.map((enrollment) => (
              <option key={enrollment.course_code || enrollment.id} value={enrollment.course_code}>
                {enrollment.course_name} ({enrollment.course_code})
              </option>
            ))}
          </select>
          
          <button 
            className="student-monitoring-refresh"
            onClick={handleManualRefresh}
            title="Refresh reports"
            disabled={loading}
          >
            {loading ? '⏳' : '🔄'}
          </button>
        </div>
      </div>

      {/* Stats Overview */}
      {stats.filteredReports > 0 && (
        <div className="student-stats-overview">
          <div className="stat-card">
            <span className="stat-number">{stats.filteredReports}</span>
            <span className="stat-label">Total Reports</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.avgAttendance}%</span>
            <span className="stat-label">Avg Attendance</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.uniqueLecturers}</span>
            <span className="stat-label">Lecturers</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.ratedReports}</span>
            <span className="stat-label">Rated</span>
          </div>
        </div>
      )}

      <div className="student-monitoring-content">
        {enrollments.length === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">🎓</div>
            <h5>No Courses Enrolled</h5>
            <p>You haven't enrolled in any courses yet.</p>
            <button 
              className="guest-btn guest-btn-primary"
              onClick={() => navigate('/student/classes')}
            >
              Browse Available Courses
            </button>
          </div>
        ) : stats.filteredReports === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">📋</div>
            <h5>No Reports Available</h5>
            <p>No lecture reports found for your enrolled courses.</p>
            {error && <p className="text-warning">⚠️ Some data may not be loaded due to errors</p>}
            <div className="student-action-buttons">
              <button className="guest-btn guest-btn-primary" onClick={handleManualRefresh}>
                Check Again
              </button>
              <button 
                className="guest-btn guest-btn-outline"
                onClick={() => setSelectedCourse('all')}
              >
                Show All Courses
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="student-monitoring-summary">
              <p>
                📈 Showing <strong>{stats.filteredReports}</strong> report(s) for{' '}
                <strong>
                  {selectedCourse === 'all' 
                    ? 'all your courses' 
                    : enrollments.find(e => e.course_code === selectedCourse)?.course_name || selectedCourse
                  }
                </strong>
              </p>
            </div>

            <div className="student-table-container">
              <table className="student-monitoring-table">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Date & Time</th>
                    <th>Topic Covered</th>
                    <th>Attendance</th>
                    <th>Your Rating</th>
                    <th>Actions</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map((report, index) => {
                    const attendanceRate = report.actual_students_present / report.total_students_registered;
                    const attendanceClass = attendanceRate > 0.7 ? 'high' : attendanceRate > 0.5 ? 'medium' : 'low';
                    const hasRated = hasRatedReport(report.id);
                    const studentRating = getStudentRating(report.id);
                    const isNew = isNewReport(report, index);
                    
                    return (
                      <tr key={report.id} className={`student-report-row ${isNew ? 'new-report' : ''}`}>
                        <td>
                          <div className="student-course-info">
                            <strong>{report.course_name}</strong>
                            <span className="student-course-code">{report.course_code}</span>
                          </div>
                        </td>
                        <td className="student-lecturer-info">
                          <span className="lecturer-avatar">👨‍🏫</span>
                          {report.lecturer_name}
                        </td>
                        <td className="student-date-info">
                          <div className="date-primary">
                            {new Date(report.date_of_lecture).toLocaleDateString('en-US', {
                              weekday: 'short',
                              year: 'numeric',
                              month: 'short',
                              day: 'numeric'
                            })}
                          </div>
                          <small className="text-muted">
                            {new Date(report.created_at || report.date_of_lecture).toLocaleTimeString()}
                          </small>
                        </td>
                        <td>
                          <div className="student-topic-info" title={report.topic_taught}>
                            <strong>{report.topic_taught}</strong>
                            {report.learning_outcomes && (
                              <small className="text-muted d-block">
                                {report.learning_outcomes.substring(0, 60)}...
                              </small>
                            )}
                          </div>
                        </td>
                        <td>
                          <span className={`student-attendance-badge ${attendanceClass}`}>
                            {report.actual_students_present}/{report.total_students_registered}
                            <small>({Math.round(attendanceRate * 100)}%)</small>
                          </span>
                        </td>
                        <td className="student-rating-info">
                          {hasRated ? (
                            <div className="student-rating-display">
                              <span className="student-rating-stars">
                                {'⭐'.repeat(studentRating)}
                              </span>
                              <span className="student-rating-value">({studentRating}/5)</span>
                            </div>
                          ) : (
                            <span className="student-not-rated">Not rated</span>
                          )}
                        </td>
                        <td className="student-actions">
                          <div className="action-buttons">
                            <button 
                              className="student-view-report-btn"
                              onClick={() => navigateToReportDetails(report.id)}
                              title="View full report details"
                            >
                              📋
                            </button>
                            {!hasRated && (
                              <button 
                                className="student-rate-btn"
                                onClick={() => navigateToRating(report.id)}
                                title="Rate this lecture"
                              >
                                ⭐
                              </button>
                            )}
                          </div>
                        </td>
                        <td className="student-status">
                          {isNew && (
                            <span className="new-indicator" title="New report - submitted recently">
                              NEW
                            </span>
                          )}
                          {hasRated && (
                            <span className="rated-indicator" title="You've rated this lecture">
                              RATED
                            </span>
                          )}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>

            {/* Quick Stats Footer */}
            <div className="student-monitoring-footer">
              <div className="student-quick-stats">
                <span>📊 {stats.filteredReports} reports</span>
                <span>•</span>
                <span>👥 {stats.uniqueLecturers} lecturers</span>
                <span>•</span>
                <span>📚 {new Set(filteredReports.map(r => r.course_name)).size} courses</span>
                <span>•</span>
                <span>⭐ {stats.ratedReports} rated</span>
              </div>
              <div className="student-update-info">
                Last updated: {lastUpdate.toLocaleTimeString()}
                {isSubscribed && <span className="live-badge">LIVE</span>}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Monitoring;
