import React, { useState, useEffect } from 'react';
import { getPRLReports, addFeedback } from '../../services/api';

const Reports = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    fetchReports();
  }, [user?.id]);

  const fetchReports = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching reports...');
      
      console.log('PRL Reports - Fetching data for PRL:', user);
      
      const response = await getPRLReports(user.id);
      console.log('PRL Reports API Response:', response);
      
      const reportsData = response.data || response || [];
      setReports(reportsData);
      
      setDebugInfo(`
        PRL ID: ${user.id}
        Reports Found: ${reportsData.length}
        User Role: ${user.role}
      `);
      
    } catch (error) {
      console.error('Error fetching PRL reports:', error);
      setError(`Failed to load reports: ${error.message}`);
      setDebugInfo(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitFeedback = async (e) => {
    e.preventDefault();
    try {
      await addFeedback({
        report_id: selectedReport.id,
        prl_id: user.id,
        feedback_text: feedback
      });
      alert('Feedback submitted successfully!');
      setSelectedReport(null);
      setFeedback('');
      fetchReports(); // Refresh to see updated feedback
    } catch (error) {
      alert('Error submitting feedback');
    }
  };

  // Test function to load sample data
  const loadSampleData = () => {
    const sampleReports = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110",
        lecturer_name: "Dr. Smith",
        date_of_lecture: "2024-01-15",
        actual_students_present: 42,
        total_students_registered: 45,
        topic_taught: "Introduction to React and Component Architecture",
        venue: "Room 101",
        scheduled_time: "09:00-11:00",
        learning_outcomes: "Understanding React components, props, and state management",
        recommendations: "More practical examples needed",
        week_of_reporting: "Week 6",
        feedback_given: null
      },
      {
        id: 2,
        course_name: "Database Systems",
        course_code: "DBS3010",
        lecturer_name: "Prof. Johnson",
        date_of_lecture: "2024-01-16",
        actual_students_present: 35,
        total_students_registered: 38,
        topic_taught: "SQL Queries and Database Normalization",
        venue: "Lab 205",
        scheduled_time: "14:00-16:00",
        learning_outcomes: "Mastering complex SQL queries and normalization forms",
        recommendations: "Provide more exercises on JOIN operations",
        week_of_reporting: "Week 6",
        feedback_given: "Good coverage of normalization concepts"
      },
      {
        id: 3,
        course_name: "Network Security",
        course_code: "NS4010",
        lecturer_name: "Dr. Brown",
        date_of_lecture: "2024-01-17",
        actual_students_present: 22,
        total_students_registered: 25,
        topic_taught: "Cryptography and Encryption Algorithms",
        venue: "Room 305",
        scheduled_time: "11:00-13:00",
        learning_outcomes: "Understanding symmetric and asymmetric encryption",
        recommendations: "Include real-world case studies",
        week_of_reporting: "Week 6",
        feedback_given: null
      }
    ];

    setReports(sampleReports);
    setDebugInfo('Loaded sample data for testing');
    console.log('Sample reports loaded:', sampleReports);
  };

  if (loading) {
    return (
      <div className="prl-reports-container">
        <div className="loading-spinner"></div>
        <p>Loading reports data...</p>
        <small className="text-muted">{debugInfo}</small>
      </div>
    );
  }

  return (
    <div className="prl-reports-container">
      {/* Debug Panel */}
      <div className="debug-panel">
        <strong>PRL REPORTS DEBUG INFO</strong>
        <div>PRL ID: {user?.id} | Role: {user?.role}</div>
        <div>Reports Found: {reports.length}</div>
        <div>{debugInfo}</div>
        <div className="debug-actions">
          <button onClick={loadSampleData} className="btn-test">
            Load Sample Data
          </button>
        </div>
      </div>

      <div className="row">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h4>Lecture Reports in My Stream</h4>
              <span className="badge bg-primary">{reports.length} reports</span>
            </div>
            <div className="card-body">
              {error && (
                <div className="alert alert-warning">
                  <strong>Notice:</strong> {error}
                </div>
              )}

              {reports.length === 0 ? (
                <div className="text-center py-5">
                  <h5>No Reports Found in Your Stream</h5>
                  <p className="text-muted">
                    This could be because:
                  </p>
                  <ul className="text-start text-muted">
                    <li>No lecturers in your stream have submitted reports yet</li>
                    <li>There are no courses assigned to your stream</li>
                    <li>There might be a data loading issue</li>
                  </ul>
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-striped">
                    <thead>
                      <tr>
                        <th>Course Code</th>
                        <th>Course Name</th>
                        <th>Lecturer</th>
                        <th>Date</th>
                        <th>Attendance</th>
                        <th>Topic</th>
                        <th>Status</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {reports.map((report) => (
                        <tr key={report.id}>
                          <td>
                            <strong>{report.course_code}</strong>
                          </td>
                          <td>{report.course_name}</td>
                          <td>{report.lecturer_name}</td>
                          <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                          <td>
                            {report.actual_students_present}/{report.total_students_registered}
                            <br />
                            <small className="text-muted">
                              ({Math.round((report.actual_students_present / report.total_students_registered) * 100)}%)
                            </small>
                          </td>
                          <td>
                            <span title={report.topic_taught}>
                              {report.topic_taught.substring(0, 30)}
                              {report.topic_taught.length > 30 ? '...' : ''}
                            </span>
                          </td>
                          <td>
                            <span className={`badge ${report.feedback_given ? 'bg-success' : 'bg-warning'}`}>
                              {report.feedback_given ? 'Feedback Given' : 'Pending Feedback'}
                            </span>
                          </td>
                          <td>
                            <button 
                              className="btn btn-primary btn-sm"
                              onClick={() => setSelectedReport(report)}
                            >
                              {report.feedback_given ? 'View/Edit' : 'Add'} Feedback
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="col-md-4">
          {selectedReport && (
            <div className="card">
              <div className="card-header">
                <h5>
                  {selectedReport.feedback_given ? 'View/Edit Feedback' : 'Add Feedback'}
                </h5>
              </div>
              <div className="card-body">
                <div className="report-details mb-3">
                  <p><strong>Course:</strong> {selectedReport.course_name} ({selectedReport.course_code})</p>
                  <p><strong>Lecturer:</strong> {selectedReport.lecturer_name}</p>
                  <p><strong>Date:</strong> {new Date(selectedReport.date_of_lecture).toLocaleDateString()}</p>
                  <p><strong>Topic:</strong> {selectedReport.topic_taught}</p>
                  <p><strong>Attendance:</strong> {selectedReport.actual_students_present}/{selectedReport.total_students_registered}</p>
                  <p><strong>Venue:</strong> {selectedReport.venue}</p>
                  <p><strong>Time:</strong> {selectedReport.scheduled_time}</p>
                </div>
                
                <form onSubmit={handleSubmitFeedback}>
                  <div className="mb-3">
                    <label className="form-label">Your Feedback</label>
                    <textarea
                      className="form-control"
                      value={feedback}
                      onChange={(e) => setFeedback(e.target.value)}
                      rows="6"
                      required
                      placeholder="Provide constructive feedback on this lecture report..."
                    />
                  </div>
                  <div className="d-flex gap-2">
                    <button type="submit" className="btn btn-success">
                      {selectedReport.feedback_given ? 'Update Feedback' : 'Submit Feedback'}
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-secondary"
                      onClick={() => {
                        setSelectedReport(null);
                        setFeedback('');
                      }}
                    >
                      Cancel
                    </button>
                  </div>
                </form>

                {selectedReport.feedback_given && (
                  <div className="mt-3 p-2 bg-light rounded">
                    <small>
                      <strong>Previous Feedback:</strong> {selectedReport.feedback_given}
                    </small>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Reports;
