import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  getStudentMonitoring, 
  getStudentEnrollments, 
  getStudentRatings
} from '../../services/api';
import './Student.css';

const Monitoring = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [usingMockData, setUsingMockData] = useState(false);
  const navigate = useNavigate();

  // Fetch all data
  const fetchData = useCallback(async () => {
    try {
      setError('');
      setLoading(true);
      
      console.log('🔄 Fetching latest data for student:', user?.id);
      
      const [reportsRes, enrollmentsRes, ratingsRes] = await Promise.allSettled([
        getStudentMonitoring(user?.id),
        getStudentEnrollments(user?.id),
        getStudentRatings(user?.id)
      ]);
      
      // Handle responses
      let reportsData = [];
      let enrollmentsData = [];
      let ratingsData = [];
      let mockDataUsed = false;

      // Check if we're using mock data (no response.data structure in mock)
      if (reportsRes.status === 'fulfilled') {
        reportsData = reportsRes.value.data || reportsRes.value || [];
        if (!reportsRes.value.config) { // Mock data doesn't have config
          mockDataUsed = true;
        }
      }
      
      if (enrollmentsRes.status === 'fulfilled') {
        enrollmentsData = enrollmentsRes.value.data || enrollmentsRes.value || [];
      }
      
      if (ratingsRes.status === 'fulfilled') {
        ratingsData = ratingsRes.value.data || ratingsRes.value || [];
      }

      setUsingMockData(mockDataUsed);
      setReports(reportsData);
      setEnrollments(enrollmentsData);
      setRatings(ratingsData);
      setLastUpdate(new Date());
      
      console.log('✅ Data loaded:', {
        reports: reportsData.length,
        enrollments: enrollmentsData.length,
        ratings: ratingsData.length,
        usingMockData: mockDataUsed
      });
      
    } catch (error) {
      console.error('❌ Error fetching monitoring data:', error);
      setError(error.message || 'Failed to load data');
    } finally {
      setLoading(false);
    }
  }, [user?.id]);

  // Initial data load
  useEffect(() => {
    if (user?.id) {
      fetchData();
    }
  }, [user?.id, fetchData]);

  // Manual refresh
  const handleManualRefresh = async () => {
    await fetchData();
  };

  // Enhanced filtering
  const filteredReports = useMemo(() => {
    if (selectedCourse === 'all') {
      return reports.filter(report => 
        enrollments.some(enrollment => 
          enrollment.course_code === report.course_code
        )
      );
    }
    return reports.filter(report => report.course_code === selectedCourse);
  }, [reports, enrollments, selectedCourse]);

  // Debug info
  useEffect(() => {
    if (!loading) {
      console.log('🔍 CURRENT STATE:', {
        reportsCount: reports.length,
        enrollmentsCount: enrollments.length,
        filteredCount: filteredReports.length,
        selectedCourse,
        hasUser: !!user?.id
      });
    }
  }, [reports, enrollments, filteredReports, selectedCourse, loading, user]);

  if (loading) {
    return (
      <div className="student-monitoring-container">
        <div className="student-loading">
          <div className="student-loading-spinner"></div>
          <p>Loading your lecture reports...</p>
          <small>Fetching data from server</small>
        </div>
      </div>
    );
  }

  return (
    <div className="student-monitoring-container">
      {/* Debug Info - Remove in production */}
      <div style={{ 
        background: '#f8f9fa', 
        padding: '10px', 
        marginBottom: '20px', 
        borderRadius: '5px',
        border: '1px solid #dee2e6',
        fontSize: '12px'
      }}>
        <strong>🧪 Debug Info:</strong>
        <div>User: {user?.id} | Role: {user?.role}</div>
        <div>Reports: {reports.length} | Enrollments: {enrollments.length} | Filtered: {filteredReports.length}</div>
        <div>Using {usingMockData ? 'MOCK DATA' : 'REAL API'}</div>
      </div>

      <div className="student-monitoring-header">
        <div className="student-monitoring-title-section">
          <h4>📊 Lecture Reports Monitoring</h4>
          <p className="student-monitoring-subtitle">
            View reports submitted by lecturers for your enrolled courses
            {usingMockData && <span style={{color: 'orange', marginLeft: '10px'}}>⚠️ USING DEMO DATA</span>}
          </p>
        </div>
        
        <div className="student-monitoring-controls">
          <div className="student-monitoring-stats">
            <span className="student-stat-badge">
              {reports.length} total • {filteredReports.length} filtered
            </span>
          </div>
          
          <select 
            className="student-monitoring-select"
            value={selectedCourse}
            onChange={(e) => setSelectedCourse(e.target.value)}
          >
            <option value="all">📚 All My Courses</option>
            {enrollments.map((enrollment) => (
              <option key={enrollment.course_code} value={enrollment.course_code}>
                {enrollment.course_name} ({enrollment.course_code})
              </option>
            ))}
          </select>
          
          <button 
            className="student-monitoring-refresh"
            onClick={handleManualRefresh}
            title="Refresh reports"
          >
            🔄
          </button>
        </div>
      </div>

      <div className="student-monitoring-content">
        {enrollments.length === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">🎓</div>
            <h5>No Courses Enrolled</h5>
            <p>You haven't enrolled in any courses yet.</p>
            <p className="text-muted">
              Visit the <strong>My Classes</strong> section to enroll in courses.
            </p>
          </div>
        ) : reports.length === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">📋</div>
            <h5>No Reports Available</h5>
            <p>No lecture reports have been submitted for your enrolled courses yet.</p>
            <p className="text-muted">
              Reports will appear here when lecturers submit them after each lecture session.
            </p>
            {usingMockData ? (
              <button className="guest-btn guest-btn-primary" onClick={handleManualRefresh}>
                Load Demo Reports
              </button>
            ) : (
              <button className="guest-btn guest-btn-primary" onClick={handleManualRefresh}>
                Check for New Reports
              </button>
            )}
          </div>
        ) : filteredReports.length === 0 ? (
          <div className="student-empty-state">
            <div className="student-empty-icon">🔍</div>
            <h5>No Reports Match Your Filter</h5>
            <p>No reports found for the selected course filter.</p>
            <div className="student-action-buttons">
              <button 
                className="guest-btn guest-btn-outline"
                onClick={() => setSelectedCourse('all')}
              >
                Show All Courses
              </button>
              <button className="guest-btn guest-btn-primary" onClick={handleManualRefresh}>
                Refresh Data
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="student-monitoring-summary">
              <p>
                📈 Showing <strong>{filteredReports.length}</strong> report(s) for{' '}
                <strong>
                  {selectedCourse === 'all' 
                    ? 'all your courses' 
                    : enrollments.find(e => e.course_code === selectedCourse)?.course_name
                  }
                </strong>
              </p>
            </div>

            <div className="student-table-container">
              <table className="student-monitoring-table">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Date</th>
                    <th>Topic Covered</th>
                    <th>Attendance</th>
                    <th>Venue</th>
                    <th>Week</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map((report) => {
                    const attendanceRate = report.actual_students_present / report.total_students_registered;
                    const attendanceClass = attendanceRate > 0.7 ? 'high' : attendanceRate > 0.5 ? 'medium' : 'low';
                    
                    return (
                      <tr key={report.id} className="student-report-row">
                        <td>
                          <div className="student-course-info">
                            <strong>{report.course_name}</strong>
                            <span className="student-course-code">{report.course_code}</span>
                          </div>
                        </td>
                        <td className="student-lecturer-info">
                          👨‍🏫 {report.lecturer_name}
                        </td>
                        <td className="student-date-info">
                          {new Date(report.date_of_lecture).toLocaleDateString('en-US', {
                            weekday: 'short',
                            month: 'short',
                            day: 'numeric'
                          })}
                        </td>
                        <td>
                          <div className="student-topic-info">
                            {report.topic_taught}
                          </div>
                        </td>
                        <td>
                          <span className={`student-attendance-badge ${attendanceClass}`}>
                            {report.actual_students_present}/{report.total_students_registered}
                            <small>({Math.round(attendanceRate * 100)}%)</small>
                          </span>
                        </td>
                        <td className="student-venue-info">
                          🏢 {report.venue}
                        </td>
                        <td className="student-week-info">
                          {report.week_of_reporting}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>

      {/* Demo Data Notice */}
      {usingMockData && (
        <div style={{
          background: '#fff3cd',
          border: '1px solid #ffeaa7',
          borderRadius: '5px',
          padding: '10px',
          marginTop: '20px',
          textAlign: 'center'
        }}>
          <strong>⚠️ Demo Mode:</strong> Showing sample data. Reports will appear here automatically when lecturers submit real reports.
        </div>
      )}
    </div>
  );
};

export default Monitoring;
