import React, { useState, useEffect } from 'react';
import { getAvailableClasses, getStudentEnrollments, enrollStudent, unenrollStudent } from '../../services/api';

const Enrollment = ({ user }) => {
  const [availableClasses, setAvailableClasses] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [classesRes, enrollmentsRes] = await Promise.all([
        getAvailableClasses(),
        getStudentEnrollments(user.id)
      ]);
      setAvailableClasses(classesRes.data);
      setEnrollments(enrollmentsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEnroll = async (classId, courseId) => {
    try {
      await enrollStudent({
        student_id: user.id,
        class_id: classId,
        course_id: courseId
      });
      alert('Successfully enrolled in class!');
      fetchData();
    } catch (error) {
      alert(error.response?.data?.message || 'Error enrolling in class');
    }
  };

  const handleUnenroll = async (enrollmentId) => {
    if (window.confirm('Are you sure you want to unenroll from this class?')) {
      try {
        await unenrollStudent(enrollmentId);
        alert('Successfully unenrolled from class!');
        fetchData();
      } catch (error) {
        alert('Error unenrolling from class');
      }
    }
  };

  if (loading) return <div>Loading enrollment data...</div>;

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h4>My Enrolled Classes</h4>
            </div>
            <div className="card-body">
              {enrollments.length === 0 ? (
                <p>You are not enrolled in any classes yet.</p>
              ) : (
                <div className="list-group">
                  {enrollments.map((enrollment) => (
                    <div key={enrollment.id} className="list-group-item">
                      <div className="d-flex justify-content-between align-items-start">
                        <div>
                          <h6>{enrollment.course_name} ({enrollment.course_code})</h6>
                          <p className="mb-1">
                            <strong>Class:</strong> {enrollment.class_name}<br/>
                            <strong>Lecturer:</strong> {enrollment.lecturer_name}<br/>
                            <strong>Venue:</strong> {enrollment.venue}<br/>
                            <strong>Semester:</strong> {enrollment.semester}
                          </p>
                        </div>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleUnenroll(enrollment.id)}
                        >
                          Unenroll
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h4>Available Classes</h4>
            </div>
            <div className="card-body">
              {availableClasses.length === 0 ? (
                <p>No classes available for enrollment.</p>
              ) : (
                <div className="list-group">
                  {availableClasses.map((classItem) => {
                    const isEnrolled = enrollments.some(e => e.class_id === classItem.id);
                    return (
                      <div key={classItem.id} className="list-group-item">
                        <div className="d-flex justify-content-between align-items-start">
                          <div>
                            <h6>{classItem.course_name} ({classItem.course_code})</h6>
                            <p className="mb-1">
                              <strong>Class:</strong> {classItem.class_name}<br/>
                              <strong>Lecturer:</strong> {classItem.lecturer_name}<br/>
                              <strong>Venue:</strong> {classItem.venue}<br/>
                              <strong>Semester:</strong> {classItem.semester}<br/>
                              <strong>Year:</strong> {classItem.academic_year}
                            </p>
                          </div>
                          <button
                            className={`btn btn-sm ${isEnrolled ? 'btn-secondary' : 'btn-primary'}`}
                            onClick={() => handleEnroll(classItem.id, classItem.course_id)}
                            disabled={isEnrolled}
                          >
                            {isEnrolled ? 'Enrolled' : 'Enroll'}
                          </button>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Enrollment;
