import React, { useState, useEffect } from 'react';
import { getStudentMonitoring, submitRating, getStudentRatings, getStudentEnrollments } from '../../services/api';
import './Student.css';

const Rating = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [enrollments, setEnrollments] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingForm, setRatingForm] = useState({
    rating: 5,
    comment: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');

  // Lecturer mapping - UPDATE THESE WITH YOUR ACTUAL LECTURER DATA
  const lecturerMap = {
    'Dr. Smith': 1,
    'Prof. Johnson': 2,
    'Dr. Brown': 3,
    'Prof. Davis': 4,
    'Dr. Wilson': 5,
    'Prof. Anderson': 6,
    'Dr. Thomas': 7,
    'Prof. Martinez': 8,
    'Dr. Taylor': 9,
    'Prof. Lee': 10
  };

  useEffect(() => {
    fetchData();
  }, [user?.id]);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Fetching data...');
      
      console.log('🎯 RATING COMPONENT - Fetching data for student:', user?.id);
      
      const [reportsRes, ratingsRes, enrollmentsRes] = await Promise.all([
        getStudentMonitoring(user?.id),
        getStudentRatings(user?.id),
        getStudentEnrollments(user?.id)
      ]);

      const reportsData = reportsRes.data || reportsRes || [];
      const ratingsData = ratingsRes.data || ratingsRes || [];
      const enrollmentsData = enrollmentsRes.data || enrollmentsRes || [];

      setReports(reportsData);
      setRatings(ratingsData);
      setEnrollments(enrollmentsData);

      // Debug information
      setDebugInfo(`
        Reports: ${reportsData.length}
        Ratings: ${ratingsData.length} 
        Enrollments: ${enrollmentsData.length}
        Student ID: ${user?.id}
      `);

      console.log('📊 RATING DATA:', {
        reports: reportsData,
        ratings: ratingsData,
        enrollments: enrollmentsData,
        student: user
      });

    } catch (error) {
      console.error('❌ Error fetching rating data:', error);
      setError(`Failed to load data: ${error.message}`);
      setDebugInfo(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Get lecturer ID from report - FIXED VERSION
  const getLecturerId = (report) => {
    console.log('🔍 Getting lecturer ID for report:', report);
    
    // First, check if report has lecturer_id directly
    if (report.lecturer_id) {
      console.log('✅ Using direct lecturer_id:', report.lecturer_id);
      return report.lecturer_id;
    }
    
    // Check for different field names
    const possibleFields = ['lecturer_id', 'lecturerId', 'lecturer_id', 'lecturerID'];
    for (let field of possibleFields) {
      if (report[field]) {
        console.log(`✅ Using lecturer_id from field ${field}:`, report[field]);
        return report[field];
      }
    }
    
    // Map lecturer name to ID
    if (report.lecturer_name && lecturerMap[report.lecturer_name]) {
      const mappedId = lecturerMap[report.lecturer_name];
      console.log(`✅ Mapped lecturer "${report.lecturer_name}" to ID:`, mappedId);
      return mappedId;
    }
    
    // Fallback - use first available lecturer ID
    const fallbackId = 1;
    console.warn(`⚠️ Could not find lecturer_id for "${report.lecturer_name}", using fallback:`, fallbackId);
    return fallbackId;
  };

  // Debug report structure
  const debugReportStructure = (report) => {
    console.log('🔍 REPORT STRUCTURE ANALYSIS:', {
      id: report.id,
      course_name: report.course_name,
      course_code: report.course_code,
      lecturer_name: report.lecturer_name,
      lecturer_id: report.lecturer_id,
      has_lecturer_id: !!report.lecturer_id,
      all_keys: Object.keys(report)
    });
    
    // Check for lecturer_id in different formats
    const lecturerIdFields = ['lecturer_id', 'lecturerId', 'lecturer_id', 'lecturerID'];
    lecturerIdFields.forEach(field => {
      if (report[field]) {
        console.log(`📌 Found lecturer ID in field "${field}":`, report[field]);
      }
    });
  };

  const handleSelectReport = (report) => {
    console.log('📝 Selected report for rating:', report);
    debugReportStructure(report);
    
    const lecturerId = getLecturerId(report);
    console.log('🎯 Final lecturer_id to use:', lecturerId);
    
    setSelectedReport({
      ...report,
      lecturer_id: lecturerId // Ensure lecturer_id is set
    });
  };

  const handleSubmitRating = async (e) => {
    e.preventDefault();
    try {
      setError('');
      
      if (!selectedReport) {
        throw new Error('No report selected');
      }

      console.log('⭐ SUBMITTING RATING - Selected Report:', selectedReport);
      
      // Get the final lecturer_id
      const lecturerId = getLecturerId(selectedReport);
      
      const ratingData = {
        report_id: parseInt(selectedReport.id),
        student_id: parseInt(user.id),
        lecturer_id: parseInt(lecturerId),
        rating: parseInt(ratingForm.rating),
        comment: ratingForm.comment || '',
        course_code: selectedReport.course_code || ''
      };

      console.log('📤 FINAL RATING DATA BEING SENT:', ratingData);
      
      // Validate data before sending
      if (!ratingData.report_id || !ratingData.student_id || !ratingData.lecturer_id || !ratingData.rating) {
        throw new Error(`Missing required fields: ${JSON.stringify(ratingData)}`);
      }

      const response = await submitRating(ratingData);
      console.log('✅ RATING SUBMITTED SUCCESSFULLY:', response.data);
      
      alert('✅ Rating submitted successfully!');
      setSelectedReport(null);
      setRatingForm({ rating: 5, comment: '' });
      fetchData(); // Refresh data
      
    } catch (error) {
      console.error('❌ ERROR SUBMITTING RATING:', error);
      console.error('❌ Error details:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data
      });
      
      const errorMessage = error.response?.data?.error || error.message || 'Unknown error occurred';
      setError(`Failed to submit rating: ${errorMessage}`);
    }
  };

  // TEST FUNCTION: Load sample data to verify the component works
  const loadSampleData = () => {
    const sampleReports = [
      {
        id: 100,
        course_name: "Web Development",
        course_code: "DIWA2110",
        lecturer_name: "Dr. Smith",
        lecturer_id: 1, // Explicitly set lecturer_id
        date_of_lecture: "2024-01-20",
        topic_taught: "React Components and State Management",
        actual_students_present: 25,
        total_students_registered: 30,
        venue: "Lab 101",
        week_of_reporting: 6,
        created_at: new Date().toISOString()
      },
      {
        id: 101,
        course_name: "Database Systems",
        course_code: "DBS3010", 
        lecturer_name: "Prof. Johnson",
        lecturer_id: 2, // Explicitly set lecturer_id
        date_of_lecture: "2024-01-19",
        topic_taught: "SQL Joins and Advanced Queries",
        actual_students_present: 28,
        total_students_registered: 32,
        venue: "Room 205",
        week_of_reporting: 6,
        created_at: new Date().toISOString()
      }
    ];

    const sampleEnrollments = [
      {
        id: 1,
        course_name: "Web Development",
        course_code: "DIWA2110"
      },
      {
        id: 2,
        course_name: "Database Systems",
        course_code: "DBS3010"
      }
    ];

    setReports(sampleReports);
    setEnrollments(sampleEnrollments);
    setDebugInfo('✅ Loaded sample data for testing');
    console.log('🧪 Sample data loaded:', sampleReports);
  };

  // Test with known good data
  const testWithKnownData = async () => {
    try {
      console.log('🧪 Testing with known good data...');
      
      // Use data that matches your table structure exactly
      const testData = {
        report_id: 999, // Use a new report ID that doesn't exist in your table
        student_id: parseInt(user.id),
        lecturer_id: 2, // Use a lecturer_id that exists
        rating: 4,
        comment: 'Test rating from frontend - known data',
        course_code: 'TEST101'
      };
      
      console.log('📤 Test data:', testData);
      
      const response = await submitRating(testData);
      console.log('✅ Test successful:', response.data);
      alert('✅ Test rating submitted successfully!');
      fetchData(); // Refresh to see the new rating
      
    } catch (error) {
      console.error('❌ Test failed:', error);
      const errorDetails = error.response?.data || error.message;
      alert(`❌ Test failed: ${JSON.stringify(errorDetails)}`);
    }
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading rating data...</p>
          <small className="text-muted">{debugInfo}</small>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {/* Debug Panel */}
      <div className="debug-panel" style={{
        background: '#f8f9fa',
        border: '2px dashed #dc3545',
        padding: '10px',
        marginBottom: '20px',
        borderRadius: '5px',
        fontSize: '12px',
        fontFamily: 'monospace'
      }}>
        <strong>🧪 RATING DEBUG INFO</strong>
        <div>Student: {user?.id} | Role: {user?.role}</div>
        <div>Reports: {reports.length} | Ratings: {ratings.length} | Enrollments: {enrollments.length}</div>
        <div>{debugInfo}</div>
        <div className="mt-2">
          <button 
            onClick={loadSampleData}
            style={{
              background: '#28a745',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '3px',
              margin: '2px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            🧪 Load Sample Data
          </button>
          <button 
            onClick={testWithKnownData}
            style={{
              background: '#17a2b8',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '3px',
              margin: '2px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            🔬 Test Known Data
          </button>
          <button 
            onClick={fetchData}
            style={{
              background: '#6c757d',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '3px',
              margin: '2px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            🔄 Refresh Real Data
          </button>
        </div>
      </div>

      <h3>⭐ Rate Lectures</h3>
      
      {error && (
        <div className="alert alert-danger alert-dismissible fade show" role="alert">
          <strong>❌ Error:</strong> {error}
          <button type="button" className="btn-close" onClick={() => setError('')}></button>
        </div>
      )}

      <div className="row">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5>📋 Available Reports for Rating</h5>
              <div>
                <button className="btn btn-sm btn-outline-primary me-2" onClick={fetchData}>
                  🔄 Refresh
                </button>
                <button className="btn btn-sm btn-outline-success" onClick={loadSampleData}>
                  🧪 Test Data
                </button>
              </div>
            </div>
            <div className="card-body">
              {reports.length === 0 ? (
                <div className="text-center text-muted py-4">
                  <div style={{fontSize: '3rem'}}>📭</div>
                  <h5>No Reports Available for Rating</h5>
                  <p>This could be because:</p>
                  <ul className="text-start">
                    <li>No reports have been submitted for your enrolled courses</li>
                    <li>You're not enrolled in any courses</li>
                    <li>There's an issue with data loading</li>
                  </ul>
                  <div className="mt-3">
                    <button className="btn btn-primary me-2" onClick={fetchData}>
                      Check Again
                    </button>
                    <button className="btn btn-outline-secondary" onClick={loadSampleData}>
                      Load Sample Data
                    </button>
                  </div>
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-striped table-hover">
                    <thead className="table-dark">
                      <tr>
                        <th>Course</th>
                        <th>Lecturer</th>
                        <th>Date</th>
                        <th>Topic</th>
                        <th>Lecturer ID</th>
                        <th>Status</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {reports.map((report) => {
                        const alreadyRated = ratings.some(r => r.report_id === report.id);
                        const lecturerId = getLecturerId(report);
                        
                        return (
                          <tr key={report.id} className={alreadyRated ? 'table-success' : ''}>
                            <td>
                              <strong>{report.course_name}</strong>
                              <br />
                              <small className="text-muted">{report.course_code}</small>
                            </td>
                            <td>👨‍🏫 {report.lecturer_name}</td>
                            <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                            <td>
                              <span title={report.topic_taught}>
                                {report.topic_taught.length > 40 
                                  ? report.topic_taught.substring(0, 40) + '...' 
                                  : report.topic_taught}
                              </span>
                            </td>
                            <td>
                              <code>{lecturerId}</code>
                            </td>
                            <td>
                              {alreadyRated ? (
                                <span className="badge bg-success">✅ Rated</span>
                              ) : (
                                <span className="badge bg-warning text-dark">⭐ Pending</span>
                              )}
                            </td>
                            <td>
                              <button 
                                className={`btn btn-sm ${alreadyRated ? 'btn-outline-success' : 'btn-primary'}`}
                                onClick={() => handleSelectReport(report)}
                                disabled={alreadyRated}
                                title={alreadyRated ? 'You have already rated this lecture' : 'Rate this lecture'}
                              >
                                {alreadyRated ? '✓ Rated' : 'Rate Now'}
                              </button>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Rating Form Sidebar */}
        <div className="col-md-4">
          {selectedReport && (
            <div className="card sticky-top" style={{top: '20px'}}>
              <div className="card-header bg-primary text-white">
                <h5 className="mb-0">⭐ Rate This Lecture</h5>
              </div>
              <div className="card-body">
                <div className="mb-3">
                  <strong>Course:</strong> {selectedReport.course_name}
                </div>
                <div className="mb-3">
                  <strong>Lecturer:</strong> {selectedReport.lecturer_name} 
                  <small className="text-muted ms-2">(ID: {getLecturerId(selectedReport)})</small>
                </div>
                <div className="mb-3">
                  <strong>Date:</strong> {new Date(selectedReport.date_of_lecture).toLocaleDateString()}
                </div>
                <div className="mb-3">
                  <strong>Topic:</strong> {selectedReport.topic_taught}
                </div>
                <div className="mb-3">
                  <strong>Report ID:</strong> <code>{selectedReport.id}</code>
                </div>
                
                <form onSubmit={handleSubmitRating}>
                  <div className="mb-3">
                    <label className="form-label"><strong>Your Rating</strong></label>
                    <select 
                      className="form-select"
                      value={ratingForm.rating}
                      onChange={(e) => setRatingForm({...ratingForm, rating: parseInt(e.target.value)})}
                      required
                    >
                      <option value="1">⭐ - Poor</option>
                      <option value="2">⭐⭐ - Fair</option>
                      <option value="3">⭐⭐⭐ - Good</option>
                      <option value="4">⭐⭐⭐⭐ - Very Good</option>
                      <option value="5">⭐⭐⭐⭐⭐ - Excellent</option>
                    </select>
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label"><strong>Your Feedback (Optional)</strong></label>
                    <textarea
                      className="form-control"
                      value={ratingForm.comment}
                      onChange={(e) => setRatingForm({...ratingForm, comment: e.target.value})}
                      rows="4"
                      placeholder="What did you think of this lecture? Any suggestions for improvement?"
                      style={{resize: 'vertical'}}
                    />
                  </div>
                  
                  <div className="d-grid gap-2">
                    <button type="submit" className="btn btn-success btn-lg">
                      ✅ Submit Rating
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-outline-secondary"
                      onClick={() => setSelectedReport(null)}
                    >
                      ❌ Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          {/* My Ratings Card */}
          <div className="card mt-3">
            <div className="card-header">
              <h5 className="mb-0">📊 My Ratings ({ratings.length})</h5>
            </div>
            <div className="card-body">
              {ratings.length === 0 ? (
                <div className="text-center text-muted py-3">
                  <div style={{fontSize: '2rem'}}>📝</div>
                  <p>You haven't rated any lectures yet.</p>
                  <small>Your ratings will appear here once you submit them.</small>
                </div>
              ) : (
                <div style={{maxHeight: '400px', overflowY: 'auto'}}>
                  {ratings.map((rating) => (
                    <div key={rating.id} className="mb-3 p-3 border rounded bg-light">
                      <div className="d-flex justify-content-between align-items-start">
                        <div className="flex-grow-1">
                          <div className="d-flex align-items-center mb-1">
                            <strong className="me-2">{'⭐'.repeat(rating.rating)}</strong>
                            <span className="text-muted">({rating.rating}/5)</span>
                          </div>
                          {rating.comment && (
                            <p className="mb-2 text-dark">{rating.comment}</p>
                          )}
                          <small className="text-muted d-block">
                            Report ID: <code>{rating.report_id}</code> | 
                            Submitted: {new Date(rating.created_at).toLocaleDateString()}
                          </small>
                          {rating.course_name && (
                            <small className="text-primary d-block">
                              Course: {rating.course_name}
                            </small>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Rating;
