import React, { useState } from 'react';
import { logout } from '../../utils/auth';
import Courses from './Courses';
import Reports from './Reports';
import Monitoring from './Monitoring';
import Classes from './Classes';
import Lectures from './Lectures';
import Rating from './Rating';

const PLDashboard = ({ user }) => {
  const [activeTab, setActiveTab] = useState('dashboard');

  return (
    <div className="container-fluid">
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container">
          <span className="navbar-brand">Program Leader Dashboard</span>
          <div className="navbar-nav ms-auto">
            <span className="navbar-text me-3">Welcome, {user?.name}</span>
            <button className="btn btn-outline-light btn-sm" onClick={logout}>
              Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <div className="row">
          <div className="col-md-3">
            <div className="list-group">
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'dashboard' ? 'active' : ''}`}
                onClick={() => setActiveTab('dashboard')}
              >
                Dashboard
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'courses' ? 'active' : ''}`}
                onClick={() => setActiveTab('courses')}
              >
                Courses
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'reports' ? 'active' : ''}`}
                onClick={() => setActiveTab('reports')}
              >
                Reports
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'monitoring' ? 'active' : ''}`}
                onClick={() => setActiveTab('monitoring')}
              >
                Monitoring
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'classes' ? 'active' : ''}`}
                onClick={() => setActiveTab('classes')}
              >
                Classes
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'lectures' ? 'active' : ''}`}
                onClick={() => setActiveTab('lectures')}
              >
                Lectures
              </button>
              <button 
                className={`list-group-item list-group-item-action ${activeTab === 'rating' ? 'active' : ''}`}
                onClick={() => setActiveTab('rating')}
              >
                Rating
              </button>
            </div>
          </div>
          <div className="col-md-9">
            {activeTab === 'dashboard' && (
              <div className="card">
                <div className="card-header">
                  <h4>Program Leader Overview</h4>
                </div>
                <div className="card-body">
                  <p>Welcome to the Program Leader Dashboard. Use the navigation menu to:</p>
                  <ul>
                    <li>Manage courses and assign lecturers</li>
                    <li>View reports with PRL feedback</li>
                    <li>Monitor overall program performance</li>
                    <li>Manage classes and lecturers</li>
                    <li>Review rating data across the program</li>
                  </ul>
                </div>
              </div>
            )}
            {activeTab === 'courses' && <Courses user={user} />}
            {activeTab === 'reports' && <Reports user={user} />}
            {activeTab === 'monitoring' && <Monitoring user={user} />}
            {activeTab === 'classes' && <Classes user={user} />}
            {activeTab === 'lectures' && <Lectures user={user} />}
            {activeTab === 'rating' && <Rating user={user} />}
            // Add this to the PLDashboard component
{activeTab === 'class-management' && <ClassManagement user={user} />}


<button 
  className={`list-group-item list-group-item-action ${activeTab === 'class-management' ? 'active' : ''}`}
  onClick={() => setActiveTab('class-management')}
>
  Class Management
</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PLDashboard;
