import React, { useState, useEffect } from 'react';
import { getPRLReports, addFeedback } from '../../services/api';

const Reports = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const data = await getPRLReports(user.id);
      setReports(data.data || []);
    } catch (error) {
      console.log('Error loading reports');
      setReports([]);
    } finally {
      setLoading(false);
    }
  };

  const submitFeedback = async (e) => {
    e.preventDefault();
    try {
      await addFeedback({
        report_id: selectedReport.id,
        prl_id: user.id,
        feedback_text: feedback
      });
      alert('Success!');
      setSelectedReport(null);
      setFeedback('');
      fetchReports();
    } catch (error) {
      alert('Error');
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="row">
      <div className="col-md-8">
        <div className="card">
          <div className="card-header">
            <h4>Program Reports</h4>
          </div>
          <div className="card-body">
            {reports.length === 0 ? (
              <p>No reports found.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Lecturer</th>
                    <th>Date</th>
                    <th>Attendance</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {reports.map((report) => (
                    <tr key={report.id}>
                      <td>{report.course_name}</td>
                      <td>{report.lecturer_name}</td>
                      <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                      <td>{report.actual_students_present}/{report.total_students_registered}</td>
                      <td>
                        <button 
                          className="btn btn-primary btn-sm"
                          onClick={() => setSelectedReport(report)}
                        >
                          Feedback
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      </div>

      {selectedReport && (
        <div className="col-md-4">
          <div className="card">
            <div className="card-header">
              <h5>Add Feedback</h5>
            </div>
            <div className="card-body">
              <form onSubmit={submitFeedback}>
                <div className="mb-3">
                  <label>Feedback</label>
                  <textarea
                    className="form-control"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    rows="4"
                    required
                  />
                </div>
                <button type="submit" className="btn btn-success">Submit</button>
                <button 
                  type="button" 
                  className="btn btn-secondary ms-2"
                  onClick={() => setSelectedReport(null)}
                >
                  Cancel
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Reports;
