import React, { useState, useEffect } from 'react';
import { getPRLCourses } from '../../services/api';

const Courses = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await getPRLCourses(user.id);
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading courses...</div>;

  return (
    <div className="card">
      <div className="card-header d-flex justify-content-between align-items-center">
        <h4>Courses in My Stream</h4>
        <span className="badge bg-primary">{courses.length} courses</span>
      </div>
      <div className="card-body">
        {courses.length === 0 ? (
          <p>No courses found in your stream.</p>
        ) : (
          <div className="table-responsive">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Course Code</th>
                  <th>Course Name</th>
                  <th>Stream</th>
                  <th>Assigned Lecturer</th>
                </tr>
              </thead>
              <tbody>
                {courses.map((course) => (
                  <tr key={course.id}>
                    <td>{course.course_code}</td>
                    <td>{course.course_name}</td>
                    <td>{course.stream}</td>
                    <td>{course.assigned_lecturer_id ? 'Assigned' : 'Not Assigned'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Courses;
