import React, { useState, useEffect } from 'react';
import { getStudentMonitoring, submitRating, getStudentRatings } from '../../services/api';

const Rating = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingForm, setRatingForm] = useState({
    rating: 5,
    comment: ''
  });

  useEffect(() => {
    fetchReports();
    fetchRatings();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await getStudentMonitoring(user.id);
      setReports(response.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    }
  };

  const fetchRatings = async () => {
    try {
      const response = await getStudentRatings(user.id);
      setRatings(response.data);
    } catch (error) {
      console.error('Error fetching ratings:', error);
    }
  };

  const handleSubmitRating = async (e) => {
    e.preventDefault();
    try {
      await submitRating({
        report_id: selectedReport.id,
        student_id: user.id,
        lecturer_id: selectedReport.lecturer_id,
        ...ratingForm
      });
      alert('Rating submitted successfully!');
      setSelectedReport(null);
      setRatingForm({ rating: 5, comment: '' });
      fetchRatings();
    } catch (error) {
      alert('Error submitting rating');
    }
  };

  return (
    <div className="container mt-4">
      <h3>Rate Lectures</h3>
      
      <div className="row">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header">
              <h5>Available Reports for Rating</h5>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>Course</th>
                      <th>Lecturer</th>
                      <th>Date</th>
                      <th>Topic</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reports.map((report) => (
                      <tr key={report.id}>
                        <td>{report.course_name}</td>
                        <td>{report.lecturer_name}</td>
                        <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                        <td>{report.topic_taught}</td>
                        <td>
                          <button 
                            className="btn btn-primary btn-sm"
                            onClick={() => setSelectedReport(report)}
                          >
                            Rate
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-4">
          {selectedReport && (
            <div className="card">
              <div className="card-header">
                <h5>Rate: {selectedReport.course_name}</h5>
              </div>
              <div className="card-body">
                <form onSubmit={handleSubmitRating}>
                  <div className="mb-3">
                    <label className="form-label">Rating (1-5)</label>
                    <select 
                      className="form-control"
                      value={ratingForm.rating}
                      onChange={(e) => setRatingForm({...ratingForm, rating: parseInt(e.target.value)})}
                    >
                      {[1,2,3,4,5].map(num => (
                        <option key={num} value={num}>{num} Star{num !== 1 ? 's' : ''}</option>
                      ))}
                    </select>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Comment</label>
                    <textarea
                      className="form-control"
                      value={ratingForm.comment}
                      onChange={(e) => setRatingForm({...ratingForm, comment: e.target.value})}
                      rows="3"
                    />
                  </div>
                  <button type="submit" className="btn btn-success">Submit Rating</button>
                  <button 
                    type="button" 
                    className="btn btn-secondary ms-2"
                    onClick={() => setSelectedReport(null)}
                  >
                    Cancel
                  </button>
                </form>
              </div>
            </div>
          )}

          <div className="card mt-3">
            <div className="card-header">
              <h5>My Ratings</h5>
            </div>
            <div className="card-body">
              {ratings.map((rating) => (
                <div key={rating.id} className="mb-2 p-2 border rounded">
                  <strong>Rating: {rating.rating}/5</strong>
                  <p className="mb-1">{rating.comment}</p>
                  <small className="text-muted">
                    {new Date(rating.created_at).toLocaleDateString()}
                  </small>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Rating;
